-- Records of news
-- ----------------------------
INSERT INTO `news` VALUES (1, 'Thông số màn hình của Realme 5s được xác nhận trên Flipkart', 'Tin tức', 'Theo một báo cáo gần đây cho biết, Realme sẽ chính thức trình làng mẫu di động tầm trung mới của hãng là Realme 5s tại một sự kiện diễn ra vào...', '<p>Realme&nbsp;sẽ ch&iacute;nh thức tr&igrave;nh l&agrave;ng mẫu di động tầm trung mới của h&atilde;ng l&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd/realme-5s\" target=\"_blank\" title=\"Điện thoại Realme 5s\" type=\"Điện thoại Realme 5s\">Realme 5s</a>&nbsp;tại một sự kiện diễn ra v&agrave;o ng&agrave;y 20/11 tới đ&acirc;y.</p>\r\n\r\n<p>Được biết, Realme 5s xuất hiện lần đầu ti&ecirc;n tr&ecirc;n Flipkart v&agrave;i ng&agrave;y trước tuy nhi&ecirc;n chưa c&oacute; nhiều th&ocirc;ng tin về m&aacute;y. V&agrave; mới đ&acirc;y, trang web n&agrave;y vừa tiết lộ những th&ocirc;ng tin th&uacute; vị li&ecirc;n quan đến m&agrave;n h&igrave;nh của sản phẩm.</p>\r\n\r\n<p><img alt=\"Thông số màn hình của Realme 5s được xác nhận trên Flipkart\" src=\"https://cdn.tgdd.vn/Files/2019/11/15/1219157/5s1_800x405.jpg\" title=\"Thông số màn hình của Realme 5s được xác nhận trên Flipkart\" /></p>\r\n\r\n<p>Cụ thể, theo h&igrave;nh ảnh mới được đăng tải th&igrave; Realme 5s sẽ được trang bị m&agrave;n h&igrave;nh k&iacute;ch thước 6.51 inch, độ ph&acirc;n giải HD+ v&agrave; sử dụng thiết kế notch h&igrave;nh giọt nước để chứa camera selfie.</p>\r\n\r\n<p>M&agrave;n h&igrave;nh n&agrave;y nhiều khả năng sẽ c&oacute; độ ph&acirc;n giải ch&iacute;nh x&aacute;c l&agrave; 720 x 1.600 pixel v&agrave; c&oacute; tỷ lệ khung h&igrave;nh 20:9 tương tự như&nbsp;<a href=\"https://www.thegioididong.com/dtdd/realme-5\" target=\"_blank\" title=\"Điện thoại Realme 5\" type=\"Điện thoại Realme 5\">Realme 5</a>. Đồng thời, cũng c&oacute; thể thấy&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"điện thoại\" type=\"điện thoại\">điện thoại</a>&nbsp;sẽ sở hữu viền bezel kh&aacute; mỏng ở 3 cạnh v&agrave; phần &#39;cằm&#39; c&oacute; k&iacute;ch thước d&agrave;y hơn một ch&uacute;t.</p>\r\n\r\n<p><img alt=\"Realme 5\" src=\"https://cdn.tgdd.vn/Files/2019/11/15/1219157/5s3_800x450.jpg\" title=\"Realme 5\" /></p>\r\n\r\n<p>Realme 5</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, theo c&aacute;c b&aacute;o c&aacute;o trước đ&acirc;y th&igrave; Realme 5s sẽ c&oacute; hệ thống 4 camera sau 48 MP, sử dụng bộ vi xử l&yacute; Snapdragon 665, đi k&egrave;m dung lượng RAM 4 GB, c&ugrave;ng với bộ nhớ trong 64 GB c&oacute; thể mở rộng v&agrave; được t&iacute;ch hợp vi&ecirc;n pin dung lượng 5.000 mAh, cũng như chạy tr&ecirc;n hệ điều h&agrave;nh Android 9 Pie khi xuất xưởng.</p>\r\n\r\n<p>Ngo&agrave;i ra, hiện vẫn chưa c&oacute; th&ocirc;ng tin về gi&aacute; b&aacute;n của Realme 5s. C&aacute;c bạn dự đo&aacute;n xem gi&aacute; của m&aacute;y sẽ l&agrave; bao nhi&ecirc;u? H&atilde;y để lại b&igrave;nh luận nh&eacute;.</p>\r\n\r\n<p>Nguồn:&nbsp;<a href=\"https://www.gizmochina.com/2019/11/15/realme-5s-display-revealed-on-flipkart/\" rel=\"nofollow\" target=\"_blank\" title=\"Gizmochina\" type=\"Gizmochina\">Gizmochina</a></p>\r\n\r\n<p>&quot;&gt;</p>\r\n', 'http://localhost:8080/ProjectWeb_1920/data/images/img-tintuc/1.jpg', 'Nguyễn Khắc Ngọc', '2019-11-15', 1, 1);
INSERT INTO `news` VALUES (2, 'Motorola Razr V3 là chiếc smartphone thành công nhất của Motorola, liệu Razr 2019 sẽ tiến triển hơn?', 'Tin tức', 'Cách đây vài ngày, Motorola đã trình làng smartphone Razr 2019 với trang bị màn hình gập cực kỳ độc đáo. Với kỳ vọng Razr 2019 sẽ cạnh tranh được...', '<h2>C&aacute;ch đ&acirc;y v&agrave;i ng&agrave;y, Motorola đ&atilde; tr&igrave;nh l&agrave;ng smartphone&nbsp;<a href=\"https://www.thegioididong.com/dtdd/motorola-razr-2019\" target=\"_blank\" title=\"Razr 2019\" type=\"Razr 2019\">Razr 2019</a>&nbsp;với trang bị m&agrave;n h&igrave;nh gập cực kỳ độc đ&aacute;o. Với kỳ vọng Razr 2019 sẽ cạnh tranh được với Galaxy Fold của Samsung, Motorola tin rằng đ&atilde; đến l&uacute;c mang thương hiệu của m&igrave;nh trở lại bằng một chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"SmartPhone\" type=\"Smartphone \">điện thoại</a>&nbsp;mới. Liệu Motorola c&oacute; phục hồi được danh tiếng của m&igrave;nh?</h2>\r\n\r\n<p dir=\"ltr\">C&aacute;ch đ&acirc;y hơn một thập kỷ, Motorola đ&atilde; ph&aacute;t h&agrave;nh chiếc Razr V3, một chiếc điện thoại mang lại th&agrave;nh c&ocirc;ng rực rỡ cho Motorola, đồng thời cũng l&agrave; người ti&ecirc;n phong mở ra kỷ nguy&ecirc;n ph&aacute;t triển cho điện thoại th&ocirc;ng minh ng&agrave;y nay.</p>\r\n\r\n<p dir=\"ltr\">Motorola Razr V3 c&oacute; sự kh&aacute;c biệt lớn so với c&aacute;c d&ograve;ng điện thoại di động kh&aacute;c tại thời điểm đ&oacute;. Thứ nhất, n&oacute; c&oacute; một th&acirc;n h&igrave;nh tương đối mỏng t&iacute;nh ở thời điểm ra mắt. Thứ hai, n&oacute; được ho&agrave;n thiện bởi chất liệu nh&ocirc;m, c&ugrave;ng trang bị m&agrave;n h&igrave;nh m&agrave;u 2.2 inch. Hơn nữa, nhiều người c&ograve;n cho rằng b&agrave;n ph&iacute;m t&iacute;ch hợp tr&ecirc;n Razr V3 tr&ocirc;ng cũng rất tuyệt.</p>\r\n\r\n<p><img alt=\"Motorola Razr V3\" src=\"https://cdn.tgdd.vn/Files/2019/11/15/1219140/img_1182_800x450.jpg\" title=\"Motorola Razr V3\" />H&igrave;nh ảnh Motorola Razr V3</p>\r\n\r\n<p dir=\"ltr\">Tại thời điểm đ&oacute;, Motorola Razr V3 xuất hiện như một thứ chỉ c&oacute; trong c&aacute;c bộ phim khoa học viễn tưởng. C&aacute;c n&uacute;t bấm được c&aacute;ch điệu, kết hợp với &aacute;nh s&aacute;ng điện ph&aacute;t quang của Razr V3 khiến n&oacute; giống như một thiết bị li&ecirc;n lạc của Star Trek.</p>\r\n\r\n<p dir=\"ltr\">Ban đầu, Motorola Razr V3 l&agrave; một thiết bị đắt tiền v&agrave; xa sỉ đối với đa số người d&ugrave;ng. N&oacute; c&oacute; gi&aacute; l&ecirc;n đến 500 USD, một con số kh&aacute; cao khi tiền l&uacute;c đ&oacute; c&ograve;n c&oacute; gi&aacute; trị. Tuy nhi&ecirc;n, mục ti&ecirc;u của Motorola l&agrave; nhắm v&agrave;o những người gi&agrave;u hay giới thượng lưu. Razr V3 l&uacute;c bấy giờ được tiếp thị như một thiết bị mang kiểu d&aacute;ng thời trang cao cấp.</p>\r\n\r\n<p dir=\"ltr\">Vậy ngo&agrave;i những yếu tố về thiết kế v&agrave; mức gi&aacute; đắt đỏ, Motorola Razr V3 c&ograve;n g&igrave; để mang lại th&agrave;nh c&ocirc;ng cho Motorola. H&atilde;y c&ugrave;ng Team Thế Giới Di Động ph&acirc;n t&iacute;ch những điểm khiến Razr V3 trở th&agrave;nh d&ograve;ng điện thoại th&agrave;nh c&ocirc;ng tại thời điểm c&aacute;ch đ&acirc;y hơn một thập kỷ nh&eacute;!</p>\r\n\r\n<h3 dir=\"ltr\"><strong>Kết nối với iTunes trước cả iPhone</strong></h3>\r\n\r\n<p dir=\"ltr\">Trong khi Razr V3 l&agrave; một chiếc điện thoại đắt tiền, Motorola đ&atilde; bắt đầu hạ gi&aacute; chiếc điện thoại n&agrave;y ngay sau khi ra mắt. Khoảng cuối năm 2005, một biến thế mới với t&ecirc;n gọi Razr V3i đ&atilde; được ra mắt. N&oacute; ngay lập tức thu được nhiều ấn tượng nhờ những cải tiến về t&iacute;nh năng, m&agrave;n h&igrave;nh v&agrave; camera.&nbsp;</p>\r\n\r\n<p><img alt=\"Motorola Razr V3i\" src=\"https://cdn.tgdd.vn/Files/2019/11/15/1219140/img_1181_800x539.jpg\" title=\"Motorola Razr V3i\" />H&igrave;nh ảnh Motorola Razr V3</p>\r\n\r\n<p dir=\"ltr\">T&iacute;nh năng đ&aacute;ng được ch&uacute; &yacute; nhất của Razr V3i l&agrave; khả năng kết nối với iTunes của Apple. Theo đ&oacute;, người d&ugrave;ng c&oacute; thể tải xuống hơn 100 b&agrave;i h&aacute;t từ t&agrave;i khoản iTunes của họ về điện thoại. T&iacute;nh năng n&agrave;y đ&atilde; xuất hiện tr&ecirc;n Razr V3i được gần 2 năm trước khi Apple ra mắt chiếc iPhone đầu ti&ecirc;n.</p>\r\n\r\n<p dir=\"ltr\">Theo số liệu thống k&ecirc;, c&aacute;c phi&ecirc;n bản của Razr V3 đ&atilde; b&aacute;n được hơn 130 triệu chiếc, khiến n&oacute; trở th&agrave;nh d&ograve;ng điện thoại vỏ s&ograve; b&aacute;n chạy nhất thế giới.</p>\r\n\r\n<h3 dir=\"ltr\"><strong>Thất bại của Razr V3 v&agrave; hy vọng trở lại&nbsp;</strong></h3>\r\n\r\n<p dir=\"ltr\">V&agrave;o năm 2007, ngay sau khi chiếc iPhone đầu ti&ecirc;n được ra mắt, Motorola đ&atilde; ph&aacute;t h&agrave;nh chiếc Razr thế hệ tiếp theo với t&ecirc;n gọi Razr 2. N&oacute; c&oacute; nhiều cải tiến so với Razr thế hệ đầu, nhưng chiếc điện thoại n&agrave;y đ&atilde; bước đầu đ&aacute;nh dấu sự thất bại của Motorola. Motorola Razr 2 tuy được n&acirc;ng cấp với nhiều t&iacute;nh năng mới, nhưng iPhone tại thời đ&oacute; đ&atilde; dẫn đầu cả về thiết kế lẫn doanh số b&aacute;n h&agrave;ng. Trong khi Razr 2 vẫn giữ nguy&ecirc;n thiết kế gập vỏ s&ograve; truyền thống c&ugrave;ng b&agrave;n ph&iacute;m vật l&yacute;. Kết quả l&agrave; sự thất bại đ&aacute;ng kể về doanh số của Razr 2.</p>\r\n\r\n<p><img alt=\"Motorola Razr 2\" src=\"https://cdn.tgdd.vn/Files/2019/11/15/1219140/img_1179_800x450.jpg\" title=\"Motorola Razr 2\" />H&igrave;nh ảnh Motorola Razr 2</p>\r\n\r\n<p dir=\"ltr\">Năm 2011, Motorola đ&atilde; cố gắng hồi sinh thương hiệu của m&igrave;nh, lần n&agrave;y l&agrave; sự ra mắt của d&ograve;ng điện thoại&nbsp;<a href=\"https://www.thegioididong.com/dtdd/motorola-droid-razr\" target=\"_blank\" title=\"Motorola DROID RAZR\" type=\"Motorola DROID RAZR\">Droid Razr</a>. Chiếc điện thoại mới n&agrave;y kh&ocirc;ng c&ograve;n giữ kiểu d&aacute;ng gập vỏ s&ograve; như thế hệ tiền nhiệm, thay v&agrave;o đ&oacute; l&agrave; một thiết bị chạy Android c&oacute; thiết kế mỏng c&ugrave;ng chiếc cằm kh&aacute; gi&agrave;y ở dưới. N&oacute; được thiết kế với cấu tr&uacute;c chắc chắn, t&iacute;ch hợp m&agrave;n h&igrave;nh 4.3 inch v&agrave; ho&agrave;n thiện bởi chất liệu Kevlar ở mặt sau.</p>\r\n\r\n<p dir=\"ltr\">Tuy nhi&ecirc;n, những chiếc Droid Razr ra mắt tại thời điểm n&agrave;y đều kh&ocirc;ng thể cạnh tranh với c&aacute;c d&ograve;ng điện thoại phổ biến như iPhone hay d&ograve;ng Galaxy S series của Samsung.</p>\r\n\r\n<p><img alt=\"Motorola Droid Razr\" src=\"https://cdn.tgdd.vn/Files/2019/11/15/1219140/img_1180_799x450.jpg\" title=\"Motorola Droid Razr\" />H&igrave;nh ảnh Motorola Droid Razr</p>\r\n\r\n<p dir=\"ltr\">B&acirc;y giờ, Motorola đang đưa Razr trở lại một lần nữa. Model mới c&oacute; thiết kế của một chiếc Razr cổ điển với trang bị m&agrave;n h&igrave;nh gập v&agrave; chạy tr&ecirc;n nền tảng hệ điều h&agrave;nh Android, nh&igrave;n thiết kế tổng thể kh&aacute; giống với chiếc Razr V3 đ&atilde; tạo th&agrave;nh c&ocirc;ng cho Motorola. V&agrave; tất nhi&ecirc;n, n&oacute; cũng c&oacute; mức gi&aacute; kh&aacute; cao, khoảng 1.500 USD (khoảng 34.8 triệu đồng).</p>\r\n\r\n<p><img alt=\"Motorola Razr 2019\" src=\"https://cdn.tgdd.vn/Files/2019/11/15/1219140/img_1173_800x450.jpg\" title=\"Motorola Razr 2019\" />H&igrave;nh ảnh Motorola Razr V3 mới ra mắt</p>\r\n\r\n<p dir=\"ltr\">Với việc ra mắt chiếc Razr 2019 mới, Motorola đang hy vọng v&agrave;o một thiết kế kh&aacute;c biệt sẽ thu h&uacute;t những người đang t&igrave;m khiếm một chiếc điện thoại đặc biệt. Liệu chiếc Motorola Razr 2019 mới ra mắt n&agrave;y sẽ thu h&uacute;t được h&agrave;ng triệu người mua giống như con số đ&atilde; đạt được với Razr V3? Bạn thử đo&aacute;n xem đ&aacute;p &aacute;n cho c&acirc;u hỏi tr&ecirc;n l&agrave; g&igrave;?</p>\r\n\r\n<p dir=\"ltr\">Nguồn:&nbsp;<a href=\"https://www.androidauthority.com/throwbackthursday-motorola-razr-v3-1053853/\" rel=\"nofollow\" target=\"_blank\" title=\"Android Police\" type=\"Android Police \">Android Police</a>&nbsp;</p>\r\n', 'http://localhost:8080/ProjectWeb_1920/images/img-tintuc/2.jpg', 'Hồ Nguyễn Anh Phong', '2019-11-16', 0, 1);
INSERT INTO `news` VALUES (3, 'iPhone 5G vẫn chưa thấy đâu nhưng các chuyên gia nhận định Apple sẽ dẫn đầu thị phần smartphone 5G trong năm sau', 'Tin tức', 'Đúng như tiêu đề, theo một báo cáo gần đây của Strategy Analytics, Apple sẽ dẫn đầu thị trường smartphone 5G bằng thế hệ iPhone tiếp theo. Nhà...', '<h2>Đ&uacute;ng như ti&ecirc;u đề, theo một b&aacute;o c&aacute;o gần đ&acirc;y của Strategy Analytics, Apple sẽ dẫn đầu thị trường&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"smartphone\">smartphone&nbsp;</a>5G bằng thế hệ&nbsp;<a href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" title=\"iPhone\">iPhone&nbsp;</a>tiếp theo.</h2>\r\n\r\n<p>Trước đ&oacute;, nh&agrave; ph&acirc;n t&iacute;ch Ming-Chi Kuo cũng đ&atilde; từng ti&ecirc;n đo&aacute;n rằng thế hệ&nbsp;<a href=\"https://www.thegioididong.com/dtdd/iphone-2020\" target=\"_blank\" title=\"iPhone 2020\">iPhone 2020</a>&nbsp;sẽ được Apple t&iacute;ch hợp c&ocirc;ng nghệ mạng 5G, hoặc &iacute;t nhất l&agrave; mang l&ecirc;n một biến thế cao cấp hơn.</p>\r\n\r\n<p>Th&ocirc;ng tin r&ograve; rỉ từ v&agrave;i tuần trước đ&atilde; cho biết về sự tồn tại về một mẫu iPhone gi&aacute; rẻ của Apple. Chiếc iPhone n&agrave;y sẽ c&oacute; thiết kế giống<a href=\"https://www.thegioididong.com/dtdd/iphone-8-64gb\" target=\"_blank\" title=\"iPhone 8\">&nbsp;iPhone 8</a>, cấu h&igrave;nh mạnh mẽ của chip A13 trong khi mức gi&aacute; chỉ 399 USD (khoảng 9.3 triệu VNĐ).</p>\r\n\r\n<p>Được đồn đo&aacute;n sẽ ra mắt v&agrave;o đầu năm sau, n&ecirc;n việc chiếc iPhone gi&aacute; rẻ n&agrave;y c&oacute; 5G cũng l&agrave; một điều đ&aacute;ng để k&igrave; vọng.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, Strategy Analytics cũng n&oacute;i th&ecirc;m, trong ngắn hạn th&igrave; Apple c&oacute; thể đứng nhất, nhưng về l&acirc;u d&agrave;i th&igrave; c&ocirc;ng ty n&agrave;y nhận định&nbsp;<a href=\"https://www.thegioididong.com/dtdd-samsung\" onclick=\"window.open(this.href, \'\', \'resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no\'); return false;\" title=\"Samsung\">Samsung&nbsp;</a>mới l&agrave; người dẫn đầu bởi sự phổ cập của 5G xuống c&aacute;c ph&acirc;n kh&uacute;c tầm trung v&agrave; gi&aacute; rẻ.</p>\r\n\r\n<p><img alt=\"Thị phần 5G của các hãng smartphone năm 2020 theo dự đoán của Strategy Analytics\" src=\"https://cdn.tgdd.vn/Files/2019/11/15/1219117/5gmarket_800x443.jpg\" title=\"Thị phần 5G của các hãng smartphone năm 2020 theo dự đoán của Strategy Analytics\" /></p>\r\n\r\n<p>Thị phần 5G của c&aacute;c h&atilde;ng smartphone năm 2020 theo dự đo&aacute;n của Strategy Analytics</p>\r\n\r\n<p>C&ograve;n một c&aacute;i t&ecirc;n kh&aacute;c rất đ&aacute;ng để nhắc t&ecirc;n ở đ&acirc;y ch&iacute;nh l&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd-huawei\" target=\"_blank\" title=\"Huawei\">Huawei</a>. Mặc d&ugrave; bị k&igrave;m h&atilde;m một phần do ch&iacute;nh s&aacute;ch trừng phạt của Mỹ, nhưng c&ocirc;ng nghệ 5G của Huawei lại được đ&aacute;nh gi&aacute; l&agrave; ti&ecirc;n tiến nhất hiện nay.</p>\r\n\r\n<p>Kh&ocirc;ng thể phủ nhận rằng khi Apple thường được nhắc đến với c&aacute;i t&ecirc;n &quot;kẻ tạo xu hướng&quot;, bạn nghĩ sự tham gia của Apple v&agrave;o mảng 5G c&oacute; l&agrave;m thị trường smartphone năm sau s&ocirc;i động hơn?</p>\r\n\r\n<p>Nguồn:&nbsp;<a href=\"https://www.macrumors.com/2019/11/14/strategy-analytics-apple-5g-lead-2020/?fbclid=IwAR0hxTltK_skuNsVtdY64ikCKgRXkkE7YQN5dYbd51JykHm2T2hBRVpL814\" rel=\"nofollow\" target=\"_blank\" title=\"Nguồn\">MacRumors</a></p>\r\n', 'http://localhost:8080/ProjectWeb_1920/images/img-tintuc/3.jpg', 'Nguyễn Khắc Ngọc', '2019-11-16', 1, 1);
INSERT INTO `news` VALUES (4, 'Sau cú ngã ngựa, Huawei vẫn “bất khả chiến bại”', 'Tin tức', 'Trong bài phát biểu tại hội nghị tin tức về đổi mới, chủ tịch Hội đồng quản trị William Xu khẳng định Huawei hoàn toàn có thể tăng trưởng mà không...', '<h2>Th&aacute;ng 5 vừa qua,&nbsp;<a href=\"https://www.thegioididong.com/dtdd-huawei\" target=\"_blank\" title=\"Huawei\" type=\"Huawei\">Huawei</a>&nbsp;bị đưa v&agrave;o danh s&aacute;ch đen của Mỹ do bị nghi ngờ g&acirc;y ra mối đe dọa an ninh cho Mỹ mặc d&ugrave; ph&iacute;a Washington kh&ocirc;ng c&oacute; một bằng chứng cụ thể. Lệnh cấm vận khiến Huawei kh&ocirc;ng thể hợp t&aacute;c với c&aacute;c c&ocirc;ng ty cung cấp thiết bị linh kiện xuất xứ từ Mỹ, c&aacute;c thiết bị di động mới nhất của Huawei buộc l&ograve;ng kh&ocirc;ng được trang bị GMS. Tuy nhi&ecirc;n, doanh thu năm 2019 l&ecirc;n đến 86.1 tỷ USD, tăng 24% so với năm ngo&aacute;i đ&atilde; chứng tỏ Huawei l&agrave; một đối thủ đ&aacute;ng gờm, một chiến binh &ldquo;bất khả chiến bại&rdquo;.</h2>\r\n\r\n<h3><strong>Doanh thu Huawei sau lệnh cấm vận</strong></h3>\r\n\r\n<p>Huawei tiếp tục duy tr&igrave; thị phần mặc d&ugrave; bị đưa v&agrave;o danh s&aacute;ch đen của Mỹ. Trong 9 th&aacute;ng đầu năm 2019, doanh thu của Huawei đạt 610.8 tỷ nh&acirc;n d&acirc;n tệ (khoảng 86.1 tỷ USD), tăng 24.4% so với năm ngo&aacute;i. Tỷ suất lợi nhuận r&ograve;ng của qu&yacute; 3/2019 l&agrave; 8.7%</p>\r\n\r\n<p>Sản lượng smartphone của Huawei ph&acirc;n phối tr&ecirc;n to&agrave;n cầu l&ecirc;n đến hơn 185 triệu m&aacute;y, tăng 26% trong 3 qu&yacute; đầu năm 2019, gi&uacute;p &ocirc;ng tr&ugrave;m Huawei giữ vững vị tr&iacute; c&ocirc;ng ty mobile lớn thứ 2 tr&ecirc;n thế giới.&nbsp;</p>\r\n\r\n<p>Huawei đ&atilde; từng lo lắng doanh thu hằng năm sẽ giảm do những l&ugrave;m x&ugrave;m từ lệnh cấm vận của Mỹ, con số ước t&iacute;nh m&agrave; Huawei đưa ra l&agrave; 30 tỷ USD. Sự kiện n&agrave;y khiến cho Huawei kh&ocirc;ng thể tiếp tục hợp t&aacute;c với Google LLC, dẫn đến c&aacute;c thiết bị di động mới nhất của Huawei kh&ocirc;ng được hỗ trợ c&aacute;c bản cập nhật Android mới nhất, c&aacute;c ứng dụng phổ biến với người d&ugrave;ng như Gmail hay Google Maps.&nbsp;</p>\r\n\r\n<p>Tuy nhi&ecirc;n, theo kết quả b&aacute;o c&aacute;o từ c&ocirc;ng ty, những ngăn cấm n&agrave;y vẫn chẳng l&agrave;m suy giảm sự tăng trưởng của con &ldquo;chiến m&atilde;&rdquo; n&agrave;y.&nbsp;</p>\r\n\r\n<p>Theo c&ocirc;ng ty nghi&ecirc;n cứu IDC, l&ocirc; h&agrave;ng smartphone của Huawei năm 2019 chủ yếu được ph&acirc;n ph&aacute;t trong thị trường nội địa Trung Quốc, trong khi đ&oacute; doanh số b&aacute;n ra tr&ecirc;n thị trường quốc tế lại suy giảm. Phần trăm vận chuyển smartphone trong thị trường nội địa đạt 66% trong qu&yacute; 3/2019 nhưng chỉ chiếm 18% ở thị trường nước ngo&agrave;i.</p>\r\n\r\n<p><img alt=\"Sản lượng smartphone của Huawei phân phối trên toàn cầu\" src=\"https://cdn.tgdd.vn/Files/2019/11/13/1218463/1_800x778.jpg\" title=\"Sản lượng smartphone của Huawei phân phối trên toàn cầu\" /></p>\r\n\r\n<p>Trong b&agrave;i ph&aacute;t biểu tại hội nghị tin tức về đổi mới, chủ tịch Hội đồng quản trị William Xu khẳng định Huawei ho&agrave;n to&agrave;n c&oacute; thể tăng trưởng m&agrave; kh&ocirc;ng cần dựa v&agrave;o bất kỳ c&ocirc;ng nghệ n&agrave;o của Mỹ.&nbsp;</p>\r\n\r\n<p>&ldquo;Ch&uacute;ng t&ocirc;i ho&agrave;n to&agrave;n ch&agrave;o đ&oacute;n c&aacute;c c&ocirc;ng ty của Mỹ quay trở lại cung cấp c&aacute;c bộ phận linh kiện cho Huawei, nhưng nếu họ kh&ocirc;ng muốn, ch&uacute;ng t&ocirc;i vẫn sẽ tiếp tục duy tr&igrave; sự tăng trưởng của m&igrave;nh&rdquo;.</p>\r\n\r\n<p>Cụ thể, c&aacute;c d&ograve;ng điện thoại flagship của Huawei vẫn rất được người ti&ecirc;u d&ugrave;ng lựa chọn tại c&aacute;c khu vực ch&acirc;u &Aacute; v&agrave; ch&acirc;u &Acirc;u. Tại thị trường nội địa, chiếc điện thoại Mate 30 vừa mới ra mắt đ&atilde; trở th&agrave;nh sản phẩm best-seller, với con số b&aacute;n ra l&ecirc;n đến 1 triệu m&aacute;y ngay trong tuần đầu ti&ecirc;n sau khi ra mắt, v&agrave; tiếp tục tăng l&ecirc;n 3 triệu m&aacute;y trong tuần thứ 2.&nbsp;</p>\r\n\r\n<p>Ngo&agrave;i ra, chiếc smartphone trong ph&acirc;n kh&uacute;c tầm trung của Huawei l&agrave; Nova 5T may mắn được xuất xưởng v&agrave; xuất khẩu sang thị trường ch&acirc;u &Acirc;u. Chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"điện thoại\" type=\"điện thoại\">điện thoại</a>&nbsp;n&agrave;y vẫn được hỗ trợ GMS (Google Mobile Services) do được chứng nhận trước khi Huawei bị đưa v&agrave;o danh s&aacute;ch đen của Mỹ. Ở Việt Nam, với cấu h&igrave;nh mạnh mẽ, kết hợp RAM 8GB v&agrave; bộ nhớ trong 128GB, 4 camera si&ecirc;u &ldquo;chất&rdquo;, c&ocirc;ng nghệ sạc nhanh SuperCharge 22.5W,...Nova 5T đang &ldquo;ph&aacute; đảo&rdquo; thế giới di động trong ph&acirc;n kh&uacute;c tầm trung.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, kh&ocirc;ng qu&aacute; kh&aacute;c biệt so với c&aacute;c thị trường tr&ecirc;n quốc tế kh&aacute;c, tin tức Huawei kh&ocirc;ng c&ograve;n được Google hỗ trợ hệ điều h&agrave;nh Android, người d&ugrave;ng kh&ocirc;ng thể truy cập Google v&agrave; c&aacute;c ứng dụng kh&aacute;c như Gmail, Maps,...thị phần của Huawei tại Việt Nam c&oacute; dấu hiệu giảm s&acirc;u. V&agrave;o th&aacute;ng 6/2019, thị phần b&aacute;n h&agrave;ng trực tiếp của Huawei tại c&aacute;c cửa h&agrave;ng giảm từ 6.8% xuống c&ograve;n 1%. L&agrave;m chủ thị trường Việt Nam trong thời gian n&agrave;y c&oacute; thể kể đến Samsung v&agrave; Oppo.</p>\r\n\r\n<h3><strong>Chiến lược v&agrave; tầm nh&igrave;n xa của Huawei</strong></h3>\r\n\r\n<p><em><strong>Tự nghi&ecirc;n cứu v&agrave; ph&aacute;t triển c&ocirc;ng nghệ 5G&nbsp;</strong></em></p>\r\n\r\n<p>Đối mặt với th&aacute;ch thức từ lệnh cấm của Tổng thống Donald Trump, c&aacute;c nh&agrave; l&atilde;nh đạo Huawei buộc phải đưa ra những hướng đi mới cho c&aacute;c d&ograve;ng sản phẩm của m&igrave;nh. CEO Huawei tuy&ecirc;n bố những hạn chế đến từ lệnh cấm của Mỹ đang gi&uacute;p Huawei trở n&ecirc;n &ldquo;bất khả chiến bại&rdquo;.</p>\r\n\r\n<p>C&aacute;c chuy&ecirc;n gia c&ocirc;ng nghiệp của Huawei khẳng định c&ocirc;ng ty đang ph&aacute;t triển theo hướng &ldquo;tự cung tự cấp&rdquo;, từng bước kh&ocirc;ng phụ thuộc v&agrave;o c&ocirc;ng nghệ của Mỹ.&nbsp;</p>\r\n\r\n<p>Trong v&ograve;ng 1 thập kỷ qua, Huawei đ&atilde; tự chi 485 tỷ nh&acirc;n d&acirc;n tệ (65 tỷ USD) để nghi&ecirc;n cứu v&agrave; ph&aacute;t triển c&ocirc;ng nghệ 5G. &ldquo;&Ocirc;ng tr&ugrave;m&rdquo; Huawei hứa hẹn tương lai c&ocirc;ng nghệ 5G kh&ocirc;ng chỉ đem lại tốc độ truyền tải internet nhanh ch&oacute;ng m&agrave; c&ograve;n hỗ trợ c&ocirc;ng nghệ l&aacute;i xe tự động v&agrave; c&aacute;c ứng dụng kh&aacute;c trong tương lai.</p>\r\n\r\n<p>C&ugrave;ng với Ericsson, Nokia, Huawei cũng l&agrave; người ti&ecirc;n phong trong việc đầu tư v&agrave; ph&aacute;t triển c&aacute;c thiết bị hỗ trợ 5G. Từ năm 2009, Huawei đ&atilde; đầu tư 4 tỷ USD cho ng&agrave;nh n&agrave;y, sản xuất ra c&aacute;c trang thiết bị m&agrave; kh&ocirc;ng gắn m&aacute;c c&ocirc;ng nghệ Mỹ.</p>\r\n\r\n<p><img alt=\"Huawei tự chi để nghiên cứu và phát triển 5G\" src=\"https://cdn.tgdd.vn/Files/2019/11/13/1218463/2_800x450.jpg\" title=\"Huawei tự chi để nghiên cứu và phát triển 5G\" /></p>\r\n\r\n<p>Th&aacute;ng 8 vừa qua, Huawei cho ra mắt chiếc điện thoại đầu ti&ecirc;n được t&iacute;ch hợp 5G, đ&oacute; l&agrave; chiếc Huawei Mate 20 X, sử dụng bộ xử l&yacute; Kirin 980 v&agrave; chipset đa chế độ Balong 5000 5G từ c&ocirc;ng ty con HiSilicon của Huawei thay v&igrave; sử dụng chip của Qualcomm v&agrave; Intel.&nbsp;</p>\r\n\r\n<p><em><strong>C&ocirc;ng nghệ chip xử l&yacute; độc lập với c&ocirc;ng nghệ của Mỹ</strong></em></p>\r\n\r\n<p>Tại hội thảo Huawei Connect 2019, Huawei c&ocirc;ng bố chuyển sang hợp t&aacute;c với ARM Holdings của Anh sau khi th&ocirc;ng b&aacute;o c&ocirc;ng ty kh&ocirc;ng bị ảnh hưởng bởi danh s&aacute;ch thực thể của Mỹ. Sự hợp t&aacute;c n&agrave;y đưa Huawei dần rời xa khỏi c&aacute;c nh&agrave; cung cấp của Mỹ.</p>\r\n\r\n<p>C&aacute;c con chip độc quyền của Huawei như chip xử l&yacute; Kirin 990 tr&ecirc;n điện thoại v&agrave; chipset AI Ascend 910 đều được x&acirc;y dựng dựa tr&ecirc;n thiết kế từ ARM. Việc tiếp tục hợp t&aacute;c n&agrave;y đ&atilde; phần n&agrave;o giảm bớt g&aacute;nh nặng cho Huawei.&nbsp;</p>\r\n\r\n<p>Theo Reuters, ph&aacute;t ng&ocirc;n vi&ecirc;n của ARM cho biết &ldquo;ARM c&oacute; thể tiếp tục hỗ trợ HiSilicon với kiến tr&uacute;c ARM v8-A, cũng như thế hệ kế tiếp của kiến tr&uacute;c n&agrave;y, cả hai đều c&oacute; nguồn gốc từ Anh&rdquo;. V&igrave; thế, Huawei c&oacute; thể y&ecirc;n t&acirc;m hợp t&aacute;c ph&aacute;t triển với ARM.</p>\r\n\r\n<p><em><strong>Hệ điều h&agrave;nh ri&ecirc;ng biệt</strong></em></p>\r\n\r\n<p>Hơn hai năm trước đến nay, đội ngũ kỹ sư của Huawei đ&atilde; bắt tay tạo ra một hệ điều h&agrave;nh ri&ecirc;ng biệt cho c&aacute;c sản phẩm c&ocirc;ng nghệ c&oacute; t&ecirc;n l&agrave; HarmonyOS. C&oacute; thể n&oacute;i, HarmonyOS kh&ocirc;ng chỉ &aacute;p dụng cho ri&ecirc;ng smartphone. Thực tế, Huawei đang tập trung t&iacute;ch hợp hệ điều h&agrave;nh n&agrave;y cho tất cả c&aacute;c thiết bị kết nối internet của Huawei.&nbsp;</p>\r\n\r\n<p>HarmonyOS m&agrave; Huawei hướng đến l&agrave; một hệ sinh th&aacute;i m&atilde; nguồn đ&oacute;ng, hoạt động đa nền tảng tr&ecirc;n c&aacute;c thiết bị Huawei. Nếu như Apple c&oacute; hệ sinh th&aacute;i với khả năng tương t&aacute;c giữa MacOS, iOS v&agrave; iPadOS, th&igrave; Huawei c&oacute; HarmonyOS. Tuy nhi&ecirc;n, HarmonyOS mới chỉ được Huawei triển khai v&agrave; tr&ecirc;n thực tế, c&ocirc;ng ty n&agrave;y vẫn chưa c&oacute; kế hoạch cho ra mắt chiếc điện thoại đầu ti&ecirc;n chạy tr&ecirc;n hệ điều h&agrave;nh HarmonyOS.</p>\r\n\r\n<p><img alt=\"Huawei phát triển hệ điều hành riêng biệt\" src=\"https://cdn.tgdd.vn/Files/2019/11/13/1218463/3_800x450.jpg\" title=\"Huawei phát triển hệ điều hành riêng biệt\" /></p>\r\n\r\n<p>Trước mắt, nhiều nghi vấn đặt ra xung quanh hệ điều h&agrave;nh mới của Huawei. Liệu HarmonyOS c&oacute; tồn tại l&acirc;u d&agrave;i khi m&agrave; qu&aacute; nhiều h&atilde;ng trước đ&oacute; đ&atilde; từng thử v&agrave; thất bại như Samsung hay Nokia? Chiến lược để cạnh tranh với hai hệ điều h&agrave;nh phổ biến với người d&ugrave;ng như Android của Google v&agrave; iOS của Apple?</p>\r\n\r\n<p>Đ&acirc;y l&agrave; chiến lược mang t&iacute;nh chất &ldquo;trường kỳ&rdquo; của Huawei. C&oacute; thể sẽ phải gặp nhiều kh&oacute; khăn, nhưng với đội ngũ kỹ sư t&agrave;i năng của Huawei, kh&ocirc;ng biết chừng Huawei c&oacute; thể tạo n&ecirc;n &ldquo;kỳ t&iacute;ch&rdquo;?</p>\r\n\r\n<p>Trong khi thị phần smartphone của Huawei tr&ecirc;n thị trường Việt Nam bắt đầu giảm dần, vị thế của họ trong lĩnh vực thiết bị đeo tay đang dần chiếm ưu thế. Số lượng người d&ugrave;ng thiết bị đeo th&ocirc;ng minh Huawei đ&atilde; vượt ngưỡng con số 100.000.000 người.</p>\r\n\r\n<p>Thời điểm n&agrave;y, Huawei đang hợp t&aacute;c với Thế giới di động (TGDĐ) cho ra mắt d&ograve;ng smartwatch Watch GT2 series v&agrave;o th&aacute;ng 10 vừa qua. C&aacute;c chuy&ecirc;n gia trong ng&agrave;nh đ&aacute;nh gi&aacute; rằng Watch GT2 sẽ như &quot;hổ mọc th&ecirc;m c&aacute;nh&quot; khi Huawei bắt tay c&ugrave;ng TGDĐ. Sự hợp t&aacute;c n&agrave;y gi&uacute;p Huawei Watch GT2 dễ d&agrave;ng tiếp cận với c&aacute;c kh&aacute;ch h&agrave;ng tiềm năng v&agrave; c&oacute; khả năng chiếm lại thị phần như trước đ&acirc;y.</p>\r\n\r\n<p>Huawei từ một thương hiệu kh&ocirc;ng ai biết trở th&agrave;nh một trong những c&aacute;i t&ecirc;n phổ biến với tất cả người d&ugrave;ng c&ocirc;ng nghệ. Th&agrave;nh c&ocirc;ng của Huawei sau lệnh cấm vận c&oacute; thể coi như &ldquo;c&uacute; lội ngược d&ograve;ng&rdquo; ngoạn mục. Nhưng liệu th&agrave;nh c&ocirc;ng n&agrave;y c&oacute; k&eacute;o d&agrave;i v&agrave; sẽ tiếp tục tr&ecirc;n đ&agrave; ph&aacute;t triển h&atilde;y c&ugrave;ng chờ xem những h&agrave;nh động của Huawei ở những thay đổi chiến lực của h&atilde;ng.</p>\r\n', 'http://localhost:8080/ProjectWeb_1920/images/img-tintuc/4.jpg', 'Hồ Nguyễn Anh Phong', '2019-11-15', 1, 1);
INSERT INTO `news` VALUES (5, 'Huawei Mate X 5G cháy hàng chỉ trong 1 phút ở lần mở bán đầu tiên tại Trung Quốc', 'Tin tức', 'Hôm nay, Huawei đã tổ chức đợt bán đầu tiên của chiếc điện thoại Huawei Mate X 5G vào lúc 10:08 sáng (giờ địa phương) tại Trung Quốc.Thiết bị này...', '<h2>H&ocirc;m nay,&nbsp;<a href=\"https://www.thegioididong.com/dtdd-huawei\" target=\"_blank\" title=\"Huawei\" type=\"Huawei\">Huawei</a>&nbsp;đ&atilde; tổ chức đợt b&aacute;n đầu ti&ecirc;n của chiếc điện thoại&nbsp;<a href=\"https://www.thegioididong.com/dtdd/huawei-mate-x\" target=\"_blank\" title=\"Điện thoại Huawei Mate X\" type=\"Điện thoại Huawei Mate X\">Huawei Mate X</a>5G v&agrave;o l&uacute;c 10:08 s&aacute;ng (giờ địa phương) tại Trung Quốc.</h2>\r\n\r\n<p>Thiết bị n&agrave;y đ&atilde; nhận được sự đ&oacute;n nhận rất tốt từ ph&iacute;a người d&ugrave;ng trong lần b&aacute;n đầu ti&ecirc;n n&agrave;y, khi m&aacute;y b&aacute;n hết chỉ trong v&ograve;ng chưa đầy một ph&uacute;t. Sau đ&oacute;, c&ocirc;ng ty đ&atilde; x&aacute;c nhận rằng đợt b&aacute;n tiếp theo của Mate X 5G sẽ được tổ chức v&agrave;o ng&agrave;y 22/11 tới đ&acirc;y.</p>\r\n\r\n<p><img alt=\"Huawei Mate X 5G cháy hàng chỉ trong 1 phút ở lần mở bán đầu tiên tại Trung Quốc\" src=\"https://cdn.tgdd.vn/Files/2019/11/15/1219095/mate1_800x467.jpg\" title=\"Huawei Mate X 5G cháy hàng chỉ trong 1 phút ở lần mở bán đầu tiên tại Trung Quốc\" /></p>\r\n\r\n<p>Được biết, Huawei Mate X 5G được mở b&aacute;n trong h&ocirc;m nay l&agrave; phi&ecirc;n bản 8 GB RAM v&agrave; 512 GB ROM c&oacute; mức gi&aacute; 16.999 NDT (~ 2.420 USD), cũng như chỉ c&oacute; một m&agrave;u duy nhất l&agrave; Interstellar Blue.</p>\r\n\r\n<p>Điểm qua về một v&agrave;i th&ocirc;ng số cấu h&igrave;nh, Huawei Mate X 5G sở hữu m&agrave;n h&igrave;nh dẻo OLED 8 inch c&oacute; thể gập lại. M&aacute;y sử dụng bộ vi xử l&yacute; Kirin 980 với modem 5G Balong 5000, đi k&egrave;m hệ thống bốn camera 40 MP đến từ Leica, cũng như được t&iacute;ch hợp vi&ecirc;n pin 4.500 mAh c&oacute; hỗ trợ sạc nhanh 55 W.</p>\r\n\r\n<p>Ngo&agrave;i ra, chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"điện thoại\" type=\"điện thoại\">điện thoại</a>&nbsp;n&agrave;y cũng hỗ trợ 2 SIM, 4G VoLTE, Wi-Fi 802.11ac, Bluetooth 5.0, GPS, NFC v&agrave; USB-C.</p>\r\n\r\n<p>Bạn c&oacute; muốn sở hữu Huawei Mate X 5G kh&ocirc;ng? H&atilde;y để lại b&igrave;nh luận nh&eacute;.</p>\r\n\r\n<p>Nguồn:&nbsp;<a href=\"https://www.gizmochina.com/2019/11/15/huawei-mate-x-5g-sold-out-in-less-than-a-minute-in-first-sale-in-china/\" rel=\"nofollow\" target=\"_blank\" title=\"Gizmochina\" type=\"Gizmochina\">Gizmochina</a></p>\r\n', 'http://localhost:8080/ProjectWeb_1920/images/img-tintuc/5.jpg', 'Nguyễn Khắc Ngọc', '2019-11-15', 0, 1);
INSERT INTO `news` VALUES (6, 'Đọ trình chụp đêm: Google Pixel 4 XL, iPhone 11 Pro Max và Galaxy Note 10+', 'Tin tức', 'Pixel 4 XL, iPhone 11 Pro Max và Galaxy Note 10+ đều là những chiếc flagship mới, có khả năng chụp ảnh ấn tượng. Để thử thách bộ 3 siêu phẩm này,...', '<h2><span style=\"font-size:48px\">P</span><a href=\"https://www.thegioididong.com/dtdd/google-pixel-4-xl\" target=\"_blank\" title=\"Pixel 4 XL\" type=\"Pixel 4 XL\">ixel 4 XL</a>,&nbsp;<a href=\"https://www.thegioididong.com/dtdd/iphone-11-pro-max\" target=\"_blank\" title=\"iPhone 11 Pro Max\" type=\"iPhone 11 Pro Max\">iPhone 11 Pro Max</a>&nbsp;v&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-note-10-plus\" target=\"_blank\" title=\"Note 10+\" type=\"Note 10+\">Galaxy Note 10+</a>&nbsp;đều l&agrave; những chiếc flagship mới, c&oacute; khả năng chụp ảnh ấn tượng. Để thử th&aacute;ch bộ 3 si&ecirc;u phẩm n&agrave;y, chuy&ecirc;n trang&nbsp;<a href=\"https://www.phonearena.com/news/Google-Pixel-4-XL-vs-iPhone-11-Pro-Max-vs-Galaxy-Note-10-Low-light-Camera-Comparison_id120051\" rel=\"nofollow\" target=\"_blank\" title=\"Phonearena\" type=\"Phonearena\">Phonearena</a>&nbsp;đ&atilde; đưa ra một b&agrave;i so s&aacute;nh về khả năng chụp trong m&ocirc;i trường thiếu s&aacute;ng. V&agrave; ch&uacute;ng ta c&ugrave;ng xem trong m&ocirc;i trường khắc nghiệt n&agrave;y, đ&acirc;u l&agrave; chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"smartphone\" type=\"smartphone\">smartphone</a>&nbsp;l&agrave;m tốt nhất nh&eacute;!</h2>\r\n\r\n<p><img alt=\"So sánh camera\" src=\"https://cdn.tgdd.vn/Files/2019/11/12/1218248/so-sanh-camera-1_800x451.jpg\" title=\"So sánh camera\" /></p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/danh-gia-chi-tiet-google-pixel-4-1199365\" target=\"_blank\" title=\"Đánh giá Google Pixel 4 XL: Siêu phẩm \'mẫu mực\' cho Android Phone?\" type=\"Đánh giá Google Pixel 4 XL: Siêu phẩm \'mẫu mực\' cho Android Phone?\">Đ&aacute;nh gi&aacute; Google Pixel 4 XL: Si&ecirc;u phẩm &#39;mẫu mực&#39; cho Android Phone?</a></p>\r\n\r\n<h3><strong>Cảnh thứ 1</strong></h3>\r\n\r\n<p>Ở m&agrave;n đối đầu đầu ti&ecirc;n, Google Pixel 4 XL c&oacute; cho chất lượng h&igrave;nh ảnh ấn tượng nhất. Camera lấy n&eacute;t nhanh, độ chi tiết cao, c&acirc;n bằng trắng tốt nhưng thi&ecirc;n về t&ocirc;ng m&agrave;u lạnh.</p>\r\n\r\n<p><img alt=\"so sánh camera\" src=\"https://cdn.tgdd.vn/Files/2019/11/12/1218248/so-sanh-camera-5_800x355.jpg\" title=\"so sánh camera\" /></p>\r\n\r\n<p>Theo thứ tự từ tr&aacute;i qua phải ảnh chụp từ Pixel 4 XL, iPhone 11 Pro Max v&agrave; Galaxy Note 10+.</p>\r\n\r\n<p>iPhone 11 Pro Max cũng cho ra một bức ảnh đẹp, một cải tiến lớn so với những chiếc iPhone trước đ&acirc;y, nhưng lấy n&eacute;t kh&ocirc;ng được tốt. Độ chi tiết kh&ocirc;ng được cao, c&acirc;n bằng trắng thi&ecirc;n về t&ocirc;ng m&agrave;u v&agrave;ng, kh&ocirc;ng ph&ugrave; hợp đối với bối cảnh n&agrave;y.</p>\r\n\r\n<p>Crop ảnh 100% để xem cận cảnh hơn, ở đ&acirc;y mới thấy r&otilde; Pixel 4 XL vượt trội về độ chi tiết.</p>\r\n\r\n<p><img alt=\"so sánh camera\" src=\"https://cdn.tgdd.vn/Files/2019/11/12/1218248/so-sanh-camera-6_800x470.jpg\" title=\"so sánh camera\" /></p>\r\n\r\n<p>Ảnh đ&atilde; được crop lại v&agrave; ảnh tr&ecirc;n Pixel 4 XL sẽ l&agrave;m bạn h&agrave;i l&ograve;ng về độ chi tiết mang lại.</p>\r\n\r\n<p>C&ograve;n Galaxy Note 10+ cũng đ&atilde; chụp được một bức ảnh đẹp, nhưng kh&ocirc;ng thể so s&aacute;nh được với hai đối thủ c&ograve;n lại, ảnh c&oacute; độ chi tiết kh&ocirc;ng cao v&agrave; kh&oacute; khăn trong việc lấy n&eacute;t.</p>\r\n\r\n<p>Chấm điểm:</p>\r\n\r\n<ul>\r\n	<li>Pixel 4 XL: 7.</li>\r\n	<li>iPhone 11 Pro Max: 6.</li>\r\n	<li>Galaxy Note 10+: 5.5.</li>\r\n</ul>\r\n\r\n<h3><strong>Cảnh thứ 2</strong></h3>\r\n\r\n<p><img alt=\"So sánh camera\" src=\"https://cdn.tgdd.vn/Files/2019/11/12/1218248/so-sanh-camera-7_800x357.jpg\" title=\"So sánh camera\" /></p>\r\n\r\n<p>Theo thứ tự từ tr&aacute;i qua phải ảnh chụp từ Pixel 4 XL, iPhone 11 Pro Max v&agrave; Galaxy Note 10+.</p>\r\n\r\n<p>Sự kh&aacute;c biệt về c&acirc;n bằng trắng được thể hiện r&otilde; trong bức ảnh n&agrave;y, nhất l&agrave; chiếc &aacute;o cho&agrave;ng của người mẫu v&agrave; Pixel 4 XL vẫn l&agrave; thiết bị chụp ảnh ch&acirc;n thực nhất. Mặc d&ugrave; c&acirc;n bằng trắng tr&ecirc;n Pixel 4 XL vẫn thi&ecirc;n về t&ocirc;ng m&agrave;u lạnh, nhưng kh&ocirc;ng sai lệch nhiều với thực tế, hai sản phẩm c&ograve;n lại vẫn thi&ecirc;n về t&ocirc;ng m&agrave;u v&agrave;ng.</p>\r\n\r\n<p><img alt=\"So sánh camera\" src=\"https://cdn.tgdd.vn/Files/2019/11/12/1218248/so-sanh-camera-8_800x474.jpg\" title=\"So sánh camera\" /></p>\r\n\r\n<p>Tiếp tục crop ảnh để c&oacute; c&aacute;i nh&igrave;n cận cảnh hơn.</p>\r\n\r\n<p>Một lần nữa, Pixel 4 XL thu được nhiều chi tiết hơn so với hai thiết bị c&ograve;n lại. N&oacute;i ri&ecirc;ng về Galaxy Note 10+th&igrave; việc lấy n&eacute;t chủ thể kh&aacute; kh&oacute; khăn. Mặc d&ugrave; đ&atilde; cho th&ecirc;m thời gian v&agrave; bằng c&aacute;ch rất thủ c&ocirc;ng l&agrave; d&ugrave;ng ng&oacute;n tay chạm v&agrave;o phần chủ thể tr&ecirc;n ứng dụng camera nhưng kết quả vẫn kh&ocirc;ng mấy khả quan.</p>\r\n\r\n<p>Chấm điểm:</p>\r\n\r\n<ul>\r\n	<li>Pixel 4 XL: 8.</li>\r\n	<li>iPhone 11 Pro Max: 6.5.</li>\r\n	<li>Galaxy Note 10+: 5.5.</li>\r\n</ul>\r\n\r\n<h3><strong>Cảnh thứ 3</strong></h3>\r\n\r\n<p><img alt=\"So sánh camera\" src=\"https://cdn.tgdd.vn/Files/2019/11/12/1218248/so-sanh-camera-9_800x202.jpg\" title=\"So sánh camera\" /></p>\r\n\r\n<p>Theo thứ tự từ tr&aacute;i qua phải ảnh chụp từ Pixel 4 XL, iPhone 11 Pro Max v&agrave; Galaxy Note 10+.</p>\r\n\r\n<p>Điều lặp lại vẫn xảy ra, Pixel 4 XL vẫn c&acirc;n bằng trắng tốt thi&ecirc;n về t&ocirc;ng m&agrave;u lạnh, ch&acirc;n thực, chi tiết r&otilde; r&agrave;ng v&agrave; tinh tế. C&ograve;n Phone 11 Pro Max v&agrave; Galaxy Note 10+ vẫn cho t&ocirc;ng m&agrave;u ngả v&agrave;ng, thể hiện m&aacute;i t&oacute;c của c&ocirc; g&aacute;i c&oacute; phần nham nhở.</p>\r\n\r\n<p><img alt=\"So sánh camera\" src=\"https://cdn.tgdd.vn/Files/2019/11/12/1218248/so-sanh-camera-10_800x466.jpg\" title=\"So sánh camera\" /></p>\r\n\r\n<p>Khi crop lại th&igrave; ở phần t&oacute;c của chủ thể, Pixel 4 XL l&agrave;m tốt nhất, v&ugrave;ng da mặt vẫn r&otilde; n&eacute;t.</p>\r\n\r\n<p>Chấm điểm:</p>\r\n\r\n<ul>\r\n	<li>Pixel 4 XL: 8.</li>\r\n	<li>iPhone 11 Pro Max: 6.5.</li>\r\n	<li>Galaxy Note 10+: 5.5.</li>\r\n</ul>\r\n\r\n<h3><strong>Cảnh thứ 4</strong></h3>\r\n\r\n<p><img alt=\"So sánh camera\" src=\"https://cdn.tgdd.vn/Files/2019/11/12/1218248/so-sanh-camera-11_800x362.jpg\" title=\"So sánh camera\" /></p>\r\n\r\n<p>Theo thứ tự từ tr&aacute;i qua phải ảnh chụp từ Pixel 4 XL, iPhone 11 Pro Max v&agrave; Galaxy Note 10+.</p>\r\n\r\n<p>Ở lần đọ sức thứ 4, bạn thể thấy r&otilde; từng chi tiết da, sự sắc n&eacute;t tr&ecirc;n tấm ảnh được chụp từ Pixel 4 XL v&agrave; h&igrave;nh ảnh được l&agrave;m kh&aacute; nổi khối. Mặc kh&aacute;c, ảnh iPhone 11 Pro Max một lần nữa bị &aacute;m v&agrave;ng nhất l&agrave; m&agrave;u của &aacute;o cho&agrave;ng. C&ograve;n Galaxy Note 10+ t&aacute;i tạo m&agrave;u sắc đẹp nhưng h&igrave;nh ảnh lại kh&ocirc;ng được sắc n&eacute;t, độ chi tiết kh&ocirc;ng cao.</p>\r\n\r\n<p><img alt=\"So sánh camera\" src=\"https://cdn.tgdd.vn/Files/2019/11/12/1218248/so-sanh-camera-12_800x461.jpg\" title=\"So sánh camera\" /></p>\r\n\r\n<p>Tiếp tục crop cận cảnh, bạn th&iacute;ch m&agrave;u da ở h&igrave;nh ảnh n&agrave;o hơn?</p>\r\n\r\n<p>Chấm điểm:</p>\r\n\r\n<ul>\r\n	<li>Pixel 4 XL: 7.</li>\r\n	<li>iPhone 11 Pro Max: 6.5.</li>\r\n	<li>Galaxy Note 10+: 5.5.</li>\r\n</ul>\r\n\r\n<h3><strong>Cảnh thứ 5</strong></h3>\r\n\r\n<p><img alt=\"So sánh camera\" src=\"https://cdn.tgdd.vn/Files/2019/11/12/1218248/so-sanh-camera-13_800x203.jpg\" title=\"So sánh camera\" /></p>\r\n\r\n<p>Theo thứ tự từ tr&aacute;i qua phải ảnh chụp từ Pixel 4 XL, iPhone 11 Pro Max v&agrave; Galaxy Note 10+.</p>\r\n\r\n<p>Ở lần so s&aacute;nh cuối c&ugrave;ng c&aacute;ch t&aacute;i tạo m&agrave;u sắc cũng kh&ocirc;ng c&oacute; g&igrave; kh&aacute;c biệt nhưng Pixel 4 XL bị đ&aacute;nh gi&aacute; thấp hơn một ch&uacute;t cho khu&ocirc;n mặt hơi thiếu s&aacute;ng, c&ograve;n iPhone 11 Pro Max v&agrave; Galaxy Note 10+ cho khu&ocirc;n mặt chủ thể r&otilde; r&agrave;ng v&agrave; s&aacute;ng hơn.</p>\r\n\r\n<p>Chấm điểm:</p>\r\n\r\n<ul>\r\n	<li>Pixel 4 XL: 7.</li>\r\n	<li>iPhone 11 Pro Max: 7.</li>\r\n	<li>Galaxy Note 10+: 6.5.</li>\r\n</ul>\r\n\r\n<h3><strong>Kết luận chung</strong></h3>\r\n\r\n<p>Tổng điểm</p>\r\n\r\n<ul>\r\n	<li>Pixel 4 XL: 37.</li>\r\n	<li>iPhone 11 Pro Max: 32.5.</li>\r\n	<li>Galaxy Note 10+: 28.5.</li>\r\n</ul>\r\n\r\n<p>Trong năm nay sản phẩm của Google l&agrave; Pixel 4 XL tiếp tục thể hiện thế mạnh của m&igrave;nh khi chi tiết h&igrave;nh ảnh r&otilde; r&agrave;ng ngay cả trong điều kiện thiếu s&aacute;ng v&agrave; khả năng c&acirc;n bằng trắng ch&iacute;nh x&aacute;c nhất trong thế giới smartphone.</p>\r\n\r\n<p><img alt=\"So sánh camera\" src=\"https://cdn.tgdd.vn/Files/2019/11/12/1218248/so-sanh-camera-4_800x533.jpg\" title=\"So sánh camera\" /></p>\r\n\r\n<p>Năm nay, iPhone 11 Pro Max đ&atilde; tiệm cận khả năng chụp ảnh của d&ograve;ng Pixel về khoản ảnh chụp thiếu s&aacute;ng. D&ograve;ng iPhone trước đ&oacute; khả năng chụp ảnh thiếu s&aacute;ng kh&ocirc;ng được đầu tư nhưng năm nay điều n&agrave;y đ&atilde; được cải thiện r&otilde; rệt. Tuy nhi&ecirc;n, thiết bị của Apple vẫn c&acirc;n bằng trắng cho ra t&ocirc;ng m&agrave;u v&agrave;ng, một số người c&oacute; thể th&iacute;ch điều n&agrave;y nhưng về mặt kỹ thuật n&oacute; kh&ocirc;ng gần với m&agrave;u thực tế.</p>\r\n\r\n<p>Cuối c&ugrave;ng, Galaxy Note 10+ thua xa hai sản phẩm c&ograve;n lại về độ chi tiết, h&igrave;nh ảnh tr&ocirc;ng bị bệt, mờ. Nếu bạn muốn lựa chọn một smartphone c&oacute; khả năng chụp thiếu s&aacute;ng tốt trong năm 2019 n&agrave;y th&igrave; đừng ưu ti&ecirc;n chọn Galaxy Note 10+.</p>\r\n', 'http://localhost:8080/ProjectWeb_1920/images/img-tintuc/6.jpg', 'Vương Gia Bảo', '2019-11-15', 1, 1);
INSERT INTO `news` VALUES (7, 'Google có tính năng mới giúp bạn phát âm đúng những từ tiếng anh cực khó', 'Tin tức', 'Mọi người trên khắp thế giới sử dụng Google Search để đặt các câu hỏi liên quan đến ngôn ngữ, như định nghĩa một từ hoặc kiểm tra lại cách phát âm...', '<h2>Mọi người tr&ecirc;n khắp thế giới sử dụng Google Search để đặt c&aacute;c c&acirc;u hỏi li&ecirc;n quan đến ng&ocirc;n ngữ, như định nghĩa một từ hoặc kiểm tra lại c&aacute;ch ph&aacute;t &acirc;m của từ đ&oacute;. Hiểu được điều n&agrave;y, Google đ&atilde; tung ra một t&iacute;nh năng mới cho ph&eacute;p người d&ugrave;ng kiểm tra c&aacute;ch ph&aacute;t &acirc;m của c&aacute;c từ kh&oacute; nhờ v&agrave;o cơ chế M&aacute;y học.</h2>\r\n\r\n<p>C&aacute;c nh&agrave; nghi&ecirc;n cứu đ&atilde; chỉ ra rằng, việc thực h&agrave;nh c&aacute;ch đọc ch&iacute;nh x&aacute;c một từ c&oacute; thể gi&uacute;p người n&oacute;i ghi nhớ được từ đ&oacute;, đặc biệt l&agrave; khi bạn đang học một ng&ocirc;n ngữ mới. Trước đ&acirc;y, khi bạn t&igrave;m kiếm th&ocirc;ng tin về một từ, đại loại như c&aacute;ch ph&aacute;t &acirc;m quokka th&igrave; bạn c&oacute; thể nghe Google ph&aacute;t &acirc;m v&agrave; bạn đọc theo mặc d&ugrave; kh&ocirc;ng biết ch&iacute;nh x&aacute;c m&igrave;nh đ&atilde; đọc đ&uacute;ng hay sai.</p>\r\n\r\n<p><img alt=\"Google Search\" src=\"https://cdn.tgdd.vn/Files/2019/11/15/1219035/img_1170_800x450.jpg\" title=\"Google Search\" /></p>\r\n\r\n<p>Với t&iacute;nh năng ph&aacute;t &acirc;m mới, bạn c&oacute; thể thực h&agrave;nh trực tiếp tr&ecirc;n Google Search bằng c&aacute;ch n&oacute;i quakka v&agrave;o micr&ocirc;&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Smartphone \" type=\"Smartphone \">điện thoại</a>&nbsp;v&agrave; Google sẽ sử dụng AI để ph&acirc;n t&iacute;ch c&aacute;ch ph&aacute;t &acirc;m từ quakka của bạn. Sau đ&oacute;, hệ thống sẽ so s&aacute;nh c&aacute;c &acirc;m tiết bạn đọc với cơ sở dữ liệu của Google để x&aacute;c định bạn đ&atilde; đọc đ&uacute;ng từ đ&oacute; hay chưa.</p>\r\n\r\n<p>V&iacute; dụ, nếu bạn thực h&agrave;nh c&aacute;ch n&oacute;i từ asteisk trong tiếng anh, c&ocirc;ng nghệ nhận diện giọng n&oacute;i sẽ ph&acirc;n t&iacute;ch c&aacute;ch bạn ph&aacute;t &acirc;m v&agrave; sau đ&oacute;, n&oacute; nhận ra rằng &acirc;m tiết cuối c&ugrave;ng của từ asteisk được đọc l&agrave; &quot;rict&quot; thay v&igrave; &quot;uhsk&quot;.</p>\r\n\r\n<p><img alt=\"\" src=\"https://www.thegioididong.com/tin-tuc/google-co-tinh-nang-moi-giup-phat-am-dung-tu-tieng-anh-cuc-kho-1219035\" /></p>\r\n\r\n<p>Những tiến bộ của c&ocirc;ng nghệ nhận dạng giọng n&oacute;i v&agrave; hệ thống M&aacute;y học c&oacute; thể cải thiện c&aacute;ch ch&uacute;ng ta học một ng&ocirc;n ngữ mới. Với t&iacute;nh năng n&agrave;y, Google đ&atilde; cung cấp cho bạn một c&aacute;ch s&aacute;ng tạo, hiệu quả hơn để thực h&agrave;nh, h&igrave;nh dung v&agrave; ghi nhớ c&aacute;c từ vựng mới.</p>\r\n\r\n<p>T&iacute;nh năng n&agrave;y hiện chỉ khả dụng đối với ng&ocirc;n ngữ tiếng anh, nhưng Google x&aacute;c nhận rằng họ sẽ bổ sung th&ecirc;m nhiều ng&ocirc;n ngữ, giọng n&oacute;i v&agrave; v&ugrave;ng khu vực kh&aacute;c trong tương lai. Bạn đ&atilde; ph&aacute;t &acirc;m đ&uacute;ng từ quakka chưa?</p>\r\n\r\n<p>Nguồn:&nbsp;<a href=\"https://blog.google/products/search/how-do-you-pronounce-quokka-practice-search/?utm_source=fb&amp;utm_medium=social&amp;utm_campaign=og\" rel=\"nofollow\" target=\"_blank\" title=\"Google\" type=\"Google\">Google</a></p>\r\n', 'http://localhost:8080/ProjectWeb_1920/images/img-tintuc/7.jpg', 'Nguyễn Khắc Ngọc', '2019-11-14', 1, 1);
INSERT INTO `news` VALUES (8, 'Apple ra mắt ứng dụng Research giúp nghiên cứu, theo dõi tình hình sức khỏe cho phái đẹp', 'Tin tức', 'Apple đã chính thức phát hành ứng dụng Research lên App Store. Ứng dụng này có chức năng theo dõi nhịp tim, cường độ vận động, thính giác và sức...', '<h2>H&ocirc;m qua (14/11),&nbsp;Apple đ&atilde; ch&iacute;nh thức ph&aacute;t h&agrave;nh ứng dụng Research l&ecirc;n App Store. Ứng dụng n&agrave;y c&oacute; chức năng theo d&otilde;i nhịp tim, cường độ vận động, th&iacute;nh gi&aacute;c v&agrave; sức khỏe phụ nữ n&oacute;i chung.</h2>\r\n\r\n<p>C&aacute;c dữ liệu ghi lại được sẽ được chuyển đến c&aacute;c b&ecirc;n li&ecirc;n quan, cụ thể ở đ&acirc;y l&agrave; c&aacute;c viện nghi&ecirc;n cứu, bao gồm Trường Y tế cộng đồng Harvard T.H. Chan v&agrave; Viện khoa học Sức khỏe M&ocirc;i trường Quốc gia NIH.</p>\r\n\r\n<p>Hiện người d&ugrave;ng&nbsp;<a href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" title=\"iPhone\">iPhone&nbsp;</a>tại Mỹ đ&atilde; c&oacute; thể tải xuống ứng dụng Research v&agrave; đăng k&yacute; tham gia chương tr&igrave;nh nghi&ecirc;n cứu cụ thể. (Tải ứng dụng Apple Research&nbsp;<a href=\"https://apps.apple.com/us/app/apple-research/id1463884356\" rel=\"nofollow\" target=\"_blank\" title=\"Research\">tại đ&acirc;y</a>)</p>\r\n\r\n<p><img alt=\"Giao diện ứng dụng Research\" src=\"https://cdn.tgdd.vn/Files/2019/11/15/1219036/apple-research-app-trio-800x475_800x475.jpg\" title=\"Giao diện ứng dụng Research\" /></p>\r\n\r\n<p>Giao diện ứng dụng Research</p>\r\n\r\n<p>Apple nhấn mạnh rằng ứng dụng Research chỉ chia sẻ những dữ liệu khi đ&atilde; c&oacute; sự chấp thuận từ người d&ugrave;ng. Ứng dụng sẽ giải th&iacute;ch r&otilde; r&agrave;ng c&aacute;ch m&agrave; những dữ liệu n&agrave;y sẽ được sử dụng v&agrave; người d&ugrave;ng c&oacute; quyền kiểm so&aacute;t được điều n&agrave;y.</p>\r\n\r\n<p>H&atilde;ng c&ograve;n n&oacute;i th&ecirc;m, c&aacute;c dữ liệu n&agrave;y sẽ được bảo mật tuyệt đối v&agrave; chỉ phục vụ cho mục ti&ecirc;u nghi&ecirc;n cứu, kh&ocirc;ng b&aacute;n cho c&aacute;c b&ecirc;n thứ ba.</p>\r\n\r\n<p>Với sự &aacute;p đảo thị phần của&nbsp;<a href=\"https://www.thegioididong.com/dong-ho-thong-minh-apple\" target=\"_blank\" title=\"Apple Watch\">Apple Watch</a>&nbsp;như hiện nay, bạn nghĩ Apple c&oacute; thể sẽ lấn s&acirc;n sang lĩnh vực chăm s&oacute;c sức khỏe trong tương lai?</p>\r\n\r\n<p>Nguồn:&nbsp;<a href=\"https://www.macrumors.com/2019/11/14/apple-research-app-released/?fbclid=IwAR2Z1QCoorabIfLrMvYPwwyqhWLiH1SNHllwZFBfRSyc_bxlBUL6pAPUKkM\" rel=\"nofollow\" target=\"_blank\" title=\"Nguồn\">MacRumors</a></p>\r\n', 'http://localhost:8080/ProjectWeb_1920/images/img-tintuc/8.jpg', 'Nguyễn Khắc Ngọc', '2019-11-14', 0, 1);
INSERT INTO `news` VALUES (9, 'Trên tay nhanh Macbook Pro 16 inch: Cuối cùng bàn phím đã \'thay đổi\'', 'Tin tức', 'Như vậy là Apple đã âm thầm trình làng mẫu MacBook Pro 16 inch sẽ thay thế cho phiên bản 15 inch, điểm thay đổi lớn nhất là bàn phím mới cùng khả...', '<h2>hư vậy l&agrave;&nbsp;<a href=\"https://www.thegioididong.com/laptop-apple-macbook\" target=\"_blank\" title=\"Apple\" type=\"Apple\">Apple</a>&nbsp;đ&atilde; &acirc;m thầm tr&igrave;nh l&agrave;ng mẫu&nbsp;<a href=\"https://www.thegioididong.com/laptop/apple-macbook-pro-touch-mr9q2sa-a-2018\" target=\"_blank\" title=\"MacBook Pro\" type=\"MacBook Pro\">MacBook Pro</a>&nbsp;16 inch&nbsp;sẽ thay thế cho phi&ecirc;n bản 15 inch, điểm thay đổi lớn nhất l&agrave; b&agrave;n ph&iacute;m mới c&ugrave;ng khả năng tản nhiệt. Ngay b&acirc;y giờ, h&atilde;y c&ugrave;ng tr&ecirc;n tay v&agrave; đ&aacute;nh gi&aacute; nhanh chiếc laptop c&oacute; gi&aacute; kh&aacute;-đau-&quot;thận&quot; n&agrave;y th&ocirc;ng qua g&oacute;c nh&igrave;n của&nbsp;<a href=\"https://www.laptopmag.com/reviews/16-inch-macbook-pro\" rel=\"nofollow\" target=\"_blank\" title=\"laptopmag\" type=\"laptopmag\">laptopmag</a>&nbsp;nh&eacute;!</h2>\r\n\r\n<p><img alt=\"Macbook Pro 16 inch\" src=\"https://cdn.tgdd.vn/Files/2019/11/14/1218645/macbook-1_800x342.jpg\" title=\"Macbook Pro 16 inch\" /></p>\r\n\r\n<p>Macbook Pro 16 inch c&oacute; gi&aacute; rất đắt đỏ v&agrave; ch&uacute;ng ta c&ugrave;ng kh&aacute;m ph&aacute; sản phẩm n&agrave;y nh&eacute;!</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/macbook-pro-2018-co-phai-la-ban-nang-cap-dang-gia-1101327\" target=\"_blank\" title=\"Đánh giá Macbook Pro 2018: Có phải là bản nâng cấp đáng giá?\" type=\"Đánh giá Macbook Pro 2018: Có phải là bản nâng cấp đáng giá?\">Đ&aacute;nh gi&aacute; Macbook Pro 2018: C&oacute; phải l&agrave; bản n&acirc;ng cấp đ&aacute;ng gi&aacute;?</a></p>\r\n\r\n<h3><strong>B&agrave;n ph&iacute;m&nbsp;Magic Keyboard mới</strong></h3>\r\n\r\n<p>Điều đầu ti&ecirc;n m&agrave; bạn ch&uacute; &yacute; đến chiếc MacBook Pro 16 inch đ&oacute; l&agrave; k&iacute;ch thước m&agrave;n h&igrave;nh lớn nhưng thứ k&iacute;ch th&iacute;ch người d&ugrave;ng chạm v&agrave;o đ&oacute; l&agrave; b&agrave;n ph&iacute;m&nbsp;Magic Keyboard mới. Đ&acirc;y l&agrave; kiểu thiết kế b&agrave;n ph&iacute;m mới thay cho cơ chế Butterfly (c&aacute;nh bướm) vốn bị ph&agrave;n n&agrave;n rất nhiều.</p>\r\n\r\n<p>B&agrave;n ph&iacute;m của MacBook Pro 16 inch sử dụng cơ chế cắt k&eacute;o truyền thống, h&agrave;nh tr&igrave;nh ph&iacute;m 1mm cho cảm gi&aacute;c s&acirc;u, thật v&agrave; bớt ồn hơn so với b&agrave;n ph&iacute;m&nbsp;Butterfly.</p>\r\n\r\n<p><img alt=\"Macbook Pro 16 inch\" src=\"https://cdn.tgdd.vn/Files/2019/11/14/1218645/macbook-pro-16inch-1_800x449.jpg\" title=\"Macbook Pro 16 inch\" /></p>\r\n\r\n<p>B&agrave;n ph&iacute;m tr&ecirc;n Macbook Pro 16 inch đ&atilde; được cải thiện mang lại cảm gi&aacute;c g&otilde; thoải m&aacute;i, dễ chịu.</p>\r\n\r\n<p>Cảm gi&aacute;c g&otilde; mềm mại nhưng vẫn chắc chắn, kh&ocirc;ng phải kiểu nằm phẳng c&oacute; phần th&ocirc; cứng như b&agrave;n ph&iacute;m&nbsp;Butterfly. Thử nghiệm thực tế của bi&ecirc;n tập vi&ecirc;n laptopmag cho thấy l&agrave; b&agrave;n ph&iacute;m của&nbsp;MacBook Pro 16 inch cho cảm gi&aacute;c thoải m&aacute;i đến nỗi kh&ocirc;ng muốn quay lại sử dụng b&agrave;n ph&iacute;m tr&ecirc;n MacBook Pro 13 inch nữa.&nbsp;</p>\r\n\r\n<p><img alt=\"Macbook Pro 16 inch\" src=\"https://cdn.tgdd.vn/Files/2019/11/14/1218645/macbook-pro-3_800x430.jpg\" title=\"Macbook Pro 16 inch\" /></p>\r\n\r\n<p>Một điều th&uacute; vị l&agrave;&nbsp;Apple cũng thay đổi thiết kế từng ph&iacute;m, hỏng ph&iacute;m n&agrave;o th&igrave; thay ph&iacute;m đ&oacute; kh&ocirc;ng phải thay nguy&ecirc;n cả cụm tốn k&eacute;m.&nbsp;</p>\r\n\r\n<p><img alt=\"Macbook Pro 16 inch\" src=\"https://cdn.tgdd.vn/Files/2019/11/14/1218645/macbook-pro-4_800x443.jpg\" title=\"Macbook Pro 16 inch\" /></p>\r\n\r\n<p>Ph&iacute;m vật l&yacute; Escape đ&atilde; quay trở lại nằm ở g&oacute;c tr&aacute;i tr&ecirc;n c&ugrave;ng.</p>\r\n\r\n<p><img alt=\"Macbook Pro 16 inch\" src=\"https://cdn.tgdd.vn/Files/2019/11/14/1218645/macbook-pro-5_800x428.jpg\" title=\"Macbook Pro 16 inch\" /></p>\r\n\r\n<p>Ph&iacute;m điều hướng h&igrave;nh chữ T.</p>\r\n\r\n<p>Apple cũng lắng nghe kh&aacute;ch h&agrave;ng bằng c&aacute;ch th&ecirc;m lại ph&iacute;m vật l&yacute; Escape v&agrave; thiết lập ph&iacute;m điều hướng h&igrave;nh chữ T, với k&iacute;ch thước ph&iacute;m bằng nhau từ đ&oacute; gi&uacute;p việc di chuyển con trỏ dễ d&agrave;ng hơn. Ngo&agrave;i ra, phần TouchBar của MacBook Pro 16 inch đ&atilde; được thu hẹp lại hơn một ch&uacute;t nhưng kh&ocirc;ng ảnh hưởng nhiều về mặt trải nghiệm.</p>\r\n\r\n<h3><strong>M&agrave;n h&igrave;nh hiển thị</strong></h3>\r\n\r\n<p>M&agrave;n h&igrave;nh của Macbook Pro 16 inch&nbsp;l&agrave; một trong những m&agrave;n h&igrave;nh c&oacute; độ s&aacute;ng cao nhất đạt 500 nits, độ ph&acirc;n giải 3.072 x 1.920 v&agrave; dải m&agrave;u rộng DCI-P3 v&agrave; hỗ trợ t&iacute;nh năng True Tone.</p>\r\n\r\n<p><img alt=\"Macbook Pro 16 inch\" src=\"https://cdn.tgdd.vn/Files/2019/11/14/1218645/macbook-pro-11_800x450.jpg\" title=\"Macbook Pro 16 inch\" /></p>\r\n\r\n<p>Macbook Pro 16 inch cho chất lượng hiển thị rất tốt, viền bezels hai b&ecirc;n kh&aacute; mỏng.</p>\r\n\r\n<p>Với c&aacute;c phần viền mỏng hơn th&igrave; khi xem nội dung, giải tr&iacute; hay chỉnh sửa video tr&ecirc;n&nbsp;Macbook Pro 16 inch sẽ đ&atilde; &quot;hơn&quot;, kết hợp với tốc độ l&agrave;m tươi nhanh th&igrave; trải nghiệm h&igrave;nh ảnh chẳng hạn khi xem đoạn trailer một bộ phim n&agrave;o đ&oacute; cho cảm gi&aacute;c kh&aacute; sống động.</p>\r\n\r\n<p>Đ&aacute;ng tiếc m&agrave;n h&igrave;nh n&agrave;y kh&ocirc;ng hỗ trợ độ ph&acirc;n giải, Apple c&oacute; thể đ&atilde; chọn độ ph&acirc;n giải&nbsp;3.072 x 1.920 để&nbsp;tiết kiệm pin, nhưng những người d&ugrave;ng chuy&ecirc;n nghiệp vẫn sẽ th&iacute;ch m&agrave;n h&igrave;nh 4K hơn.</p>\r\n\r\n<h3><strong>Thiết kế tổng thể&nbsp;</strong></h3>\r\n\r\n<p>MacBook Pro 16 inch sở hữu ngoại h&igrave;nh rất giống với những chiếc MacBook Pro kh&aacute;c, ho&agrave;n thiện bằng nh&ocirc;m chắc chắn với hai t&ugrave;y chọn m&agrave;u sắc Bạc v&agrave; X&aacute;m kh&ocirc;ng gian, b&agrave;n di chuột lớn.&nbsp;</p>\r\n\r\n<p><img alt=\"Macbook Pro 16 inch\" src=\"https://cdn.tgdd.vn/Files/2019/11/14/1218645/macbook-pro-16-inch_800x343.jpg\" title=\"Macbook Pro 16 inch\" /></p>\r\n\r\n<p>Macbook Pro 16 inch hơi d&agrave;y v&agrave; nặng một ch&uacute;t so với phi&ecirc;n bản 15 inch.</p>\r\n\r\n<p>Sự kh&aacute;c biệt đ&aacute;ng ch&uacute; &yacute; duy nhất của Macbook Pro 16 inch so với phi&ecirc;n bản 15 inch l&agrave; viền bezels ở hai b&ecirc;n mỏng hơn, nhưng vẫn tương đối d&agrave;y khi so s&aacute;nh với Dell XPS 15.</p>\r\n\r\n<p><img alt=\"Macbook Pro 16 inch\" src=\"https://cdn.tgdd.vn/Files/2019/11/14/1218645/macbook-pro-9_800x458.jpg\" title=\"Macbook Pro 16 inch\" /></p>\r\n\r\n<p>Macbook Pro 16 inch c&oacute; viền bezels mỏng, quyến rũ.</p>\r\n\r\n<p><img alt=\"Macbook Pro 16 inch\" src=\"https://cdn.tgdd.vn/Files/2019/11/14/1218645/macbook-pro-2_800x432.jpg\" title=\"Macbook Pro 16 inch\" /></p>\r\n\r\n<p>Macbook Pro 16 inch được ho&agrave;n thiện bằng nh&ocirc;m v&agrave; đương nhi&ecirc;n logo quả t&aacute;o kh&ocirc;ng ph&aacute;t s&aacute;ng.</p>\r\n\r\n<p>K&iacute;ch thước tổng thể của MacBook Pro 16 inch cũng ho&agrave;n to&agrave;n tương tự phi&ecirc;n bản 15 inch, nhưng nặng hơn 130 gram v&agrave; d&agrave;y hơn 0.76 mm. Trọng lượng ch&ecirc;nh lệch l&agrave; do c&oacute; vi&ecirc;n pin dung lượng lớn hơn (100 Wh) cho thời gian sử dụng 11 giờ, thời lượng tốt hơn MacBook Pro 15 inch.</p>\r\n\r\n<h3><strong>&Acirc;m thanh được n&acirc;ng cấp</strong></h3>\r\n\r\n<p>Hệ thống &acirc;m thanh của MacBook Pro 16 inch c&ograve;n ấn tượng hơn cả m&agrave;n h&igrave;nh. Tại 2&nbsp;cạnh b&agrave;n ph&iacute;m t&iacute;ch hợp 6 d&atilde;y loa ngo&agrave;i cho chất lượng &acirc;m thanh tốt. &Acirc;m thanh của hệ thống loa n&agrave;y kh&ocirc;ng ch&ecirc; v&agrave;o đ&acirc;u được nhất l&agrave; &acirc;m bass c&oacute; lực, &acirc;m lượng tối đa &acirc;m thanh cũng kh&ocirc;ng bị m&eacute;o, độ chi tiết cao.</p>\r\n\r\n<p><img alt=\"Macbook Pro 16 inch\" src=\"https://cdn.tgdd.vn/Files/2019/11/14/1218645/macbook-pro-8_800x460.jpg\" title=\"Macbook Pro 16 inch\" /></p>\r\n\r\n<p>Macbook Pro 16 inch cho chất lượng &acirc;m thanh đ&aacute;ng kinh ngạc.</p>\r\n\r\n<p>Ngo&agrave;i ra, Macbook Pro 16 inch c&ograve;n được hỗ trợ&nbsp;3 micro c&oacute; chất lượng ngang với micro ở ph&ograve;ng thu &acirc;m để gọi video, ra lệnh cho Siri, tiếng r&iacute;t được giảm 40%, hệ thống micro n&agrave;y c&oacute; thể cạnh tranh được với micro thu &acirc;m Blue&nbsp;Yeti chuy&ecirc;n dụng.</p>\r\n\r\n<h3><strong>Cấu h&igrave;nh phần cứng</strong></h3>\r\n\r\n<p>MacBook Pro 16 inch c&oacute; cấu h&igrave;nh rất đ&aacute;ng nể, người d&ugrave;ng c&oacute; thể lựa chọn phi&ecirc;n bản CPU Intel Core i7 thế hệ thứ 9 (6 nh&acirc;n) hoặc Core i9 (8 nh&acirc;n), bộ nhớ RAM thấp nhất l&agrave;&nbsp;16 GB v&agrave; cao nhất l&ecirc;n đến 64 GB, dung lượng bộ nhớ 512 GB SSD v&agrave; c&oacute; thể l&ecirc;n tới 8 TB, bạn kh&ocirc;ng phải nh&igrave;n nhầm đ&acirc;u.</p>\r\n\r\n<p><img alt=\"Macbook Pro 16 inch\" src=\"https://cdn.tgdd.vn/Files/2019/11/14/1218645/macbook-pro-13_800x382.jpg\" title=\"Macbook Pro 16 inch\" /></p>\r\n\r\n<p>Macbook Prp 16 inch c&oacute; sức mạnh phần cứng đ&aacute;ng nể, xử l&yacute; mọi c&ocirc;ng việc một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<p>MacBook Pro 16 inch c&oacute; thể xử l&yacute; mọi c&ocirc;ng việc một c&aacute;ch dễ d&agrave;ng,&nbsp;card đồ họa tr&ecirc;n phi&ecirc;n bản mới cũng n&acirc;ng cấp từ AMD Radeon Pro 555X l&ecirc;n 5300M (hoặc 5500M) với bộ nhớ GDDR6 4 GB (c&oacute; thể n&acirc;ng cấp l&ecirc;n 8 GB).&nbsp;Apple chia sẻ rằng hiệu suất của chiếc Macbook mới nhanh hơn tới 2.1 lần với cấu h&igrave;nh ti&ecirc;u chuẩn v&agrave; nhanh hơn tới 80% cho t&ugrave;y chọn cao cấp nhất.</p>\r\n\r\n<p><img alt=\"Macbook Pro 16 inch\" src=\"https://cdn.tgdd.vn/Files/2019/11/14/1218645/macbook-pro-7_800x419.jpg\" title=\"Macbook Pro 16 inch\" /></p>\r\n\r\n<p>Cạnh phải của Macbook Pro 16 inch l&agrave; 2 cổng USB-C, cổng cắm tai nghe 3.5mm.</p>\r\n\r\n<p><img alt=\"Macbook Pro 16 inch\" src=\"https://cdn.tgdd.vn/Files/2019/11/14/1218645/macbook-pro-6_800x423.jpg\" title=\"Macbook Pro 16 inch\" /></p>\r\n\r\n<p>Cạnh tr&aacute;i cũng tiếp tục l&agrave; 2 cổng USB-C nữa.</p>\r\n\r\n<p>Về cổng kết nối, MacBook Pro 16 inch vẫn trang bị 4 cổng USB-C chuẩn Thunderbolt 3 v&agrave; cổng tai nghe 3.5 mm. Bạn cũng kh&ocirc;ng cần lo lắng về thời lượng pin m&agrave; sản phẩm c&oacute; thể đ&aacute;p ứng được bởi với vi&ecirc;n pin 100 Wh, c&oacute; thể cho thời lượng sử dụng được 11 tiếng sau mỗi lần sạc đầy.&nbsp;</p>\r\n\r\n<p>Ngo&agrave;i ra,&nbsp;Apple đ&atilde; thiết kế lại to&agrave;n bộ hệ thống tản nhiệt, bo mạch chủ v&agrave; loa ngo&agrave;i cũng được tối ưu gi&uacute;p luồng nhiệt thổi qua gi&uacute;p nhiệt độ tr&ecirc;n chiếc laptop được kiểm so&aacute;t, kh&ocirc;ng qu&aacute; cao g&acirc;y kh&oacute; chịu.</p>\r\n\r\n<h3><strong>Kết luận chung</strong></h3>\r\n\r\n<p>Chung quy lại, Macbook Pro 16 inch c&oacute; nhược điểm l&agrave; nặng v&agrave; d&agrave;y hơn một ch&uacute;t so với MacBook Pro 15 inch. Người d&ugrave;ng kỳ vọng sản phẩm n&agrave;y c&oacute; Face ID để mở kh&oacute;a hơn mặc d&ugrave; Touch ID cũng kh&aacute; dễ sử dụng, kh&ocirc;ng hỗ trợ độ ph&acirc;n giải 4K, vẫn thiếu cổng USB-A v&agrave; khe cắm thẻ nhớ.&nbsp;</p>\r\n\r\n<p><img alt=\"Macbook Pro 16 inch\" src=\"https://cdn.tgdd.vn/Files/2019/11/14/1218645/macbook-pro-1_800x528.jpg\" title=\"Macbook Pro 16 inch\" /></p>\r\n\r\n<p>Nhưng MacBook Pro 16 inch thực sự l&agrave; một chiếc laptop c&oacute; thể l&agrave;m thỏa m&atilde;n người d&ugrave;ng chuy&ecirc;n nghiệp nhất l&agrave; c&ocirc;ng việc thiết kế đồ họa v&agrave; mong muốn một trải nghiệm g&otilde; ph&iacute;m tốt hơn.</p>\r\n', 'http://localhost:8080/ProjectWeb_1920/images/img-tintuc/9.jpg', 'Hồ Nguyễn Anh Phong', '2019-11-14', 1, 1);
INSERT INTO `news` VALUES (10, 'Apple phát hành bản cập nhật firmware đầu tiên cho AirPods Pro', 'Tin tức', 'Apple mới đây đã phát hành bản cập nhật firmware (phần mềm) đầu tiên cho người dùng AirPods Pro. Bản cập nhật đang được triển khai đến tất cả...', '<h2>Apple mới đ&acirc;y đ&atilde; ph&aacute;t h&agrave;nh bản cập nhật firmware (phần mềm) đầu ti&ecirc;n cho người d&ugrave;ng&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe/tai-nghe-bluetooth-airpods-pro-apple-mwp22\" target=\"_blank\" title=\"AirPods Pro\" type=\"AirPods Pro\">AirPods Pro</a>. Bản cập nhật đang được triển khai đến tất cả thiết bị v&agrave; được c&agrave;i đặt tự động.</h2>\r\n\r\n<p>Bản cập nhật thay đổi firmware đầu ti&ecirc;n cho AirPods Pro c&oacute; m&atilde; &quot;2B588&quot; thay cho phi&ecirc;n bản &quot;2B584&quot; đ&atilde; được c&agrave;i sẵn tr&ecirc;n thiết bị khi ph&aacute;t h&agrave;nh. Apple thường kh&ocirc;ng cung cấp chi tiết bản cập nhật d&agrave;nh cho phụ kiện, nhưng bản cập nhật cho AirPods Pro lần n&agrave;y c&oacute; thể l&agrave; tr&igrave;nh sửa lỗi hoặc một số hiệu chỉnh &acirc;m thanh.</p>\r\n\r\n<p>Bản cập nhật sẽ được tự động c&agrave;i đặt khi AirPods Pro kết nối với&nbsp;<a href=\"https://www.thegioididong.com/thu-cu-doi-moi\" target=\"_blank\" title=\"iPhone\" type=\"iPhone\">iPhone</a>&nbsp;hoặc&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad\" target=\"_blank\" title=\"iPad\" type=\"iPad\">iPad</a>. Tuy nhi&ecirc;n, người d&ugrave;ng cần giữ AirPods Pro trong hộp sạc cũng như kết nối với nguồn điện để cập nhật. Bạn c&oacute; thể kiểm tra số phi&ecirc;n bản phần mềm bằng c&aacute;ch truy cập&nbsp;<strong><em>C&agrave;i đặt (Setting) &gt; C&agrave;i đặt chung (General) &gt; Chi tiết (About) &gt; AirPods Pro</em></strong>.</p>\r\n\r\n<p>Bạn đ&atilde; cập nhật AirPods Pro của m&igrave;nh l&ecirc;n phi&ecirc;n bản mới nhất chưa?</p>\r\n\r\n<p>Nguồn:&nbsp;<a href=\"https://appleinsider.com/articles/19/11/14/airpods-pro-gain-first-firmware-update-since-release\" rel=\"nofollow\" target=\"_blank\" title=\"Apple Insider\" type=\"Apple Insider\">Apple Insider</a></p>\r\n', 'http://localhost:8080/ProjectWeb_1920/images/img-tintuc/10.jpg', 'Nguyễn Khắc Ngọc', '2019-11-14', 0, 1);
INSERT INTO `news` VALUES (11, 'Cách chặn quảng cáo xuất hiện trên iPhone không cần Jailbreak', 'Thủ thuật', 'Quảng cáo không chỉ ảnh hưởng đến trải nghiệm chơi game, xem video, nó còn khiến tiêu hao kha khá dữ liệu di động của người dùng. Do đó, chặn...', '<h2>Quảng c&aacute;o kh&ocirc;ng chỉ ảnh hưởng đến trải nghiệm chơi game, xem video, n&oacute; c&ograve;n khiến ti&ecirc;u hao kha kh&aacute; dữ liệu di động của người d&ugrave;ng. Do đ&oacute;, chặn quảng c&aacute;o xuất hiện l&agrave; một điều cần thiết, c&ugrave;ng xem c&aacute;ch thực hiện dưới đ&acirc;y d&agrave;nh cho&nbsp;<a href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" title=\"iPhone\" type=\"iPhone\">iPhone</a>&nbsp;nh&eacute;.</h2>\r\n\r\n<p>C&oacute; nhiều ứng dụng hỗ trợ người d&ugrave;ng chặn quảng c&aacute;o xuất hiện tr&ecirc;n iPhone, với cơ chế hoạt động kh&aacute; giống nhau l&agrave; th&ecirc;m cấu h&igrave;nh VPN v&agrave;o C&agrave;i đặt của iPhone. Trong b&agrave;i viết n&agrave;y, m&igrave;nh sẽ sử dụng đến Adblock Mobile.</p>\r\n\r\n<ul>\r\n	<li><a href=\"https://itunes.apple.com/vn/app/adblock-mobile-best-ad-blocker-to-block-ads/id1121734569?l=vi&amp;mt=8\" rel=\"nofollow\" target=\"_blank\" title=\"Tải về Adblock Mobile miễn phí trên Appstore.\" type=\"Tải về Adblock Mobile miễn phí trên Appstore.\">Tải về&nbsp;</a><a href=\"https://itunes.apple.com/vn/app/adblock-mobile-best-ad-blocker-to-block-ads/id1121734569?l=vi&amp;mt=8\" rel=\"nofollow\" target=\"_blank\" title=\"Adblock Mobile\" type=\"Adblock Mobile\">Adblock Mobile</a><a href=\"https://itunes.apple.com/vn/app/adblock-mobile-best-ad-blocker-to-block-ads/id1121734569?l=vi&amp;mt=8\" rel=\"nofollow\" target=\"_blank\" title=\"Tải về Adblock Mobile miễn phí trên Appstore.\" type=\"Tải về Adblock Mobile miễn phí trên Appstore.\">&nbsp;miễn ph&iacute; tr&ecirc;n Appstore.</a></li>\r\n</ul>\r\n\r\n<p>Sau khi tải về Adblock Mobile, bật ứng dụng l&ecirc;n v&agrave; chọn&nbsp;<strong>Get started &gt; Next</strong>.</p>\r\n\r\n<p><img alt=\"ảnh\" src=\"https://cdn.tgdd.vn/Files/2018/07/10/1100559/z1046125306256_0ebd3b6c8a1fb2b9427b5b089bd802b9-_2253x1334-800-resize.jpg\" title=\"ảnh\" /></p>\r\n\r\n<p>Chọn tiếp&nbsp;<strong>Enable Adblock</strong>, ngay sau đ&oacute; ứng dụng sẽ hỏi quyền th&ecirc;m cấu h&igrave;nh VPN mới v&agrave;o iPhone, chọn&nbsp;<strong>Allow</strong>.&nbsp;</p>\r\n\r\n<p><img alt=\"ảnh\" src=\"https://cdn.tgdd.vn/Files/2018/07/10/1100559/z1046125326717_a14ac022dde62862acf3b6bef0ef16d6copy-_1494x1334-800-resize.jpg\" title=\"ảnh\" /></p>\r\n\r\n<p>Bạn cần nhập mật m&atilde; iPhone hoặc sử dụng Touch ID để x&aacute;c nhận quyền th&ecirc;m VPN, b&ecirc;n cạnh đ&oacute; để quản l&yacute; cấu h&igrave;nh VPN đ&atilde; th&ecirc;m, h&atilde;y truy cập&nbsp;<strong>C&agrave;i đặt &gt; C&agrave;i đặt chung &gt; VPN</strong>.</p>\r\n\r\n<p><img alt=\"ảnh\" src=\"https://cdn.tgdd.vn/Files/2018/07/10/1100559/z1046125319909_61f286d099c584c6cce67955b00747aa-_2244x1334-800-resize.jpg\" title=\"ảnh\" /></p>\r\n\r\n<p>Khi quay lại giao diện Adblock Mobile, bạn sẽ thấy ứng dụng chuyển sang m&agrave;u xanh. Th&ecirc;m v&agrave;o đ&oacute;, biểu tượng VPN tr&ecirc;n thanh trạng th&aacute;i cũng xuất hiện. Bạn c&oacute; thể mở Adblock h&agrave;ng tuần để kiểm tra c&aacute;c ứng dụng n&agrave;o đ&atilde; thường xuy&ecirc;n xuất hiện quảng c&aacute;o.</p>\r\n\r\n<p>Nếu bạn thấy mẹo n&agrave;y hữu &iacute;ch th&igrave; h&atilde;y chia sẻ cho mọi người c&ugrave;ng biết nh&eacute;.</p>\r\n', 'http://localhost:8080/ProjectWeb_1920/images/img-tintuc/11.jpg', 'Nguyễn Khắc Ngọc', '2019-11-14', 0, 1);
INSERT INTO `news` VALUES (12, 'Galaxy M20 và M30 được Samsung thử nghiệm Android 10, bản chính thức sẽ được cập nhật vào năm sau', 'Tin tức', 'WiFi Alliance (Liên minh WiFi) vừa cho biết bộ đôi Galaxy M20 và M30 thuộc danh mục Android 10, nghĩa là Samsung đang thử nghiệm phiên bản hệ điều...', '<h2>WiFi Alliance (Li&ecirc;n minh WiFi) vừa cho biết bộ đ&ocirc;i&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-m20\" target=\"_blank\" title=\"Galaxy M20\">Galaxy M20</a>&nbsp;v&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-m30\" target=\"_blank\" title=\"Galaxy M30\">M30&nbsp;</a>thuộc danh mục Android 10, nghĩa l&agrave; Samsung đang thử nghiệm phi&ecirc;n bản hệ điều h&agrave;nh mới cho hai smartphone n&agrave;y.</h2>\r\n\r\n<p>Gần đ&acirc;y&nbsp;<a href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\" title=\"Samsung\">Samsung&nbsp;</a>c&oacute; vẻ bắt đầu gi&agrave;nh nhiều sự quan t&acirc;m hơn tới ph&acirc;n kh&uacute;c tầm trung v&agrave; gi&aacute; rẻ bởi v&igrave; sự cạnh tranh khốc liệt từ c&aacute;c h&atilde;ng&nbsp;<a href=\"https://www.thegioididong.com/dtdd/\" target=\"_blank\" title=\"smartphone\">smartphone&nbsp;</a>Trung Quốc.</p>\r\n\r\n<p>H&atilde;ng cũng tập trung cập nhật phần mềm cho c&aacute;c ph&acirc;n kh&uacute;c n&agrave;y nhanh hơn, thế n&ecirc;n theo đồn đo&aacute;n th&igrave; bộ đ&ocirc;i M20 v&agrave; M30 sẽ nhận được bản cập nhật v&agrave;o nửa đầu năm sau, thay cho 10 - 12 th&aacute;ng như truyền thống.</p>\r\n\r\n<p>Hiện tại th&igrave; chưa c&oacute; thiết bị Samsung n&agrave;o được cập nhật ch&iacute;nh thức Android 10 ch&iacute;nh thức, rất nhiều smartphone hiện c&ograve;n đang trong giai đoạn beta.</p>\r\n\r\n<p><img alt=\"Phiên bản giao diện One UI 2.0 sẽ dựa trên nên Android 10\" src=\"https://cdn.tgdd.vn/Files/2019/11/15/1218971/android-10-samsung-one-ui-2_800x500.jpg\" title=\"Phiên bản giao diện One UI 2.0 sẽ dựa trên nên Android 10\" /></p>\r\n\r\n<p>Phi&ecirc;n bản giao diện One UI 2.0 sẽ dựa tr&ecirc;n nền Android 10</p>\r\n\r\n<p>Th&ocirc;ng tin gần đ&acirc;y cho biết 2 d&ograve;ng&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-s10\" target=\"_blank\" title=\"S10\">Galaxy S10</a>&nbsp;v&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-note-10\" target=\"_blank\" title=\"Note 10\">Note 10&nbsp;</a>sẽ sớm nhận được bản cập nhật One UI 2.0 (dựa tr&ecirc;n Android 10).</p>\r\n\r\n<p>C&oacute; thể n&oacute;i &aacute;p lực từ c&aacute;c h&atilde;ng Trung Quốc l&ecirc;n Samsung giai đoạn gần đ&acirc;y đ&atilde; khiến ph&iacute;a người d&ugrave;ng ch&uacute;ng ta &quot;ngư &ocirc;ng đắc lợi&quot;, khi những smartphone cho ra c&oacute; phần cứng tốt hơn, cập nhật nhanh hơn. Bạn c&oacute; nghĩ thế kh&ocirc;ng?</p>\r\n\r\n<p>Nguồn:&nbsp;<a href=\"https://www.gizchina.com/2019/11/13/samsung-already-working-on-android-10-for-the-galaxy-m20-and-m30/?fbclid=IwAR1V37VkUn7OUUQhfY6NEDxyAhS5NaGhl0LLh1U0XxTAUN4q7fcuK-4MLrE\" rel=\"nofollow\" target=\"_blank\" title=\"Nguồn\">Gizchina</a></p>\r\n', 'http://localhost:8080/ProjectWeb_1920/images/img-tintuc/12.jpg', 'Khôi Phạm Như Nguyên', '2019-11-13', 0, 1);
INSERT INTO `news` VALUES (13, 'Apple khoá sign iOS 13.2 vì lỗi đa nhiệm, khuyến nghị người dùng cập nhật lên iOS 13.2.2', 'Tin tức', 'Sau thông cáo báo chí ra mắt AirPods Pro tích hợp tính năng chống ồn chủ động mới, Apple cũng phát hành iOS 13.2 bổ sung thêm biểu tượng cảm xúc...', '<h2>Sau th&ocirc;ng c&aacute;o b&aacute;o ch&iacute; ra mắt&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe/tai-nghe-bluetooth-airpods-pro-apple-mwp22\" target=\"_blank\" title=\"AirPods Pro\" type=\"AirPods Pro\">AirPods Pro</a>&nbsp;t&iacute;ch hợp t&iacute;nh năng chống ồn chủ động mới, Apple cũng ph&aacute;t h&agrave;nh iOS 13.2 bổ sung th&ecirc;m biểu tượng cảm x&uacute;c v&agrave; t&iacute;nh năng chụp ảnh Deep Fusion cho d&ograve;ng&nbsp;<a href=\"https://www.thegioididong.com/dtdd/iphone-11\" target=\"_blank\" title=\"iPhone 11\" type=\"iPhone 11\">iPhone 11</a>. V&agrave; mới đ&acirc;y, Apple đ&atilde; ch&iacute;nh thức kho&aacute; sign phi&ecirc;n bản iOS 13.2.</h2>\r\n\r\n<p>V&agrave;o ng&agrave;y 07/11, Apple đ&atilde; phải tung bản v&aacute; lỗi iOS 13.2.2 v&igrave; nhiều người d&ugrave;ng&nbsp;<a href=\"https://www.thegioididong.com/thu-cu-doi-moi\" target=\"_blank\" title=\"iPhone\" type=\"iPhone\">iPhone</a>&nbsp;v&agrave;&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad\" target=\"_blank\" title=\"iPad\" type=\"iPad\">iPad</a>&nbsp;đ&atilde; b&aacute;o c&aacute;o sự cố li&ecirc;n quan đến đa nhiệm khiến c&aacute;c ứng dụng chạy trong nền bị dừng đột ngột. Việc kho&aacute; sign iOS 13.2 đồng nghĩa với việc người d&ugrave;ng sẽ kh&ocirc;ng thể hạ cấp iPhone xuống iOS 13.2 th&ocirc;ng qua iTunes.</p>\r\n\r\n<p>Được biết, Apple thường xuy&ecirc;n kho&aacute; sign c&aacute;c bản iOS cũ sau khi ph&aacute;t h&agrave;nh bản cập nhật mới, nhằm khuyến kh&iacute;ch người d&ugrave;ng cập nhật để trải nghiệm những t&iacute;nh năng mới v&agrave; tr&aacute;nh gặp lỗi tồn đọng trong c&aacute;c phi&ecirc;n bản cũ. Ngo&agrave;i ra, Apple cũng tung th&ecirc;m bản thử nghiệm iOS 13.3 beta, bao gồm t&iacute;nh năng &quot;Giới hạn thời gian sử dụng&quot; c&ugrave;ng một số thay đổi kh&aacute;c.</p>\r\n\r\n<p>Bạn thấy iOS 13 c&oacute; t&iacute;nh năng n&agrave;o hay?</p>\r\n\r\n<p>Nguồn:&nbsp;<a href=\"https://www.macrumors.com/2019/11/14/apple-stops-signing-ios-13-2/\" rel=\"nofollow\" target=\"_blank\" title=\"Macrumors\" type=\"Macrumors\">Macrumors</a></p>\r\n', 'http://localhost:8080/ProjectWeb_1920/images/img-tintuc/13.jpg', 'Vương Gia Bảo', '2019-11-13', 1, 1);
INSERT INTO `news` VALUES (14, 'Redmi Note 8 Pro sắp có phiên bản dùng chip Snapdragon 730G?', 'Tin tức', 'Kể từ thời Redmi Note 4 đến nay, Xiaomi chưa ra mắt điện thoại Redmi Note nào có hai tùy chọn chip xử lý. Ban đầu Note 4 ra mắt với chip Helio X20...', '<h2>Kể từ thời&nbsp;<a href=\"https://www.thegioididong.com/dtdd/xiaomi-redmi-note-4\" target=\"_blank\" title=\"Chi tiết Xiaomi Redmi Note 4\" type=\"Chi tiết Xiaomi Redmi Note 4\">Redmi Note 4&nbsp;</a>đến nay,&nbsp;<a href=\"https://www.thegioididong.com/dtdd-xiaomi\" target=\"_blank\" title=\"Đặt mua smartphone Xiaomi tại Thegioididong.com\" type=\"Đặt mua smartphone Xiaomi tại Thegioididong.com\">Xiaomi</a>&nbsp;chưa ra mắt điện thoại Redmi Note n&agrave;o c&oacute; hai t&ugrave;y chọn chip xử l&yacute;. Ban đầu Note 4 ra mắt với chip Helio X20 v&agrave; v&agrave;i th&aacute;ng sau, một phi&ecirc;n bản chạy chip Snapdragon 650 đ&atilde; được c&ocirc;ng bố. Liệu điều tương tự c&oacute; thể xảy ra với&nbsp;<a href=\"https://www.thegioididong.com/dtdd/xiaomi-redmi-note-8-pro\" target=\"_blank\" title=\"Đặt mua Xiaomi Redmi Note 8 Pro tại Thegioididong.com\" type=\"Đặt mua Xiaomi Redmi Note 8 Pro tại Thegioididong.com\">Redmi Note 8 Pro</a>?</h2>\r\n\r\n<p>Hiện&nbsp;Redmi Note 8 Pro được trang bị chip&nbsp;Mediatek Helio G90T. Tuy nhi&ecirc;n, t&agrave;i khoản&nbsp;Twitter Xiaomishka (@xiaomishka) cho biết rằng hai chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Đặt mua smartphone tại Thegioididong.com\" type=\"Đặt mua smartphone tại Thegioididong.com\">smartphone</a>&nbsp;mới của Xiaomi với số model&nbsp;M1912G7BE v&agrave; M1912G7BC đ&atilde; được cơ quan 3C chứng nhận l&agrave; phi&ecirc;n bản Redmi Note 8 Pro d&ugrave;ng chip Snapdragon 730G của Qualcomm.</p>\r\n\r\n<p><img alt=\"Redmi Note 8 Pro sắp có phiên bản dùng chip Snapdragon 730G?\" src=\"https://cdn.tgdd.vn/Files/2019/11/14/1218886/redmi-note-8-pro-snapdragon730g_860x564-800-resize.jpg\" title=\"Redmi Note 8 Pro sắp có phiên bản dùng chip Snapdragon 730G?\" /></p>\r\n\r\n<p>Xiaomishka c&ograve;n cho biết th&ecirc;m trong tweet của m&igrave;nh rằng&nbsp;<a href=\"https://www.thegioididong.com/dtdd/xiaomi-redmi-k30\" target=\"_blank\" title=\"Chi tiết Xiaomi Redmi K30\" type=\"Chi tiết Xiaomi Redmi K30\">Redmi K30</a>&nbsp;với số model M1911U2E, chip 5G v&agrave; MediaTek sẽ ra mắt v&agrave;o th&aacute;ng 12 tới. Điện thoại sẽ hỗ trợ cả mạng 5G độc lập v&agrave; kh&ocirc;ng độc lập.</p>\r\n\r\n<p>Về thiết kế, Redmi K30 sẽ c&oacute; m&agrave;n h&igrave;nh đục lỗ chứa camera k&eacute;p tương tự như tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-s10-plus\" target=\"_blank\" title=\"Đặt mua Samsung Galaxy S10+ tại Thegioididong.com\" type=\"Đặt mua Samsung Galaxy S10+ tại Thegioididong.com\">Galaxy S10+</a>.</p>\r\n\r\n<p>Ri&ecirc;ng bạn cảm thấy th&iacute;ch Redmi Note 8 Pro d&ugrave;ng chip Mediatek Helio G90T hay Qualcomm Snapdragon 730G?</p>\r\n\r\n<p>Nguồn:&nbsp;<a href=\"https://www.gizmochina.com/2019/11/14/rumor-redmi-note-8-pro-with-snapdragon-730g-coming-soon/\" rel=\"nofollow\" target=\"_blank\" title=\"Rumor: Redmi Note 8 Pro with Snapdragon 730G coming soon\" type=\"Rumor: Redmi Note 8 Pro with Snapdragon 730G coming soon\">Gizmochina</a></p>\r\n', 'http://localhost:8080/ProjectWeb_1920/images/img-tintuc/14.jpg', 'Dương Lê', '2019-11-13', 1, 1);
INSERT INTO `news` VALUES (15, 'Vivo S5 ra mắt: Chip Snapdragon 712, 8GB RAM, 4 camera, giá từ 8.9 triệu đồng', 'Tin tức', 'Sau một thời gian khá lâu chờ đợi thì mới đây, Vivo đã chính thức trình làng mẫu di động tầm trung mới của hãng, có tên gọi là Vivo S5. Đúng như...', '<h2>Sau một thời gian kh&aacute; l&acirc;u chờ đợi th&igrave; mới đ&acirc;y,&nbsp;<a href=\"https://www.thegioididong.com/dtdd-vivo\" target=\"_blank\" title=\"Vivo\" type=\"Vivo\">Vivo</a>&nbsp;đ&atilde; ch&iacute;nh thức tr&igrave;nh l&agrave;ng mẫu di động tầm trung mới của h&atilde;ng, c&oacute; t&ecirc;n gọi l&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd/vivo-s5\" target=\"_blank\" title=\"Điện thoại Vivo S5\" type=\"Điện thoại Vivo S5\">Vivo S5</a>. Đ&uacute;ng như c&aacute;c tin đồn trước đ&oacute;, chiếc điện thoại n&agrave;y c&oacute; m&agrave;n h&igrave;nh tr&agrave;n viền ấn tượng, c&ugrave;ng với cấu h&igrave;nh phần cứng mạnh mẽ cũng như hệ thống 4 camera sau.</h2>\r\n\r\n<p>Cụ thể, Vivo S5 sở hữu m&agrave;n h&igrave;nh &#39;đục lỗ&#39; Super AMOLED k&iacute;ch thước 6.44 inch, độ ph&acirc;n giải Full HD+, với tỷ lệ khung h&igrave;nh 20:9 v&agrave; chiếm tới 91.38% diện t&iacute;ch mặt trước.</p>\r\n\r\n<p><img alt=\"Vivo S5 ra mắt: Chip Snapdragon 712, 8GB RAM, 4 camera, giá từ 8.9 triệu đồng\" src=\"https://cdn.tgdd.vn/Files/2019/11/14/1218877/s54_600x565.jpg\" title=\"Vivo S5 ra mắt: Chip Snapdragon 712, 8GB RAM, 4 camera, giá từ 8.9 triệu đồng\" /></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, m&agrave;n h&igrave;nh n&agrave;y c&ograve;n hỗ trợ c&ocirc;ng nghệ HDR 10 v&agrave; c&oacute; độ s&aacute;ng tối đa l&ecirc;n tới 1.000 nit, hứa hẹn sẽ mang lại trải nghiệm tốt hơn cho người d&ugrave;ng. Đồng thời, cảm biến v&acirc;n tay của m&aacute;y cũng được t&iacute;ch hợp ngay b&ecirc;n trong m&agrave;n h&igrave;nh.</p>\r\n\r\n<p>Về hiệu năng, Vivo S5 sử dụng bộ vi xử l&yacute; Snapdragon 712, với 8 nh&acirc;n tốc độ 2.3 GHz, đi k&egrave;m GPU Adreno 616 v&agrave; c&oacute; hai tuỳ chọn cấu h&igrave;nh bao gồm 8 GB RAM + 128 GB ROM hoặc 8 GB RAM + 256 GB ROM, cũng như bộ nhớ trong c&oacute; thể mở rộng bằng khe cắm thẻ nhớ.</p>\r\n\r\n<p><img alt=\"Vivo S5 ra mắt: Chip Snapdragon 712, 8GB RAM, 4 camera, giá từ 8.9 triệu đồng\" src=\"https://cdn.tgdd.vn/Files/2019/11/14/1218877/s52_800x449.jpg\" title=\"Vivo S5 ra mắt: Chip Snapdragon 712, 8GB RAM, 4 camera, giá từ 8.9 triệu đồng\" /></p>\r\n\r\n<p>Vivo cũng trang bị cho Vivo S5 hệ thống 4 camera sau, bao gồm cảm biến ch&iacute;nh c&oacute; độ ph&acirc;n giải 48 MP, cảm biến g&oacute;c rộng 8 MP, cảm biến tele 2 MP v&agrave; cảm biến đo chiều s&acirc;u 5 MP, c&ugrave;ng với camera selfie 32 MP ở ph&iacute;a trước.</p>\r\n\r\n<p>Ngo&agrave;i ra, c&aacute;c th&ocirc;ng số c&ograve;n lại của chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"điện thoại\" type=\"điện thoại\">điện thoại</a>&nbsp;n&agrave;y bao gồm m&aacute;y được t&iacute;ch hợp vi&ecirc;n pin 4.100 mAh c&oacute; sạc nhanh 22.5W, chạy tr&ecirc;n hệ điều h&agrave;nh Android 9.0 Pie với giao diện FunTouch OS v&agrave; hỗ trợ c&aacute;c kết nối như hai SIM, 4G VoLTE, Wi-Fi 802.11ac, Bluetooth 5.0, GPS, USB Type-C, NFC, cổng &acirc;m thanh 3.5 mm.</p>\r\n\r\n<p><img alt=\"Vivo S5 ra mắt: Chip Snapdragon 712, 8GB RAM, 4 camera, giá từ 8.9 triệu đồng\" src=\"https://cdn.tgdd.vn/Files/2019/11/14/1218877/s51_800x450.jpg\" title=\"Vivo S5 ra mắt: Chip Snapdragon 712, 8GB RAM, 4 camera, giá từ 8.9 triệu đồng\" /></p>\r\n\r\n<p>Được biết, Vivo S5 sẽ c&oacute; ba m&agrave;u l&agrave; trắng, xanh, đen v&agrave; được mở b&aacute;n từ ng&agrave;y 22/11 tới đ&acirc;y với mức gi&aacute; khởi điểm chỉ từ 2.698 NDT (khoảng 8.9 triệu đồng) cho phi&ecirc;n bản RAM 8 GB + ROM 128 GB, trong khi phi&ecirc;n bản ROM 256 GB sẽ c&oacute; gi&aacute; 2.998 NDT (khoảng 9.9 triệu đồng).</p>\r\n\r\n<p>C&aacute;c bạn thấy chiếc điện thoại n&agrave;y như thế n&agrave;o? H&atilde;y để lại b&igrave;nh luận nh&eacute;.</p>\r\n\r\n<p>Nguồn:&nbsp;<a href=\"https://www.91mobiles.com/hub/vivo-s5-specifications-price-rmb-2698-launch/\" rel=\"nofollow\" target=\"_blank\" title=\"91mobiles\" type=\"91mobiles\">91mobiles</a></p>\r\n', 'http://localhost:8080/ProjectWeb_1920/images/img-tintuc/15.jpg', 'Hồ Nguyễn Anh Phong', '2019-11-12', 1, 1);
INSERT INTO `news` VALUES (16, ' Chào tháng 12, iPhone iPad rất nhiều ưu đãi, iFan sao có thể làm ngơ', 'Khuyến mãi', 'iFan sắm iPhone và iPad trong tháng 12 này sẽ được hưởng nhiều ưu đãi như tặng phiều mua hàng, giảm giá lên đến 1 triệu.', '<p><img alt=\"iPhone iPad ưu đãi tháng mới\" src=\"https://cdn.tgdd.vn/Files/2019/12/03/1224622/maxresdefault_800x450.jpg\" title=\"iPhone iPad ưu đãi tháng mới\" /></p>\r\n\r\n<h2>iFan sắm<a href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" title=\" iPhone \" type=\" iPhone \">&nbsp;iPhone&nbsp;</a>v&agrave;<a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad\" target=\"_blank\" title=\" iPad \" type=\" iPad \">&nbsp;iPad&nbsp;</a>trong th&aacute;ng 12 n&agrave;y sẽ được hưởng nhiều ưu đ&atilde;i như tặng phiều mua h&agrave;ng, giảm gi&aacute; l&ecirc;n đến 1 triệu. Dịp cuối năm tưng bừng mua sắm, đừng qu&ecirc;n tham khảo ngay loạt sản phẩm si&ecirc;u HOT đến từ &quot;nh&agrave; T&aacute;o&quot; nh&eacute;.</h2>\r\n\r\n<p>Ưu đ&atilde;i &aacute;p dụng từ 01/12/2019 - 31/12/2019. Khuyến m&atilde;i kh&ocirc;ng &aacute;p dụng k&egrave;m trả g&oacute;p 0%.</p>\r\n\r\n<p>Lưu &yacute;:</p>\r\n\r\n<ul>\r\n	<li>Khuyến m&atilde;i c&oacute; thể thay đổi trong c&aacute;c dịp c&oacute; chương tr&igrave;nh hot sale.</li>\r\n	<li>Khuyến m&atilde;i c&oacute; thể kết th&uacute;c sớm trước thời hạn nếu hết số lượng.</li>\r\n</ul>\r\n\r\n<p>C&aacute;c iFan th&aacute;ng 12 n&agrave;y chọn mua g&igrave;?</p>\r\n', 'http://localhost:8080/ProjectWeb_1920/images/img-tintuc/16.jpg', 'Kim Hằng', '2019-11-12', 1, 1);
INSERT INTO `news` VALUES (17, 'Chốt năm 2019, smartphone Realme giảm giá đến 1.7 triệu, máy đẹp hiệu năng tốt rất đáng sở hữu', 'Khuyến mãi', 'Những ngày cuối tháng 12, săn sale mua sắm đừng quên loạt smartphone Realme được giảm giá lên đến 1.7 triệu đồng kèm theo nhiều khuyến mãi hấp dẫn khác như trả góp 0% cho những ai chưa đủ kinh phí. Vừa rinh điện thoại mới, vừa tiết kiệm được tiền, thật hấp dẫn đúng không nào!', '<h2>Những ng&agrave;y cuối th&aacute;ng 12, săn sale mua sắm đừng qu&ecirc;n loạt&nbsp;<a href=\"https://www.thegioididong.com/dtdd-realme\" target=\"_blank\" title=\"smartphone Realme\" type=\"smartphone Realme\">smartphone Realme</a>được giảm gi&aacute; l&ecirc;n đến 1.7 triệu đồng k&egrave;m theo nhiều khuyến m&atilde;i hấp dẫn kh&aacute;c như trả g&oacute;p 0% cho những ai chưa đủ kinh ph&iacute;. Vừa rinh&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"điện thoại\" type=\"điện thoại\">điện thoại</a>&nbsp;mới, vừa tiết kiệm được tiền, thật hấp dẫn đ&uacute;ng kh&ocirc;ng n&agrave;o!</h2>\r\n\r\n<p>Khuyến m&atilde;i diễn ra từ ng&agrave;y 27/12/2019 - 31/12/2019, &aacute;p dụng t&ugrave;y sản phẩm.</p>\r\n\r\n<p>Lưu &yacute;: Khuyến m&atilde;i c&oacute; thể kết th&uacute;c sớm nếu hết số lượng.&nbsp;</p>\r\n\r\n<p><strong>Realme 3 32GB</strong></p>\r\n\r\n<p>Giảm 1 triệu khi mua Online kh&ocirc;ng k&egrave;m trả g&oacute;p 0%.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dtdd/realme-3\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/194952/realme-3-green-isaac-400x400.jpg\" /></a></p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/dtdd/realme-3\">Realme 3 32GB</a></h3>\r\n\r\n<p><strong>2.990.000₫3.990.000₫</strong></p>\r\n\r\n<p>Khuyến m&atilde;iGiảm ngay 700.000đ (đ&atilde; trừ v&agrave;o gi&aacute;)Giảm ngay 300.000đ (&aacute;p dụng cho đơn h&agrave;ng đặt v&agrave; nhận h&agrave;ng trong ng&agrave;y 27/12 - 1/1) (đ&atilde; trừ v&agrave;o gi&aacute;)</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/them-vao-gio-hang?ProductId=194952\" target=\"blank\">ĐẶT MUA</a><a href=\"https://www.thegioididong.com/tra-gop/dtdd/realme-3\" target=\"blank\">TRẢ G&Oacute;P</a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Realme 3 Pro (4GB/64GB)</strong></p>\r\n\r\n<p>Giảm 1.7 triệu khi mua Online kh&ocirc;ng k&egrave;m trả g&oacute;p 0%.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dtdd/realme-3-pro\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/194955/realme-3-pro-blue-400x400.jpg\" /></a></p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/dtdd/realme-3-pro\">Realme 3 Pro (4GB/64GB)</a></h3>\r\n\r\n<p><strong>4.290.000₫5.990.000₫</strong></p>\r\n\r\n<p>Khuyến m&atilde;iGiảm ngay 1,4 triệu (đ&atilde; trừ v&agrave;o gi&aacute;)Giảm ngay 300.000đ (&aacute;p dụng cho đơn h&agrave;ng đặt v&agrave; nhận h&agrave;ng trong ng&agrave;y 27/12 - 1/1) (đ&atilde; trừ v&agrave;o gi&aacute;)</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/them-vao-gio-hang?ProductId=194955\" target=\"blank\">ĐẶT MUA</a><a href=\"https://www.thegioididong.com/tra-gop/dtdd/realme-3-pro\" target=\"blank\">TRẢ G&Oacute;P</a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Realme 5 (3GB/64GB)</strong></p>\r\n\r\n<p>Giảm 300.000 đồng k&egrave;m trả g&oacute;p 0%.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dtdd/realme-5\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/207649/realme-5-blue-400x400.jpg\" /></a></p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/dtdd/realme-5\">Realme 5 (3GB/64GB)</a></h3>\r\n\r\n<p><strong>3.690.000₫3.990.000₫</strong></p>\r\n\r\n<p>Khuyến m&atilde;iGiảm ngay 300.000đ (đ&atilde; trừ v&agrave;o gi&aacute;)</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/them-vao-gio-hang?ProductId=207649\" target=\"blank\">ĐẶT MUA</a><a href=\"https://www.thegioididong.com/tra-gop/dtdd/realme-5\" target=\"blank\">TRẢ G&Oacute;P</a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Realme 5 Pro (4GB/128GB)</strong></p>\r\n\r\n<p>Giảm 300.000 đồng k&egrave;m trả g&oacute;p 0%.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dtdd/realme-5-pro\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/209564/realme-5-pro-purple-400x400.jpg\" /></a></p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/dtdd/realme-5-pro\">Realme 5 Pro (4GB/128GB)</a></h3>\r\n\r\n<p><strong>5.690.000₫5.990.000₫</strong></p>\r\n\r\n<p>Khuyến m&atilde;iGiảm ngay 300.000đ (đ&atilde; trừ v&agrave;o gi&aacute;)</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/them-vao-gio-hang?ProductId=209564\" target=\"blank\">ĐẶT MUA</a><a href=\"https://www.thegioididong.com/tra-gop/dtdd/realme-5-pro\" target=\"blank\">TRẢ G&Oacute;P</a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Realme 5 Pro (8GB/128GB)</strong></p>\r\n\r\n<p>Giảm 500.000 đồng k&egrave;m trả g&oacute;p 0%.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dtdd/realme-5-pro-8gb\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/211163/realme-5-pro-8gb-green-400x400.jpg\" /></a></p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/dtdd/realme-5-pro-8gb\">Realme 5 Pro (8GB/128GB)</a></h3>\r\n\r\n<p><strong>6.490.000₫6.990.000₫</strong></p>\r\n\r\n<p>Khuyến m&atilde;iGiảm ngay 500.000đ (đ&atilde; trừ v&agrave;o gi&aacute;)</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/them-vao-gio-hang?ProductId=211163\" target=\"blank\">ĐẶT MUA</a><a href=\"https://www.thegioididong.com/tra-gop/dtdd/realme-5-pro-8gb\" target=\"blank\">TRẢ G&Oacute;P</a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Realme 5s</strong></p>\r\n\r\n<p>Giảm 300.000 đồng k&egrave;m trả g&oacute;p 0%. &Aacute;p dụng từ 28/12 - 31/12.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dtdd/realme-5s\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/213588/realme-5s-red-400x400.jpg\" /></a></p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/dtdd/realme-5s\">Realme 5s</a></h3>\r\n\r\n<p><strong>4.690.000₫4.990.000₫</strong></p>\r\n\r\n<p>Khuyến m&atilde;iGiảm ngay 300.000đ (&aacute;p dụng cho đơn h&agrave;ng đặt v&agrave; nhận h&agrave;ng trong ng&agrave;y 28 - 31/12) (đ&atilde; trừ v&agrave;o gi&aacute;)</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/them-vao-gio-hang?ProductId=213588\" target=\"blank\">ĐẶT MUA</a><a href=\"https://www.thegioididong.com/tra-gop/dtdd/realme-5s\" target=\"blank\">TRẢ G&Oacute;P</a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ưng &yacute; với mẫu Realme n&agrave;o b&ecirc;n tr&ecirc;n, h&atilde;y để lại b&igrave;nh luận b&ecirc;n dưới!</p>\r\n', 'http://localhost:8080/ProjectWeb_1920/data/images/img-tintuc/17.jpg', 'Chuong Ang', '2019-12-27', 1, 1);
INSERT INTO `news` VALUES (18, 'Nhìn lại một năm 2019: Đâu là những chiếc smartphone \'bá đạo nhất\' tính đến thời điểm hiện tại?', 'Tin tức', 'Bạn đang cần một chiếc điện thoại mới nhưng lại không biết rằng đâu sẽ là smartphone tốt nhất và \'bá đạo\' nhất? Bạn đang quan tâm đâu là chiếc máy có camera mạnh nhất, có pin \"trâu\" nhất hoặc sở hữu tổng thể toàn diện nhất? Ngay trong bài viết này, hãy cùng The Guardian nhìn lại một năm 2019 và đưa ra danh sách những chiếc smartphone tốt nhất theo từng nhu cầu người dùng nhé.', '<h2>ạn đang cần một chiếc điện thoại mới nhưng lại kh&ocirc;ng biết rằng đ&acirc;u sẽ l&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"smartphone\" type=\"smartphone\">smartphone</a>&nbsp;tốt nhất v&agrave; &#39;b&aacute; đạo&#39; nhất? Bạn đang quan t&acirc;m đ&acirc;u l&agrave; chiếc m&aacute;y c&oacute; camera mạnh nhất, c&oacute;&nbsp;<a href=\"https://www.thegioididong.com/dtdd?f=pin-khung-3000-mah\" target=\"_blank\" title=\"pin trâu\" type=\"pin trâu\">pin &quot;tr&acirc;u&quot;</a>&nbsp;nhất hoặc sở hữu tổng thể to&agrave;n diện nhất? Ngay trong b&agrave;i viết n&agrave;y, h&atilde;y c&ugrave;ng&nbsp;<a href=\"https://www.theguardian.com/technology/2019/dec/17/best-smartphone-2019-iphone-oneplus-samsung-and-huawei-compared-and-ranked\" rel=\"nofollow\" target=\"_blank\" title=\"The Guardian\" type=\"The Guardian\">The Guardian</a>&nbsp;nh&igrave;n lại một năm 2019 v&agrave; đưa ra danh s&aacute;ch những chiếc smartphone tốt nhất theo từng nhu cầu người d&ugrave;ng nh&eacute;.</h2>\r\n\r\n<p><img alt=\"z6\" src=\"https://cdn.tgdd.vn/Files/2019/12/23/1228025/z6_800x450.jpg\" title=\"z6\" /></p>\r\n\r\n<p>Liệu c&aacute;c thiết bị ở bức ảnh c&oacute; nằm trong bảng xếp hạng b&ecirc;n dưới hay kh&ocirc;ng?.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/top-10-laptop-tot-nhat-nam-2019-ai-xung-dang-la-vua-cua-the-gioi-laptop-1228079\" target=\"_blank\" title=\"Top 10 Laptop tốt nhất năm 2019: Ai xứng đáng là ‘vua’ thế giới laptop?\" type=\"Top 10 Laptop tốt nhất năm 2019: Ai xứng đáng là ‘vua’ thế giới laptop?\">Top 10 Laptop tốt nhất năm 2019: Ai xứng đ&aacute;ng l&agrave; &lsquo;vua&rsquo; thế giới laptop?</a></p>\r\n\r\n<p>Bật m&iacute; với bạn rằng trong bảng xếp hạng của tờ b&aacute;o The Guardian, bạn sẽ thấy sự g&oacute;p mặt của c&aacute;c &quot;&ocirc;ng lớn&quot; như: Apple,&nbsp;<a href=\"https://www.thegioididong.com/dtdd-huawei\" target=\"_blank\" title=\"Huawei\" type=\"Huawei\">Huawei</a>,&nbsp;<a href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\" title=\"Samsung\" type=\"Samsung\">Samsung</a>&nbsp;v&agrave; cả&nbsp;<a href=\"https://www.thegioididong.com/dtdd-oneplus\" target=\"_blank\" title=\"OnePlus\" type=\"OnePlus\">OnePlus</a>. Vậy những chiếc smartphone nằm trong danh s&aacute;ch n&agrave;y l&agrave; g&igrave; v&agrave; thiết bị n&agrave;o đang nắm giữ &quot;ng&ocirc;i vương&quot; ở từng ph&acirc;n kh&uacute;c kh&aacute;c nhau?</p>\r\n\r\n<h3><strong>Người d&ugrave;ng cần g&igrave;?</strong></h3>\r\n\r\n<p>Theo&nbsp;The Guardian cho biết, chưa từng c&oacute; một thời điểm th&iacute;ch hợp n&agrave;o để mua một chiếc smartphone với chất lượng tốt c&ugrave;ng gi&aacute; cả phải chăng. Như c&oacute; thể thấy, thị trường di động li&ecirc;n tục sản sinh ra nhiều thiết bị với camera chất lượng, c&oacute; m&aacute;y lại mang đến hiệu năng &quot;chất kh&oacute; tả&quot; v&agrave; cũng c&oacute; smartphone với pin x&agrave;i tới hai ng&agrave;y. Do đ&oacute; người d&ugrave;ng lu&ocirc;n đau đầu v&agrave; đắn đo trong việc lựa chọn một thiết bị th&iacute;ch hợp với nhu cầu.</p>\r\n\r\n<p><img alt=\"abc\" src=\"https://cdn.tgdd.vn/Files/2019/12/23/1228025/abc_800x513.jpg\" title=\"abc\" /></p>\r\n\r\n<p>Theo bạn, đ&acirc;u l&agrave; những chiếc smartphone đ&aacute;ng mua nhất năm 2019?</p>\r\n\r\n<p>Hiểu được t&acirc;m l&yacute; của người d&ugrave;ng, tờ b&aacute;o c&ocirc;ng nghệ n&agrave;y đ&atilde; tổng hợp v&agrave; đưa ra danh s&aacute;ch top những chiếc smartphone tốt nhất (được cập nhật lần cuối c&ugrave;ng v&agrave;o ng&agrave;y 17/12/2019) theo từng ph&acirc;n kh&uacute;c kh&aacute;c nhau như: Chiếc smartphone c&oacute; camera &#39;&#39;đỉnh&#39;&#39; nhất, thiết bị c&oacute; hiệu năng ấn tượng nhất hoặc chiếc m&aacute;y c&oacute; tổng thể to&agrave;n diện nhất,...</p>\r\n\r\n<h3><strong>1. Tổng thể to&agrave;n diện nhất: OnePlus 7T Pro</strong></h3>\r\n\r\n<p>Trong qu&aacute; tr&igrave;nh theo d&otilde;i suốt cả một năm để t&igrave;m ra đ&acirc;u l&agrave; chiếc m&aacute;y tốt nhất (năm 2019) th&igrave; tớ b&aacute;o đ&aacute;nh gi&aacute; gi&aacute; rằng,&nbsp;<a href=\"https://www.thegioididong.com/dtdd/oneplus-7t-pro\" target=\"_blank\" title=\"OnePlus 7T Pro\" type=\"OnePlus 7T Pro\">OnePlus 7T Pro</a>&nbsp;xứng đ&aacute;ng nhận được &quot;ng&ocirc;i vương&quot; ở mặt to&agrave;n diện tổng thể. Thiết bị l&agrave; một phi&ecirc;n bản cập nhật của&nbsp;<a href=\"https://www.thegioididong.com/dtdd/oneplus-7-pro\" target=\"_blank\" title=\"OnePlus 7 Pro\" type=\"OnePlus 7 Pro\">OnePlus 7 Pro</a>, mang đến thời lượng pin tốt hơn một ch&uacute;t, bộ xử l&yacute; mạnh mẽ hơn v&agrave; tăng tốc khả năng mở kh&oacute;a bằng v&acirc;n tay.</p>\r\n\r\n<p><img alt=\"z1\" src=\"https://cdn.tgdd.vn/Files/2019/12/23/1228025/z1_800x480.jpg\" title=\"z1\" /></p>\r\n\r\n<p>Thiết kế tổng thể của OnePlus 7T Pro.</p>\r\n\r\n<p>Cụ thể hơn, OnePlus 7T Pro sở hữu m&agrave;n h&igrave;nh FullView k&iacute;ch thước l&ecirc;n đến 6.67 inch, với độ ph&acirc;n giải QHD+ (2K) c&ugrave;ng tấm nền AMOLED cho m&agrave;u sắc hiển thị rực rỡ hơn v&agrave; m&agrave;u đen c&oacute; độ s&acirc;u. Kh&ocirc;ng chỉ vậy, m&agrave;n h&igrave;nh m&aacute;y c&ograve;n c&oacute; tần số qu&eacute;t l&ecirc;n đến 90 Hz mang đến độ mượt m&agrave; trong thao t&aacute;c sử dụng hằng ng&agrave;y.</p>\r\n\r\n<p>OnePlus 7T Pro được t&iacute;ch hợp chip mạnh mẽ Qualcomm Snapdragon 855+, với RAM l&ecirc;n đến 8 GB v&agrave; bộ nhớ trong l&agrave; 256 GB. M&aacute;y c&ograve;n sở hữu chuẩn chip UFS 3.0, được chạy OxygenOS 10 mới nhất của Android 10, t&iacute;nh năng mở kh&oacute;a v&acirc;n tay được cải tiện đ&aacute;ng kể v&agrave; c&oacute; khả năng chống nước (mặc d&ugrave; kh&ocirc;ng được xếp hạng IP).</p>\r\n\r\n<p><img alt=\"OnePlus 7T Pro\" src=\"https://cdn.tgdd.vn/Files/2019/10/11/1207191/oneplus_7t_proimg-2400_800x600.jpg\" title=\"hiệu năng\" /></p>\r\n\r\n<p>OnePlus 7T Pro cho hiệu năng mạnh mẽ trong giới Android.</p>\r\n\r\n<p>Với hệ thống camera ba ở mặt sau gồm: Camera ch&iacute;nh 48 MP, g&oacute;c si&ecirc;u rộng 16 MP v&agrave; camera tele 8 MP, chiếc m&aacute;y sẽ mang đến chất ảnh đẹp mắt v&agrave; sắc n&eacute;t. OnePlus 7T Pro c&oacute; thể đ&aacute;nh bại nhiều đối thủ như:&nbsp;<a href=\"https://www.thegioididong.com/dtdd/iphone-11-pro\" target=\"_blank\" title=\"iPhone 11 Pro\" type=\"iPhone 11 Pro\">iPhone 11 Pro</a>,&nbsp;<a href=\"https://www.thegioididong.com/dtdd/google-pixel-4-xl\" target=\"_blank\" title=\"Google Pixel 4 XL\" type=\"Google Pixel 4 XL\">Google Pixel 4 XL</a>,...(nhưng ở một v&agrave;i điểm th&ocirc;i nh&eacute;). Th&ecirc;m một điểm cộng nữa đ&oacute; l&agrave; thiết bị c&oacute; camera slefie pop-up với chất lượng cũng kh&ocirc;ng tồi đ&acirc;u.</p>\r\n\r\n<p>Cuối c&ugrave;ng, 7T Pro sở hữu pin 4.085 mAh c&oacute; thể k&eacute;o d&agrave;i khoảng 32 giờ giữa c&aacute;c lần sạc, khiến chiếc m&aacute;y trở th&agrave;nh một trong những thiết bị c&oacute; thời gian hoạt động ấn tượng. Qu&aacute; tr&igrave;nh sạc cũng nhanh th&ocirc;ng nhờ c&ocirc;ng nghệ sạc nhanh WarpCharge khi nạp 70% pin chỉ sau 34 ph&uacute;t.</p>\r\n\r\n<ul>\r\n	<li><strong>Vậy n&ecirc;n mua OnePlus 7T Pro hay kh&ocirc;ng?</strong></li>\r\n</ul>\r\n\r\n<p><strong>N&ecirc;n mua nếu:</strong>&nbsp;Bạn muốn trải nghiệm một thiết bị với hiệu năng ấn tượng c&ugrave;ng m&agrave;n h&igrave;nh trơn tru.</p>\r\n\r\n<p><strong>Kh&ocirc;ng n&ecirc;n mua nếu:</strong>&nbsp;Bạn chỉ th&iacute;ch một chiếc m&aacute;y gọn tay v&agrave; dễ cầm nắm.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/danh-gia-chi-tiet-oneplus-7t-pro-1207191\" target=\"_blank\" title=\"đánh giá chi tiết OnePlus 7T Pro\" type=\"đánh giá chi tiết OnePlus 7T Pro\">Đ&aacute;nh gi&aacute; chi tiết OnePlus 7T Pro: Tốt nhưng kh&ocirc;ng qu&aacute; kh&aacute;c biệt!</a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dtdd/oneplus-7t-pro\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/209744/oneplus-7t-pro-1-400x400.jpg\" /></a></p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/dtdd/oneplus-7t-pro\">OnePlus 7T Pro</a></h3>\r\n\r\n<p>M&agrave;n h&igrave;nh: 6.67&quot;, Quad HD+ (2K+)CPU: Qualcomm Snapdragon 855 Plus 8 nh&acirc;nRAM: 8 GB, ROM: 256 GBCamera: Ch&iacute;nh 48 MP &amp; Phụ 16 MP, 8 MPSelfie: 16 MPPIN: 4085 mAh, c&oacute; sạc nhanh</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><strong>2. Chiếc iPhone (iOS) tốt nhất: Apple iPhone 11 Pro</strong></h3>\r\n\r\n<p>Hiển nhi&ecirc;n rằng, iPhone 11 Pro kh&ocirc;ng phải l&agrave; chiếc iPhone lớn nhất hoặc đắt nhất trong năm nay nhưng chiếc m&aacute;y lại l&agrave; thiết bị gần như tốt nhất trong năm.</p>\r\n\r\n<p>Tờ b&aacute;o c&ocirc;ng nghệ n&agrave;y l&yacute; giải rằng, iPhone 11 Pro sở hữu m&agrave;n h&igrave;nh 5.8 inch tuyệt đẹp v&agrave; đủ lớn để trải nghiệm rồi. C&ugrave;ng th&acirc;n m&aacute;y đầy m&ecirc; hoặc, cảm gi&aacute;c sang trọng, hiệu năng vượt trội v&agrave; thời lượng pin để theo kịp hầu hết c&aacute;c đối thủ ch&iacute;nh l&agrave; những yếu tố gi&uacute;p thiết bị d&agrave;nh lấy ng&ocirc;i vị qu&aacute;n qu&acirc;n.</p>\r\n\r\n<p><img alt=\"z2\" src=\"https://cdn.tgdd.vn/Files/2019/12/23/1228025/z2_800x480.jpg\" title=\"z2\" /></p>\r\n\r\n<p>Thiết kế tổng thể của iPhone 11 Pro.</p>\r\n\r\n<p>Mặc d&ugrave; c&oacute; một sự thật rằng iPhone 11 Pro kh&ocirc;ng kh&aacute;c biệt mấy so với d&ograve;ng&nbsp;<a href=\"https://www.thegioididong.com/dtdd/iphone-x-64gb\" target=\"_blank\" title=\"iPhone X\" type=\"iPhone X\">iPhone X</a>, tuy nhi&ecirc;n ta vẫn sẽ thấy một v&agrave;i cải tiến nhỏ ở thiết kế tổng thể. Chẳng hạn như, mặt sau b&acirc;y giờ l&agrave; k&iacute;nh mờ v&agrave; cụm camera đ&atilde; nhiều hơn trước (3 ống k&iacute;nh).</p>\r\n\r\n<p>Apple Face ID vẫn l&agrave; hệ thống nhận diện khu&ocirc;n mặt tốt nhất ở thời điểm hiện tại, được hỗ trợ rộng r&atilde;i nhất trong doanh nghiệp. B&ecirc;n cạnh đ&oacute;, với chip A13 Bionic c&ugrave;ng hệ thống điều hướng bằng cử chỉ hiệu quả (được cập nhật phần mềm iOS trong v&ograve;ng năm năm) sẽ mang đến những trải nghiệm tuyệt vời cho người d&ugrave;ng.</p>\r\n\r\n<p><img alt=\"f10\" src=\"https://cdn.tgdd.vn/Files/2019/09/12/1197622/f10_800x450.jpg\" title=\"iPhone 11 Pro\" /></p>\r\n\r\n<p>iPhone 11 Pro mang đến hiệu năng mượt m&agrave; c&ugrave;ng camera chất lượng.</p>\r\n\r\n<p>iPhone 11 Pro c&ograve;n được giới thiệu l&agrave; chiếc iPhone sở hữu cụm ba camera được cải tiến đ&aacute;ng kể với ống k&iacute;nh si&ecirc;u rộng, g&oacute;c rộng v&agrave; ống k&iacute;nh tele đủ tầm cạnh tranh trong nhiếp ảnh v&agrave; đ&aacute;nh bại nhiều đối thủ ở khả năng quay video. Chưa hết đ&acirc;u, chiếc m&aacute;y c&ograve;n được t&iacute;ch hợp chế độ ban đ&ecirc;m hiệu quả với chất ảnh đẹp mắt.</p>\r\n\r\n<p>Nhưng ngo&agrave;i ưu điểm được n&ecirc;u ở tr&ecirc;n th&igrave; iPhone 11 Pro vẫn c&oacute; một số khuyết điểm nhỏ. Cụ thể thiết bị chỉ&nbsp;sử dụng đầu sạc Lightning &quot;lỗi thời&quot; thay v&igrave; ti&ecirc;u chuẩn mới USB-C v&agrave; hệ điều h&agrave;nh iOS 13 vẫn c&ograve;n nhiều lỗi cần phải sửa. Nhưng n&oacute;i chung, kh&ocirc;ng c&ograve;n chiếc điện thoại n&agrave;o kh&aacute;c c&oacute; thể s&aacute;nh được với iPhone 11 Pro về hiệu năng, khả năng hiển thị v&agrave; k&iacute;ch thước tổng thể.</p>\r\n\r\n<ul>\r\n	<li><strong>Vậy n&ecirc;n mua iPhone 11 Pro hay kh&ocirc;ng?</strong></li>\r\n</ul>\r\n\r\n<p><strong>N&ecirc;n mua nếu:</strong>&nbsp;Bạn muốn sở hữu một chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" title=\"iPhone\" type=\"iPhone\">iPhone</a>&nbsp;tốt nhất (ở thời điểm hiện tại).</p>\r\n\r\n<p><strong>Kh&ocirc;ng n&ecirc;n mua nếu:</strong>&nbsp;Bạn kh&ocirc;ng muốn chi ra gần 31 triệu đồng hoặc muốn sử dụng thiết bị&nbsp;<a href=\"https://www.thegioididong.com/tim-kiem?key=android\" target=\"_blank\" title=\"Android\" type=\"Android\">Android</a>.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/danh-gia-chi-tiet-iphone-11-pro-pro-max-iphone-2019-1192896\" target=\"_blank\" title=\"đánh giá chi tiết iPhone 11 Pro\" type=\"đánh giá chi tiết iPhone 11 Pro\">Đ&aacute;nh gi&aacute; chi tiết iPhone 11 Pro: Sự lựa chọn hợp l&yacute; nhất trong bộ 3 iPhone 11 Series</a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dtdd/iphone-11-pro\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/188705/iphone-11-pro-black-400x400.jpg\" /></a></p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/dtdd/iphone-11-pro\">iPhone 11 Pro 64GB</a></h3>\r\n\r\n<p><strong>30.990.000₫</strong></p>\r\n\r\n<p>Khuyến m&atilde;iPhiếu mua h&agrave;ng 2 triệu (được quy đổi th&agrave;nh tiền mặt), &aacute;p dụng 27/12 - 1/1Mua k&egrave;m Apple Watch S3 giảm 500,000đ, S5 giảm 1 triệu, AirPods giảm 10% (Kh&ocirc;ng &aacute;p dụng với khuyến m&atilde;i kh&aacute;c)</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/them-vao-gio-hang?ProductId=188705\" target=\"blank\">ĐẶT MUA</a><a href=\"https://www.thegioididong.com/tra-gop/dtdd/iphone-11-pro\" target=\"blank\">TRẢ G&Oacute;P</a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><strong>3. Smartphone Android tốt nhất với k&iacute;ch thước nhỏ gọn: Samsung Galaxy S10</strong></h3>\r\n\r\n<p>Bạn đang t&igrave;m một chiếc smartphone Android sở hữu m&agrave;n h&igrave;nh lớn, m&agrave;u sắc rực rỡ nhưng k&iacute;ch thước nhỏ hơn th&igrave; đ&acirc;y,&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-s10\" target=\"_blank\" title=\"Samsung Galaxy S10\" type=\"Samsung Galaxy S10\">Samsung Galaxy S10</a>&nbsp;l&agrave; lựa chọn th&iacute;ch hợp nhất.</p>\r\n\r\n<p><img alt=\"z3\" src=\"https://cdn.tgdd.vn/Files/2019/12/23/1228025/z3_800x480.jpg\" title=\"z3\" /></p>\r\n\r\n<p>Thiết kế tổng thể của Samsung Galaxy S10.</p>\r\n\r\n<p>Chiếc Galaxy S10 được trang bị m&agrave;n h&igrave;nh cong 6.1 inch với độ ph&acirc;n giải l&ecirc;n đến QHD+ (2K). Kết hợp c&ugrave;ng tấm nền AMOLED cho m&agrave;u sắc tươi s&aacute;ng, độ tương phản cao với m&agrave;u đen c&oacute; độ s&acirc;u đ&atilde; mang đến trải nghiệm xem phim, chơi game đ&atilde; mắt v&agrave; hấp dẫn. Đồng thời, m&agrave;n h&igrave;nh m&aacute;y c&ograve;n c&oacute; một &quot;lỗ bấm&quot; nhỏ ở g&oacute;c tr&ecirc;n b&ecirc;n phải để chứa camera selfie.</p>\r\n\r\n<p>Ở Galaxy S10, Samsung đ&atilde; tinh tế thiết kế c&aacute;c viền bezels mỏng đi gi&uacute;p chiếc nhỏ gọn v&agrave; nhẹ hơn so với c&aacute;c đối thủ. Kh&ocirc;ng chỉ vậy, bộ &aacute;o bằng kim loại c&ugrave;ng lớp k&iacute;nh b&oacute;ng bẩy đ&atilde; gi&uacute;p chiếc m&aacute;y tr&ocirc;ng sang trọng v&agrave; đầy quyến rũ. Tuy nhi&ecirc;n, bạn n&ecirc;n d&ugrave;ng th&ecirc;m ốp lưng để gi&uacute;p cho việc cầm nắm sản phẩm chắc tay hơn.</p>\r\n\r\n<p><img alt=\"z4\" src=\"https://cdn.tgdd.vn/Files/2019/12/23/1228025/z4_800x450.jpg\" title=\"z4\" /></p>\r\n\r\n<p>Kh&ocirc;ng thể phủ nhận vẻ đẹp của Galaxy S10, khi m&agrave; chiếc m&aacute;y được kho&aacute;c l&ecirc;n bộ &aacute;o bằng kim loại đầy sang trọng.</p>\r\n\r\n<p>Galaxy S10 gần đ&acirc;y đ&atilde; được cập nhật với phần mềm mới l&agrave; One UI 2 t&ugrave;y biến từ Android 10 (bao gồm c&aacute;c cử chỉ điều hướng được cải tiến nhiều). Bạn sẽ nhận được khoảng ba năm hỗ trợ phần mềm từ Samsung, mặc d&ugrave; c&ocirc;ng ty thường tung bản cập nhật chậm hơn&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/google-phat-hanh-ban-cap-nhat-moi-cho-google-dich-1227737\" target=\"_blank\" title=\"Google\" type=\"Google\">Google</a>&nbsp;v&agrave; OnePlus để cung cấp c&aacute;c bản cập nhật phi&ecirc;n bản Android lớn.</p>\r\n\r\n<p>Kh&ocirc;ng chỉ vậy, thiết bị c&ograve;n sở hữu cụm ba camera chất lượng ở ph&iacute;a sau cho ph&eacute;p bạn thu ph&oacute;ng cảnh vật từ 0.5 đến 2 lần v&agrave; chuyển sang zoom lai 10 lần (đ&aacute;nh bại được Pixel 4 XL v&agrave; iPhone 11 Pro). Camera selfie của S10 cũng chẳng k&eacute;m cạnh đ&acirc;u khi cho chất ảnh tươi s&aacute;ng v&agrave; khả năng l&agrave;m mịn tốt.</p>\r\n\r\n<p><img alt=\"z5\" src=\"https://cdn.tgdd.vn/Files/2019/12/23/1228025/z5_800x450.jpg\" title=\"z5\" /></p>\r\n\r\n<p>Camera trước v&agrave; sau l&agrave; một trong những điểm mạnh của Galaxy S10.</p>\r\n\r\n<p>Mặc d&ugrave; sở hữu cấu h&igrave;nh v&agrave; hiệu năng hoạt động hiệu quả, tuy nhi&ecirc;n S10 lại c&oacute; tuổi thọ pin hơi yếu (k&eacute;o d&agrave;i được một ng&agrave;y nếu chỉ d&ugrave;ng những t&aacute;c vụ th&ocirc;ng thường). Đồng thời, cảm biến v&acirc;n tay si&ecirc;u &acirc;m t&iacute;ch hợp dưới m&agrave;n h&igrave;nh đ&atilde; được chứng minh l&agrave; hơi chậm v&agrave; độ hiệu quả giảm dần theo thời gian.</p>\r\n\r\n<ul>\r\n	<li><strong>Vậy n&ecirc;n mua Samsung Galaxy S10 hay kh&ocirc;ng?</strong></li>\r\n</ul>\r\n\r\n<p><strong>N&ecirc;n mua nếu:</strong>&nbsp;Bạn sở hữu một chiếc smartphone c&oacute; sự c&acirc;n bằng giữa k&iacute;ch thước m&agrave;n h&igrave;nh v&agrave; thiết kế tổng thể.</p>\r\n\r\n<p><strong>Kh&ocirc;ng n&ecirc;n mua nếu:</strong>&nbsp;Mong muốn c&oacute; một chiếc m&aacute;y với thời lượng pin d&agrave;i.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/tren-tay-danh-gia-chi-tiet-sieu-pham-samsung-galaxy-s10-va-galaxy-s10--1151126\" target=\"_blank\" title=\"đánh giá Samsung Galaxy S10\" type=\"đánh giá Samsung Galaxy S10\">Tr&ecirc;n tay, đ&aacute;nh gi&aacute; chi tiết si&ecirc;u phẩm Samsung Galaxy S10 v&agrave; Galaxy S10+</a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-s10\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/161554/sieu-pham-galaxy-s-black-400x400.jpg\" /></a></p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-s10\">Samsung Galaxy S10</a></h3>\r\n\r\n<p>M&agrave;n h&igrave;nh: 6.1&quot;, Quad HD+ (2K+)CPU: Exynos 9820 8 nh&acirc;nRAM: 8 GB, ROM: 128 GBCamera: Ch&iacute;nh 12 MP &amp; Phụ 12 MP, 16 MPSelfie: 10 MPPIN: 3400 mAh, c&oacute; sạc nhanh</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><strong>4. Camera &quot;chất&quot; v&agrave; tốt nhất: Huawei P30 Pro</strong></h3>\r\n\r\n<p>Theo g&oacute;c đ&aacute;nh gi&aacute; của trang b&aacute;o c&ocirc;ng nghệ The Guardian th&igrave; chiếc smartphone sở hữu camera tốt nhất (đến thời điểm hiện tại) ch&iacute;nh l&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd/huawei-p30-pro\" target=\"_blank\" title=\"Huawei P30 Pro\" type=\"Huawei P30 Pro\">Huawei P30 Pro</a>. Mặc d&ugrave; v&agrave;o cuối năm 2019, thị trường di động &quot;đ&oacute;n nhận&quot; nhiều thiết bị sở hữu camera ấn tượng nhưng kh&ocirc;ng c&oacute; chiếc m&aacute;y n&agrave;o mang đến sự kết hợp to&agrave;n diện như hệ thống&nbsp;Leica Quad Camera&nbsp;tr&ecirc;n P30 Pro.</p>\r\n\r\n<p><img alt=\"z7\" src=\"https://cdn.tgdd.vn/Files/2019/12/23/1228025/z7_800x480.jpg\" title=\"z7\" /></p>\r\n\r\n<p>Thiết kế tổng thể của Huawei P30 Pro.</p>\r\n\r\n<p>Cụm camera sau của Huawei P30 Pro gồm: Camera g&oacute;c si&ecirc;u rộng 20 MP (zoom gấp 0.6 lần), camera ch&iacute;nh 40 MP (được kết hợp bởi một camera zoom quang gấp 5 lần), gi&uacute;p bạn tiếp cận vật thể, khung cảnh ở xa hơn bất kỳ điện thoại th&ocirc;ng minh n&agrave;o kh&aacute;c. Kh&ocirc;ng chỉ vậy, c&ocirc;ng nghệ zoom lai 10 lần v&agrave; zoom kỹ thuật số l&ecirc;n đến 50 lần sẽ cho ph&eacute;p bạn chụp được những bức ảnh ở cự li rất xa. Ngo&agrave;i ra th&igrave; m&aacute;y c&ograve;n c&oacute; một camera độ s&acirc;u 8 MP v&agrave; cảm biến 3D ToF.</p>\r\n\r\n<p>Kh&ocirc;ng chỉ c&oacute; khả năng thu ph&oacute;ng h&igrave;nh ảnh ấn tượng th&ocirc;i đ&acirc;u m&agrave; ngay cả t&iacute;nh năng chụp ảnh đ&ecirc;m của P30 Pro cũng &quot;đỉnh&quot; chẳng k&eacute;m, biến m&agrave;n đ&ecirc;m th&agrave;nh ban ng&agrave;y chỉ bằng một c&aacute;i &quot;t&aacute;ch&quot;.</p>\r\n\r\n<p><img alt=\"z8\" src=\"https://cdn.tgdd.vn/Files/2019/12/23/1228025/z8_800x450.jpg\" title=\"z8\" /></p>\r\n\r\n<p>Huawei P30 Pro l&agrave; trước smartphone sở hữu camera to&agrave;n diện nhất theo The Guardian.</p>\r\n\r\n<p>Huawei P30 Pro sở hữu thiết kế tổng thể đẹp mắt với c&aacute;c viền cạnh xung quanh được l&agrave;m mỏng c&ugrave;ng khung kim loại đầy sang trọng. Ở mặt trước, chiếc m&aacute;y được trang bị m&agrave;n h&igrave;nh cong OLED k&iacute;ch thước 6.47 inch với độ ph&acirc;n giải Full HD+ v&agrave; một notch nhỏ ở ph&iacute;a tr&ecirc;n chứa camera selfie. Chưa hết, th&acirc;n m&aacute;y cũng kh&ocirc;ng qu&aacute; to n&ecirc;n bạn c&oacute; thể cầm nắm dễ d&agrave;ng.</p>\r\n\r\n<p>Về cấu h&igrave;nh th&igrave; P30 Pro sở hữu bộ vi xử l&yacute;&nbsp;Kirin 980 với RAM 8 GB v&agrave; ROM l&ecirc;n đến 128 GB, mang đến hiệu năng mạnh mẽ v&agrave; mượt m&agrave;. M&aacute;y c&ograve;n được t&iacute;ch hợp cảm biến v&acirc;n tay quang học tr&ecirc;n m&agrave;n h&igrave;nh (chỉ đứng thứ hai sau OnePlus 7T Pro), c&oacute; pin 4.200 mAh c&oacute; thể sử dụng đến&nbsp;hai ng&agrave;y (nếu &iacute;t d&ugrave;ng m&aacute;y), được t&iacute;ch hợp cả c&ocirc;ng nghệ sạc nhanh v&agrave; c&oacute; sạc kh&ocirc;ng d&acirc;y.</p>\r\n\r\n<p><img alt=\"Ảnh minh họa\" src=\"https://cdn.tgdd.vn/Files/2019/03/27/1157074/8_800x450.jpg\" title=\"sạc pin P30 Pro\" /></p>\r\n\r\n<p>Kh&ocirc;ng chỉ c&oacute; hiệu năng mượt m&agrave;, Huawei P30 Pro c&ograve;n c&oacute; thời lượng pin d&agrave;i, được t&iacute;ch hợp c&ocirc;ng nghệ sạc nhanh v&agrave; c&ocirc;ng nghệ sạc kh&ocirc;ng d&acirc;y.</p>\r\n\r\n<p>Cuối c&ugrave;ng, Huawei P30 Pro được chạy giao diện EMUI 10 t&ugrave;y biến từ&nbsp;Android 10 mang đến nhiều t&iacute;nh năng đa dạng v&agrave; hấp dẫn. Tuy nhi&ecirc;n c&oacute; một điều bạn cần phải biết đ&oacute; l&agrave; Huawei vẫn đang phải đối mặt với c&aacute;c lệnh cấm từ Mỹ như một phần của cuộc chiến thương mại Mỹ - Trung. Nhưng đừng qu&aacute; lo lắng nh&eacute; v&igrave; Android 10 của P30 Pro vẫn nhận được c&aacute;c bản cập nhật như b&igrave;nh thường.</p>\r\n\r\n<ul>\r\n	<li><strong>Vậy n&ecirc;n mua Huawei P30 Pro hay kh&ocirc;ng?</strong></li>\r\n</ul>\r\n\r\n<p><strong>N&ecirc;n mua nếu:</strong>&nbsp;Bạn muốn sở hữu smartphone với camera ấn tượng.</p>\r\n\r\n<p><strong>Kh&ocirc;ng n&ecirc;n mua nếu:</strong>&nbsp;Bạn muốn sở hữu smartphone nhỏ gọn hoặc lo lắng về lệnh cấm Huawei của Hoa Kỳ.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/danh-gia-huawei-p30-va-p30-pro-nhung-chien-binh-moi-cua-huawei-1157074\" target=\"_blank\" title=\"Đánh giá Huawei P30 Pro\" type=\"Đánh giá Huawei P30 Pro\">Đ&aacute;nh gi&aacute; Huawei P30 v&agrave; P30 Pro: Những &quot;chiến binh&quot; mới của Huawei</a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dtdd/huawei-p30-pro\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/197228/huawei-p30-pro-1-400x400.jpg\" /></a></p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/dtdd/huawei-p30-pro\">Huawei P30 Pro</a></h3>\r\n\r\n<p>M&agrave;n h&igrave;nh: 6.47&quot;, Full HD+CPU: Kirin 980 8 nh&acirc;nRAM: 8 GB, ROM: 256 GBCamera: Ch&iacute;nh 40 MP &amp; Phụ 20 MP, 8 MP, TOF 3DSelfie: 32 MPPIN: 4200 mAh, c&oacute; sạc nhanh</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><strong>5. Thiết bị đ&aacute;ng gi&aacute; nhất: OnePlus 7T</strong></h3>\r\n\r\n<p>Kh&ocirc;ng chỉ OnePlus 7T Pro m&agrave; ngay cả&nbsp;<a href=\"https://www.thegioididong.com/dtdd/oneplus-7t\" target=\"_blank\" title=\"OnePlus 7T\" type=\"OnePlus 7T\">OnePlus 7T</a>&nbsp;cũng được g&oacute;p mặt trong bảng xếp hạng n&agrave;y. Với hiệu năng mượt m&agrave;, thiết kế đẹp mắt c&ugrave;ng trải nghiệm &quot;đ&atilde;&quot; so với mức gi&aacute; đ&atilde; khiến OnePlus 7T gi&agrave;nh lấy vị tr&iacute; đầu bảng trong danh s&aacute;ch những chiếc smartphone đ&aacute;ng gi&aacute; nhất.</p>\r\n\r\n<p><img alt=\"z9\" src=\"https://cdn.tgdd.vn/Files/2019/12/23/1228025/z9_800x480.jpg\" title=\"z9\" /></p>\r\n\r\n<p>Thiết kế tổng thể của OnePlus 7T.</p>\r\n\r\n<p>OnePlus 7T c&oacute; m&agrave;n h&igrave;nh cong k&iacute;ch thước 6.41 inch, với tấm nền OLED cho h&igrave;nh ảnh đẹp mắt c&ugrave;ng với một notch nhỏ chứa camera selfie. Chiếc m&aacute;y cũng c&oacute; tốc độ l&agrave;m tươi l&ecirc;n đến 90 Hz giống như người anh OnePlus 7T Pro, mang đến cảm gi&aacute;c trơn tru v&agrave; mượt m&agrave; khi sử dụng c&aacute;c t&aacute;c vụ.</p>\r\n\r\n<p>Với bộ vi xử l&yacute; Snapdragon 855+, bộ nhớ RAM 8 GB c&ugrave;ng chuẩn chip UFS 3.0 sẽ mang những trải nghiệm sử dụng &quot;đ&atilde;&quot; v&agrave; kh&oacute; qu&ecirc;n. OnePlus 7T c&oacute; khả năng hoạt động trong 31 giờ chỉ với một lần sạc, sở hữu c&ocirc;ng nghệ sạc nhanh&nbsp;WarpCharge (đầy pin trong 60 ph&uacute;t)&nbsp;v&agrave; được t&iacute;ch hợp cảm biến v&acirc;n tay trong m&agrave;n h&igrave;nh si&ecirc;u nhanh với độ ch&iacute;nh x&aacute;c cao.</p>\r\n\r\n<p><img alt=\"z10\" src=\"https://cdn.tgdd.vn/Files/2019/12/23/1228025/z10_800x532.jpg\" title=\"z10\" /></p>\r\n\r\n<p>Thiết kế cụm camera OnePlus 7T kh&aacute; th&uacute; vị, c&ugrave;ng với đ&oacute; chất lượng ảnh cũng kh&ocirc;ng tồi ch&uacute;t n&agrave;o.</p>\r\n\r\n<p>Về khả năng chụp ảnh th&igrave; như bạn c&oacute; thể thấy, OnePlus 7T sở hữu cụm ba camera ở ph&iacute;a sau gồm: 1 camera ch&iacute;nh c&oacute; độ ph&acirc;n giải 48 MP, 1 camera g&oacute;c rộng 16 MP, 1 camera tele 12 MP c&ugrave;ng đ&egrave;n flash LED hai t&ocirc;ng m&agrave;u. X&eacute;t về chất lượng ảnh chụp th&igrave; cũng kh&ocirc;ng mấy nổi bật,&nbsp;tuy nhi&ecirc;n OnePlus 7T &quot;dư sức&quot; đ&aacute;nh bại nhiều đối thủ &quot;nặng k&iacute;&quot; tr&ecirc;n thị trường. Ở đ&oacute;, kh&ocirc;ng c&oacute; xếp hạng chống nước ch&iacute;nh thức.</p>\r\n\r\n<p>OnePlus 7T c&ograve;n được chạy giao diện OxygenOS 10 t&ugrave;y biến từ Android 10 với nhiều t&iacute;nh năng đa dạng, được OnePlus đảm bảo hai năm cập nhật phần mềm v&agrave; th&ecirc;m một năm cập nhật bảo mật (hai th&aacute;ng một lần). Một điều thiếu s&oacute;t l&agrave; m&aacute;y kh&ocirc;ng c&oacute; bất k&igrave; chuẩn chống nước n&agrave;o.</p>\r\n\r\n<ul>\r\n	<li><strong>Vậy n&ecirc;n mua OnePlus 7T hay kh&ocirc;ng?</strong></li>\r\n</ul>\r\n\r\n<p><strong>N&ecirc;n mua nếu:</strong>&nbsp;Bạn muốn c&oacute; một chiếc flagship nhưng kh&ocirc;ng muốn bỏ ra hơn 549 Euro (khoảng hơn 14 triệu đồng).</p>\r\n\r\n<p><strong>Kh&ocirc;ng n&ecirc;n mua nếu:</strong>&nbsp;Bạn muốn c&oacute; một chiếc smartphone c&oacute; camera &quot;xịn&quot;.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/danh-gia-chi-tiet-oneplus-7t-1202981\" target=\"_blank\" title=\"đánh giá OnePlus 7T\" type=\"đánh giá OnePlus 7T\">Đ&aacute;nh gi&aacute; chi tiết OnePlus 7T: Si&ecirc;u phẩm đ&aacute;ng quan t&acirc;m m&ugrave;a cuối năm!</a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dtdd/oneplus-7t\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/210336/oneplus-7t-1-400x400.jpg\" /></a></p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/dtdd/oneplus-7t\">OnePlus 7T</a></h3>\r\n\r\n<p>M&agrave;n h&igrave;nh: 6.55&quot;, Full HD+CPU: Qualcomm Snapdragon 855 Plus 8 nh&acirc;nRAM: 8 GB, ROM: 128 GBCamera: Ch&iacute;nh 48 MP &amp; Phụ 16 MP, 12 MPSelfie: 16 MPPIN: 3800 mAh, c&oacute; sạc nhanh</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><strong>Ngo&agrave;i những chiếc smartphone ở tr&ecirc;n th&igrave; c&ograve;n lựa chọn n&agrave;o kh&aacute;c?</strong></h3>\r\n\r\n<p>Nếu bạn chưa &quot;ưng &yacute;&quot; với những chiếc smartphone ở tr&ecirc;n th&igrave; vẫn c&ograve;n một v&agrave;i lựa chọn đ&aacute;ng quan t&acirc;m kh&aacute;c. C&ugrave;ng xem ngay ở phần dưới đ&acirc;y n&agrave;o.</p>\r\n\r\n<ul>\r\n	<li><strong>Apple iPhone 11</strong></li>\r\n</ul>\r\n\r\n<p>Apple&nbsp;<a href=\"https://www.thegioididong.com/dtdd/iphone-11\" target=\"_blank\" title=\"iPhone 11\" type=\"iPhone 11\">iPhone 11</a>&nbsp;l&agrave; phi&ecirc;n bản kế nhiệm của&nbsp;<a href=\"https://www.thegioididong.com/dtdd/iphone-xr\" target=\"_blank\" title=\"iPhone Xr\" type=\"iPhone Xr\">iPhone Xr</a>, c&oacute; mức gi&aacute; rẻ nhất trong bộ ba sản phầm thuộc d&ograve;ng iPhone 11. Tuy nhi&ecirc;n, chiếc m&aacute;y c&oacute; pin vẫn c&ograve;n hơi &#39;&#39;yếu&quot;, th&acirc;n m&aacute;y được l&agrave;m bằng nh&ocirc;m v&agrave; thủy tinh thay v&igrave; được gia c&ocirc;ng bằng th&eacute;p kh&ocirc;ng gỉ. Điều n&agrave;y đ&atilde; khiến cho iPhone 11 mất đi cảm gi&aacute;c sang trọng khi so s&aacute;nh với đ&agrave;n anh iPhone 11 Pro v&agrave; iPhone 11 Pro Max.</p>\r\n\r\n<p><img alt=\"z11\" src=\"https://cdn.tgdd.vn/Files/2019/12/23/1228025/z11_800x450.jpg\" title=\"z11\" /></p>\r\n\r\n<p>Thiết kế tổng thể của iPhone 11.</p>\r\n\r\n<p>B&ugrave; lại th&igrave; iPhone 11 mang đến nhiều phi&ecirc;n bản m&agrave;u sắc tươi s&aacute;ng như: Đỏ, trắng, v&agrave;ng,...Nhưng tất nhi&ecirc;n, iPhone 11 Pro vẫn l&agrave; chiếc smartphone đ&aacute;ng để mua nếu bạn muốn c&oacute; chiếc iPhone tốt nhất. C&ograve;n nếu bạn muốn một chiếc điện thoại rẻ hơn, h&atilde;y chuyển sang Android hoặc mua iPhone Xr.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dtdd/iphone-11\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-11-red-400x400.jpg\" /></a></p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/dtdd/iphone-11\">iPhone 11 64GB</a></h3>\r\n\r\n<p><strong>21.990.000₫</strong></p>\r\n\r\n<p>Khuyến m&atilde;iPhiếu mua h&agrave;ng 600.000đ (được quy đổi th&agrave;nh tiền mặt), &aacute;p dụng 27/12 - 1/1Mua k&egrave;m Apple Watch S3 giảm 500,000đ, S5 giảm 1 triệu, AirPods giảm 10% (Kh&ocirc;ng &aacute;p dụng với khuyến m&atilde;i kh&aacute;c)</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/them-vao-gio-hang?ProductId=153856\" target=\"blank\">ĐẶT MUA</a><a href=\"https://www.thegioididong.com/tra-gop/dtdd/iphone-11\" target=\"blank\">TRẢ G&Oacute;P</a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><strong>Apple iPhone 11 Pro Max</strong></li>\r\n</ul>\r\n\r\n<p>Nếu bạn cần một chiếc iPhone v&agrave; mong muốn m&aacute;y phải c&oacute; m&agrave;n h&igrave;nh hoặc thời lượng pin tốt th&igrave; iPhone 11 Pro Max l&agrave; lựa chọn duy nhất. Nhưng c&oacute; một sự thật rằng, iPhone 11 Pro Max kh&aacute; lớn, đắt v&agrave; nặng, do đ&oacute; iPhone 11 Pro hoặc iPhone 11 vẫn l&agrave; những lựa chọn tốt hơn.</p>\r\n\r\n<p><img alt=\"z12\" src=\"https://cdn.tgdd.vn/Files/2019/12/23/1228025/z12_800x450.jpg\" title=\"z12\" /></p>\r\n\r\n<p>Thiết kế tổng thể của iPhone 11 Pro Max.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dtdd/iphone-11-pro-max\"><img src=\"https://cdn.tgdd.vn/Products/Images/42/200533/iphone-11-pro-max-green-400x400.jpg\" /></a></p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/dtdd/iphone-11-pro-max\">iPhone 11 Pro Max 64GB</a></h3>\r\n\r\n<p><strong>33.990.000₫</strong></p>\r\n\r\n<p>Khuyến m&atilde;iPhiếu mua h&agrave;ng 2 triệu (được quy đổi th&agrave;nh tiền mặt), &aacute;p dụng 27/12 - 1/1Mua k&egrave;m Apple Watch S3 giảm 500,000đ, S5 giảm 1 triệu, AirPods giảm 10% (Kh&ocirc;ng &aacute;p dụng với khuyến m&atilde;i kh&aacute;c)</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/them-vao-gio-hang?ProductId=200533\" target=\"blank\">ĐẶT MUA</a><a href=\"https://www.thegioididong.com/tra-gop/dtdd/iphone-11-pro-max\" target=\"blank\">TRẢ G&Oacute;P</a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ngo&agrave;i ra, th&ecirc;m một v&agrave;i sản phẩm m&agrave; bạn c&oacute; thể ch&uacute; &yacute; đến đ&oacute; l&agrave;:</p>\r\n\r\n<ul>\r\n	<li><a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-s10e\" target=\"_blank\" title=\"Galaxy S10e\" type=\"Galaxy S10e\">Samsung Galaxy S10e</a>&nbsp;/&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-s10-plus\" target=\"_blank\" title=\"Galaxy S10 Plus\" type=\"Galaxy S10 Plus\">Galaxy S10+</a>&nbsp;/&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-s10-5g\" target=\"_blank\" title=\"Galaxy S10 5G\" type=\"Galaxy S10 5G\">Galaxy S10 5G</a>.</li>\r\n	<li><a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-note-10-plus\" target=\"_blank\" title=\"Galaxy Note 10+\" type=\"Galaxy Note 10+\">Samsung Galaxy Note 10+</a>.</li>\r\n	<li><a href=\"https://www.thegioididong.com/dtdd/huawei-mate-20-pro\" target=\"_blank\" title=\"Huawei Mate 20 Pro\" type=\"Huawei Mate 20 Pro\">Huawei Mate 20 Pro</a>.</li>\r\n	<li><a href=\"https://www.thegioididong.com/dtdd/google-pixel-4-xl\" target=\"_blank\" title=\"Google Pixel 4 XL\" type=\"Google Pixel 4 XL\">Google Pixel 4 XL</a>.</li>\r\n	<li><a href=\"https://www.thegioididong.com/dtdd/xiaomi-mi-mix-3\" target=\"_blank\" title=\"Xiaomi Mi Mix 3\" type=\"Xiaomi Mi Mix 3\">Xiaomi Mi Mix 3</a>.</li>\r\n</ul>\r\n\r\n<h3><strong>Tổng kết</strong></h3>\r\n\r\n<p>Đ&oacute; ch&iacute;nh l&agrave; danh s&aacute;nh những chiếc smartphone &quot;b&aacute; đạo&quot; nhất trong từng ph&acirc;n kh&uacute;c kh&aacute;c nhau. Tuy nhi&ecirc;n m&igrave;nh tin chắc rằng, chỉ sau v&agrave;i th&aacute;ng nữa th&ocirc;i (bước qua năm 2020) th&igrave; bảng xếp hạng n&agrave;y sẽ mang đến rất nhiều sự thay đổi. Nếu bạn vẫn d&agrave;nh sự quan t&acirc;m đến chuy&ecirc;n mục top những sản phẩm &quot;b&aacute; đạo&quot; nhất trong năm 2020, h&atilde;y lu&ocirc;n theo d&otilde;i tin tức tr&ecirc;n trang&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc\" target=\"_blank\" title=\"TGDD\" type=\"TGDD\">Thế Giới Di Động</a>&nbsp;nh&eacute;.</p>\r\n\r\n<p>Hỏi nhỏ n&egrave;, bạn c&oacute; cho rằng đ&acirc;y l&agrave; những chiếc flagship tốt nhất trong năm 2019 hay kh&ocirc;ng? Nếu kh&ocirc;ng th&igrave; h&atilde;y b&igrave;nh luận ngay b&ecirc;n dưới v&agrave; đưa ra bảng xếp hạng smartphone (theo c&aacute; nh&acirc;n bạn) ngay nh&eacute;.</p>\r\n', 'http://localhost:8080/ProjectWeb_1920/data/images/img-tintuc/18.jpg', 'Chuong Ang', '2019-12-29', 1, 1);
INSERT INTO `news` VALUES (19, '5 phần mềm đo nhiệt độ trên máy tính Windows chuẩn nhất', 'Thủ thuật', 'Trong quá trình sử dụng  máy tính , chúng ta cần phải kiểm tra nhiệt độ hoạt động của phần cứng để kiểm tra xem CPU...', '<h3>Trong qu&aacute; tr&igrave;nh sử dụng&nbsp;<a href=\"http://www.thegioididong.com/may-tinh-de-ban\" target=\"_blank\">m&aacute;y t&iacute;nh</a>, ch&uacute;ng ta cần phải kiểm tra nhiệt độ hoạt động của phần cứng để kiểm tra xem CPU của m&igrave;nh c&oacute; hoạt động b&igrave;nh thường hay kh&ocirc;ng. Dưới đ&acirc;y l&agrave; 5 phần mềm gi&uacute;p bạn kiểm tra nhiệt CPU một c&aacute;ch đơn giản v&agrave; ch&iacute;nh x&aacute;c.</h3>\r\n\r\n<p><em>&quot;Bộ vị xử l&yacute; m&aacute;y t&iacute;nh (CPU) của bạn thực hiện h&agrave;ng triệu hoạt động v&agrave; c&ocirc;ng việc mỗi gi&acirc;y, điều n&agrave;y c&oacute; thể khiến bộ xử l&yacute; trở n&ecirc;n kh&aacute; n&oacute;ng. Nếu CPU qu&aacute; n&oacute;ng, bạn c&oacute; thể gặp c&aacute;c sự cố ổn định hệ thống, sự cố v&agrave; chậm m&aacute;y t&iacute;nh hoặc thậm ch&iacute; tạo ra thiệt hại một c&aacute;ch l&acirc;u d&agrave;i. B&agrave;i viết dưới đ&acirc;y sẽ đưa ra c&aacute;c phần mềm cung cấp cho bạn giải ph&aacute;p đơn giản để c&oacute; thể kiểm tra nhiệt độ của CPU một c&aacute;c dễ d&agrave;ng.&quot;</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><strong>1. Phần mềm CPUID HWMonitor</strong></h3>\r\n\r\n<p><strong><img alt=\"Phần mềm HWMonitor\" src=\"//cdn.tgdd.vn/hoi-dap/1228709/5-phan-mem-do-nhiet-do-tren-may-tinh-windows-chuan-.jpg\" title=\"Phần mềm HWMonitor\" /></strong></p>\r\n\r\n<p><strong>HWMonitor</strong>&nbsp;l&agrave; một chương tr&igrave;nh gi&aacute;m s&aacute;t phần cứng đọc c&aacute;c cảm biến sức khỏe ch&iacute;nh của hệ thống PC:&nbsp;<strong>điện &aacute;p, nhiệt độ, tốc độ quạt.Chương tr&igrave;nh xử l&yacute; c&aacute;c chip cảm biến phổ biến nhất</strong>, như d&ograve;ng ITE&reg; IT87, hầu hết c&aacute;c IC Winbond&reg; v&agrave; c&aacute;c loại kh&aacute;c. Ngo&agrave;i ra, n&oacute; c&oacute; thể đọc c&aacute;c&nbsp;<strong>cảm biến nhiệt l&otilde;i CPU hiện đại, cũng như c&oacute; nhiệt độ ổ cứng v&agrave; nhiệt độ GPU của Card đồ họa.</strong></p>\r\n\r\n<ul>\r\n	<li>Link tải:&nbsp;<a href=\"http://www.cpuid.com/downloads/hwmonitor/hwmonitor_1.35.zip\" target=\"_blank\">Tại đ&acirc;y</a></li>\r\n</ul>\r\n\r\n<h3>2. Phần mềm Speccy</h3>\r\n\r\n<p><img alt=\"Phần mềm Speccy\" src=\"//cdn.tgdd.vn/hoi-dap/1228709/5-phan-mem-do-nhiet-do-tren-may-tinh-windows-chuan-1-.jpg\" title=\"Phần mềm Speccy\" /></p>\r\n\r\n<p>Speccy&nbsp;l&agrave; một c&ocirc;ng cụ c&ocirc;ng cụ kiểm tra phần cứng&nbsp;ti&ecirc;n tiến, to&agrave;n diện, kh&ocirc;ng tốn nhiều dung lượng cho PC&nbsp;của bạn. N&oacute; cung cấp cho bạn số liệu thống k&ecirc; chi tiết về mọi phần cứng trong m&aacute;y t&iacute;nh của bạn; bao gồm&nbsp;CPU, bo mạch chủ, RAM, card đồ họa, đĩa cứng, ổ đĩa quang v&agrave; hỗ trợ &Acirc;m thanh.</p>\r\n\r\n<p>Ngo&agrave;i ra,&nbsp;Speccy&nbsp;cũng th&ecirc;m nhiệt độ của c&aacute;c th&agrave;nh phần kh&aacute;c nhau của bạn, v&igrave; vậy bạn c&oacute; thể dễ d&agrave;ng kiểm tra nếu c&oacute; vấn đề.&nbsp;C&aacute;c t&iacute;nh năng ch&iacute;nh bao gồm:th&ocirc;ng tin về bộ xử l&yacute;, k&iacute;ch thước v&agrave; tốc độ ổ cứng, dung lượng bộ nhớ (RAM), Card đồ họa, Hệ điều h&agrave;nh, ổ quang, đồng hồ tốc độ.</p>\r\n\r\n<ul>\r\n	<li>Link tải:&nbsp;<a href=\"http://filehippo.com/download_speccy\" target=\"_blank\">Tại đ&acirc;y</a></li>\r\n</ul>\r\n\r\n<h3><strong>3. Phần mềm Core Temp</strong></h3>\r\n\r\n<p><img alt=\"Phần mềm Core Temp\" src=\"//cdn.tgdd.vn/hoi-dap/1228709/5-phan-mem-do-nhiet-do-tren-may-tinh-windows-chuan-2-.jpg\" title=\"Phần mềm Core Temp\" /></p>\r\n\r\n<p><strong>Core Temp</strong>&nbsp;l&agrave; một chương tr&igrave;nh&nbsp;<strong>nhỏ gọn nhưng mạnh mẽ</strong>&nbsp;để theo d&otilde;i nhiệt độ của bộ xử l&yacute; v&agrave; c&aacute;c th&ocirc;ng tin quan trọng kh&aacute;c. Điều l&agrave;m cho Core Temp trở n&ecirc;n độc đ&aacute;o l&agrave; c&aacute;ch n&oacute; hoạt động. N&oacute; c&oacute; khả năng hiển thị&nbsp;<strong>nhiệt độ của từng l&otilde;i ri&ecirc;ng lẻ của mỗi bộ xử l&yacute; trong hệ thống&nbsp;</strong>của bạn. Bạn c&oacute; thể thấy sự dao động nhiệt độ trong thời gian thực với khối lượng c&ocirc;ng việc kh&aacute;c nhau. C&aacute;c chỉ số nhiệt độ đều dựa tr&ecirc;n cảm biến&nbsp;<strong>DTS</strong>.</p>\r\n\r\n<p><em>&quot;<strong>DTS</strong>&quot; (Cảm biến nhiệt kỹ thuật số) trong c&aacute;c sản phẩm của họ.&nbsp;<strong>DTS</strong>&nbsp;cung cấp số đọc nhiệt độ ph&acirc;n giải ch&iacute;nh x&aacute;c v&agrave; cao hơn so với c&aacute;c cảm biến nhiệt t&iacute;ch hợp th&ocirc;ng thường. T&iacute;nh năng n&agrave;y được hỗ trợ bởi tất cả c&aacute;c bộ xử l&yacute; x86, gần đ&acirc;y c&aacute;c bộ xử l&yacute; của Intel, AMD v&agrave; VIA đều được hỗ trợ.</em></p>\r\n\r\n<ul>\r\n	<li>Link tải:&nbsp;<a href=\"http://www.alcpu.com/CoreTemp/\" target=\"_blank\">Tại đ&acirc;y</a></li>\r\n</ul>\r\n\r\n<h3><strong>4. Phần mềm Real Temp</strong></h3>\r\n\r\n<p><img alt=\"Phần mềm Real Temp\" src=\"//cdn.tgdd.vn/hoi-dap/1228709/5-phan-mem-do-nhiet-do-tren-may-tinh-windows-chuan-3-1.jpg\" title=\"Phần mềm Real Temp\" /></p>\r\n\r\n<p><strong>RealTemp</strong>&nbsp;l&agrave; chương tr&igrave;nh theo d&otilde;i nhiệt độ được thiết kế cho tất cả c&aacute;c&nbsp;<strong>bộ xử l&yacute; l&otilde;i đơn, l&otilde;i k&eacute;p v&agrave; l&otilde;i tứ của Intel</strong>. Mỗi l&otilde;i tr&ecirc;n c&aacute;c bộ xử l&yacute; n&agrave;y c&oacute; cảm biến nhiệt kỹ thuật số (<strong>DTS</strong>) b&aacute;o c&aacute;o dữ liệu nhiệt độ li&ecirc;n quan đến<strong>&nbsp;TJ. Max -&nbsp;</strong>nhiệt độ l&otilde;i hoạt động tối đa an to&agrave;n cho CPU.</p>\r\n\r\n<p>Việc n&agrave;y rất quan trọng bởi v&igrave; khi CPU n&oacute;ng l&ecirc;n, c&aacute;c cảm biến sẽ giảm hiệu suất để giữ nhiệt độ trong tầm kiểm so&aacute;t. L&uacute;c n&agrave;y, dựa v&agrave;o RealTemp, bạn h&atilde;y l&agrave;m m&aacute;t CPU hoặc ngưng c&aacute;c t&aacute;c vụ n&agrave;y để hỗ trợ bảo vệ CPU của m&igrave;nh.</p>\r\n\r\n<ul>\r\n	<li>Link tải:&nbsp;<a href=\"http://www.techpowerup.com/download/techpowerup-real-temp/\" target=\"_blank\">Tại đ&acirc;y</a></li>\r\n</ul>\r\n\r\n<h3><strong>5. Phần mềm SpeedFan</strong></h3>\r\n\r\n<p><img alt=\"Phần mềm Speedfan\" src=\"//cdn.tgdd.vn/hoi-dap/1228709/5-phan-mem-do-nhiet-do-tren-may-tinh-windows-chuan-4-1.jpg\" title=\"Phần mềm Speedfan\" />​</p>\r\n\r\n<p>Nếu bạn cần một c&ocirc;ng cụ c&oacute; thể&nbsp;<strong>thay đổi tốc độ quạt của m&aacute;y t&iacute;nh, đọc nhiệt độ của bo mạch chủ v&agrave; đĩa cứng của bạn, đọc điện &aacute;p v&agrave; tốc độ quạt v&agrave; kiểm tra trạng th&aacute;i của đĩa cứng</strong>&nbsp;,<strong>SpeedFan</strong>&nbsp;đ&uacute;ng l&agrave; phần mềm d&agrave;nh cho bạn. SpeedFan l&agrave; phần mềm gi&uacute;p bạn thực hiện c&aacute;c việc n&agrave;y.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute; SpeedFan c&ograve;n cho ph&eacute;p cung cấp cho ch&uacute;ng ta th&ocirc;ng tin về&nbsp;<strong>tốc độ quạt tản nhiệt, t&igrave;nh trạng hoạt động của CPU</strong>, thậm ch&iacute; bạn c&ograve;n c&oacute; thể điều chỉnh tốc độ quạt của VGA nếu muốn. Giống như c&aacute;c phần mềm tr&ecirc;n th&igrave; SpeedFan cũng ho&agrave;n to&agrave;n miễn ph&iacute; n&ecirc;n đ&acirc;y cũng l&agrave; lựa chọn tốt cho bạn.</p>\r\n\r\n<ul>\r\n	<li>Link tải:&nbsp;<a href=\"http://www.almico.com/sfdownload.php\" target=\"_blank\">Tại đ&acirc;y</a></li>\r\n</ul>\r\n', 'http://localhost:8080/ProjectWeb_1920/data/images/img-tintuc/19.jpg', 'Khôi Phạm Như Nguyên', '2019-12-24', 1, 1);

-- ----------------------------
-- Table structure for orders
-- ----------------------------
DROP TABLE IF EXISTS `orders`;
CREATE TABLE `orders`  (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `image` varchar(255) CHARACTER SET utf8 COLLATE utf8_vietnamese_ci NULL DEFAULT NULL,
  `productname` varchar(255) CHARACTER SET utf8 COLLATE utf8_vietnamese_ci NULL DEFAULT NULL,
  `price` int(10) NULL DEFAULT NULL,
  `quantity` int(100) NULL DEFAULT NULL,
  `totalprice` bigint(20) NULL DEFAULT NULL,
  `ngay_gio` varchar(255) CHARACTER SET utf8 COLLATE utf8_vietnamese_ci NULL DEFAULT NULL,
  `trang_thai` varchar(255) CHARACTER SET latin1 COLLATE latin1_swedish_ci NULL DEFAULT NULL,
  `id_product` int(11) NULL DEFAULT NULL,
  `id_khach_hang` varchar(255) CHARACTER SET utf8 COLLATE utf8_vietnamese_ci NULL DEFAULT NULL,
  `address` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 33 CHARACTER SET = latin1 COLLATE = latin1_swedish_ci ROW_FORMAT = Dynamic;

