-- ----------------------------
-- Table structure for payment
-- ----------------------------
DROP TABLE IF EXISTS `payment`;
CREATE TABLE `payment`  (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `paytype` varchar(255) CHARACTER SET utf8 COLLATE utf8_vietnamese_ci NOT NULL,
  `createdby` varchar(255) CHARACTER SET utf8 COLLATE utf8_vietnamese_ci NULL DEFAULT NULL,
  `createddate` datetime(0) NULL DEFAULT NULL,
  `modifiedby` varchar(255) CHARACTER SET utf8 COLLATE utf8_vietnamese_ci NULL DEFAULT NULL,
  `modifieddate` datetime(0) NULL DEFAULT NULL,
  `id_cart` int(11) NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `id_cart`(`id_cart`) USING BTREE,
  CONSTRAINT `payment_ibfk_1` FOREIGN KEY (`id_cart`) REFERENCES `cart` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE = InnoDB AUTO_INCREMENT = 1 CHARACTER SET = utf8 COLLATE = utf8_vietnamese_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Table structure for product
-- ----------------------------
DROP TABLE IF EXISTS `product`;
CREATE TABLE `product`  (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `productname` varchar(255) CHARACTER SET utf8 COLLATE utf8_vietnamese_ci NOT NULL,
  `price` int(11) NOT NULL,
  `pricepromotion` int(11) NULL DEFAULT NULL,
  `image` varchar(255) CHARACTER SET utf8 COLLATE utf8_vietnamese_ci NOT NULL,
  `imagedetail` text CHARACTER SET utf8 COLLATE utf8_vietnamese_ci NULL,
  `type` varchar(255) CHARACTER SET utf8 COLLATE utf8_vietnamese_ci NULL DEFAULT NULL,
  `active` tinyint(2) NULL DEFAULT NULL,
  `description` text CHARACTER SET utf8 COLLATE utf8_vietnamese_ci NULL,
  `detail` text CHARACTER SET utf8 COLLATE utf8_vietnamese_ci NULL,
  `createdby` varchar(255) CHARACTER SET utf8 COLLATE utf8_vietnamese_ci NULL DEFAULT NULL,
  `createddate` datetime(0) NULL DEFAULT NULL,
  `modifiedby` varchar(255) CHARACTER SET utf8 COLLATE utf8_vietnamese_ci NULL DEFAULT NULL,
  `modifieddate` datetime(0) NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 110 CHARACTER SET = utf8 COLLATE = utf8_vietnamese_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of product
-- ----------------------------
INSERT INTO `product` VALUES (3, 'Iphone 11 Pro Max 64GB', 30000000, 0, 'http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphone11/iphone11.jpg', 'http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphone11promax/1.jpg-http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphone11promax/3.jpg-http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphone11promax/4.jpg-http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphone11promax/5.jpg-', 'iphone', 1, '<p><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:15.0pt\"><span style=\"color:#333333\">Sức mạnh h&agrave;ng đầu của Pro</span></span></strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#333333\">Được trang bị bộ vi xử l&yacute; Apple A13 Bionic, iPhone 11 Pro Max tự tin thể hiện đẳng cấp hiệu năng &ldquo;Pro&rdquo;. So với thế hệ trước, Apple A13 Bionic nhanh hơn 20% v&agrave; ti&ecirc;u thụ năng lượng &iacute;t hơn tới 40% cả về CPU lẫn GPU. iPhone 11 Pro Max mạnh mẽ vượt trội khi đặt cạnh bất cứ đối thủ n&agrave;o tr&ecirc;n thị trường hiện nay. Mọi t&aacute;c vụ kể cả những tựa game nặng nhất cũng đều được thể hiện trơn tru, mượt m&agrave; tr&ecirc;n iPhone 11 Pro Max 64GB.<img alt=\"\" src=\"http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphone11promax/iPhone-11-Pro-10.jpg\" style=\"height:540px; width:960px\" /></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#333333\"><img alt=\"\" src=\"http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphone11promax/10.jpg\" style=\"height:394px; width:700px\" /></span></span></span></span></span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:13.5pt\"><span style=\"background-color:white\"><strong><strong><span style=\"font-size:15.0pt\"><span style=\"color:#333333\">Thời lượng pin tốt nhất trong lịch sử iPhone</span></span></strong></strong></span></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"color:#333333\">Bạn đ&atilde; từng thấy iPhone Xs Max c&oacute; thời lượng pin tốt đến mức n&agrave;o, nhưng đ&oacute; chưa phải l&agrave; tất cả. iPhone 11 Pro Max l&agrave; chiếc iPhone c&oacute; thời lượng pin tốt nhất từ trước đến nay, thậm ch&iacute; c&ograve;n vượt xa khi so với iPhone Xs Max. Thời gian sử dụng của iPhone 11 Pro Max d&agrave;i hơn 5 giờ, cho ph&eacute;p bạn thoải m&aacute;i l&agrave;m tất cả những điều m&igrave;nh th&iacute;ch. Kết quả n&agrave;y c&oacute; được nhờ sự kết nối ăn khớp giữa phần cứng (bao gồm pin, chip, m&agrave;n h&igrave;nh) v&agrave; hệ điều h&agrave;nh mới. Ấn tượng hơn nữa, với sạc nhanh 18W đi k&egrave;m, bạn chỉ mất 30 ph&uacute;t sạc cho 50% pin. Lu&ocirc;n đầy đủ năng lượng v&agrave; sẵn s&agrave;ng đương đầu với mọi thử th&aacute;ch, đ&oacute; l&agrave; iPhone 11 Pro Max.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphone11promax/8.jpg\" style=\"height:394px; width:700px\" /></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:13.5pt\"><span style=\"background-color:white\"><strong><strong><span style=\"font-size:15.0pt\"><span style=\"color:#333333\">T&aacute;c phẩm nghệ thuật đ&iacute;ch thực</span></span></strong></strong></span></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"color:#333333\">Kh&ocirc;ng chỉ đơn thuần l&agrave; một chiếc điện thoại, iPhone 11 Pro Max l&agrave; một t&aacute;c phẩm nghệ thuật đ&iacute;ch thực. Sự kết hợp ho&agrave;n hảo giữa những vật liệu cao cấp l&agrave; khung th&eacute;p kh&ocirc;ng gỉ v&agrave; hai mặt k&iacute;nh cường lực gi&uacute;p iPhone 11 Pro Max rất sang trọng, đẳng cấp. iPhone mới đ&atilde; chuyển sang mặt lưng sơn nh&aacute;m độc đ&aacute;o, đẹp mắt hơn đồng thời kh&ocirc;ng bị b&aacute;m bẩn hay dấu v&acirc;n tay. iPhone Pro Max c&oacute; 4 m&agrave;u thời thượng l&agrave; V&agrave;ng, X&aacute;m kh&ocirc;ng gian, Bạc v&agrave; Xanh Midnight. D&ugrave; l&agrave; một chiếc điện thoại m&agrave;n h&igrave;nh lớn, nhưng sự ho&agrave;n thiện cao cấp gi&uacute;p iPhone 11 Pro Max vẫn dễ d&agrave;ng cầm nắm v&agrave; thao t&aacute;c.</span></span></span></p>\r\n\r\n<p><img alt=\"\" src=\"http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphone11promax/9.jpg\" style=\"height:394px; width:700px\" /></p>\r\n\r\n<h3><strong>K&iacute;nh cường lực si&ecirc;u cứng, chống nước ho&agrave;n hảo</strong></h3>\r\n\r\n<p>iPhone 11 Pro Max kh&ocirc;ng chỉ đẹp xuất sắc m&agrave; c&ograve;n l&agrave; một chiếc điện thoại v&ocirc; c&ugrave;ng bền vững. Được chế t&aacute;c từ khung th&eacute;p kh&ocirc;ng gỉ v&agrave; hai mặt k&iacute;nh cường lực cứng nhất thế giới smartphone, iPhone 11 Pro Max c&oacute; khả năng chống va đập cực tốt. Chất lỏng cũng kh&ocirc;ng phải l&agrave; mối nguy hại với iPhone 11 Pro Max khi với chuẩn chống nước IP68, điện thoại c&oacute; khả năng ng&acirc;m nước dưới độ s&acirc;u 4m trong thời gian tối đa 30 ph&uacute;t. Thiết kế vững chắc gi&uacute;p bạn y&ecirc;n t&acirc;m hơn khi sử dụng.<img alt=\"\" src=\"http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphone11promax/iphone-11-pro-max-5.jpg\" style=\"height:540px; width:960px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div id=\"eJOY__extension_root\" style=\"all:unset\">&nbsp;</div>\r\n\r\n<p>&quot;&gt;</p>\r\n', 'Màn hình-6.5 inchs, Super Retina XDR, 1242 x 2688 Pixels-Cammera trước-12.0 MP-Camera sau-Triple 12MP Ultra Wide, Wide and Telephoto cameras-Ram-4GB-Bộ nhớ trong-64GB-CPU-Apple A13 Bionic, 6, Đang cập nhật-GPU-Apple GPU 4 nhân-Dung lượng pin-Lâu hơn iPhone Xs Max 5h-Hệ điều hành-	iOS 13-Thẻ sim-	eSIM và NanoSIM, 1 Sim-', NULL, NULL, NULL, NULL);
INSERT INTO `product` VALUES (4, 'Iphone 11 Pro 128GB', 30250000, 29399000, 'http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphone11pro/iphone11pro.jpg', 'http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphone11pro/iphone11pro7.jpg-http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphone11pro/iphone11pro8.jpg-http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphone11pro/iphone11pro9.jpg-http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphone11pro/iphone11pro10.jpg-', 'iphone', 1, '<p>&nbsp;</p>\r\n\r\n<h3><strong>Thời lượng pin kh&oacute; tin</strong></h3>\r\n\r\n<p>Bạn sẽ bất ngờ về thời lượng pin của iPhone 11 Pro. Chiếc iPhone mới c&oacute; thời gian sử dụng l&acirc;u hơn iPhone Xs trước đ&acirc;y tới 4 giờ đồng hồ. Đ&acirc;y l&agrave; kết quả của cả hệ thống vận h&agrave;nh, bao gồm pin, chip, m&agrave;n h&igrave;nh, phần mềm được cải tiến. Tr&ecirc;n iPhone 11 Pro bạn c&oacute; thể sử dụng thoải m&aacute;i kh&ocirc;ng lo hết pin, đồng thời sạc cực nhanh bằng củ sạc 18W đi k&egrave;m. Chỉ cần 30 ph&uacute;t sạc cho cả ng&agrave;y d&agrave;i năng động, lu&ocirc;n lu&ocirc;n sẵn s&agrave;ng v&agrave; kh&ocirc;ng phải chờ đợi với iPhone 11 Pro.<img alt=\"\" src=\"http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphone11pro/iphone11pro4.jpg\" style=\"height:394px; width:700px\" /></p>\r\n\r\n<h3><strong>Thiết kế mặt lưng nh&aacute;m cao cấp</strong></h3>\r\n\r\n<p>Vẫn được ho&agrave;n thiện từ khung th&eacute;p kh&ocirc;ng gỉ cao cấp v&agrave; hai mặt k&iacute;nh cường lực b&oacute;ng bẩy, nhưng giờ đ&acirc;y lớp sơn nh&aacute;m ở mặt lưng mang đến diện mạo ho&agrave;n to&agrave;n mới cho iPhone 11 Pro. Mặt lưng sẽ lu&ocirc;n s&aacute;ng đẹp, mịn m&agrave;ng m&agrave; kh&ocirc;ng bị b&aacute;m bẩn, mồ h&ocirc;i hay dấu v&acirc;n tay. Chiếc iPhone nhỏ gọn, cho cảm gi&aacute;c cầm nắm ho&agrave;n hảo nhưng vẫn c&oacute; m&agrave;n h&igrave;nh đủ lớn để trải nghiệm tốt mọi thứ. Bạn sẽ c&oacute; 4 lựa chọn m&agrave;u sắc sang trọng, bao gồm V&agrave;ng, X&aacute;m kh&ocirc;ng gian, Bạc v&agrave; m&agrave;u mới Xanh Midnigh đặc biệt.<img alt=\"\" src=\"http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphone11pro/iphone11pro5.jpg\" style=\"height:394px; width:700px\" /></p>\r\n\r\n<h3><strong>Chất liệu si&ecirc;u cứng, ho&agrave;n thiện chắc chắn, chống nước chuẩn IP68</strong></h3>\r\n\r\n<p>Những chất liệu tạo n&ecirc;n iPhone 11 Pro bao gồm th&eacute;p kh&ocirc;ng gỉ v&agrave; k&iacute;nh cường lực cứng nhất thế giới smartphone. Ch&iacute;nh v&igrave; vậy iPhone 11 Pro v&ocirc; c&ugrave;ng chắc chắn, sẵn s&agrave;ng đương đầu mọi thử th&aacute;ch. Khả năng chống nước chuẩn IP68 cho ph&eacute;p bạn ng&acirc;m nước dưới độ s&acirc;u 4m trong thời gian l&ecirc;n tới 30 ph&uacute;t, đồng nghĩa với việc iPhone 11 miễn nhiễm với hầu hết nguy cơ từ chất lỏng.<img alt=\"\" src=\"http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphone11pro/iphone11pro6.jpg\" style=\"height:394px; width:700px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'Màn hình-5.8 inchs, Super Retina XDR, 1125 x 2436 Pixels-Hệ điều hành-IOS13-Camera sau-Triple 12MP Ultra Wide, Wide and Telephoto cameras-Camera trước-12MP-CPU-Apple A13 Bionic, 6, Đang cập nhật-GPU-	Apple GPU 4 nhân-Bộ nhớ trong-128GB-Thẻ sim-	eSIM và NanoSIM, 1 Sim-', NULL, NULL, NULL, NULL);
INSERT INTO `product` VALUES (5, 'Iphone 11 Pro 64GB', 30990000, 28000000, 'http://localhost:8080/ProjectWeb_1920/images/iphone/iphone11pro.jpg', 'http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphone11pro/iphone11pro7.jpg-http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphone11pro/iphone11pro8.jpg-http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphone11pro/iphone11pro9.jpg-http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphone11pro/iphone11pro10.jpg-', 'iphone', 1, '<p>&nbsp;</p>\r\n\r\n<h3><strong>Thiết kế mặt lưng nh&aacute;m cao cấp</strong></h3>\r\n\r\n<p>Vẫn được ho&agrave;n thiện từ khung th&eacute;p kh&ocirc;ng gỉ cao cấp v&agrave; hai mặt k&iacute;nh cường lực b&oacute;ng bẩy, nhưng giờ đ&acirc;y lớp sơn nh&aacute;m ở mặt lưng mang đến diện mạo ho&agrave;n to&agrave;n mới cho iPhone 11 Pro. Mặt lưng sẽ lu&ocirc;n s&aacute;ng đẹp, mịn m&agrave;ng m&agrave; kh&ocirc;ng bị b&aacute;m bẩn, mồ h&ocirc;i hay dấu v&acirc;n tay. Chiếc iPhone nhỏ gọn, cho cảm gi&aacute;c cầm nắm ho&agrave;n hảo nhưng vẫn c&oacute; m&agrave;n h&igrave;nh đủ lớn để trải nghiệm tốt mọi thứ. Bạn sẽ c&oacute; 4 lựa chọn m&agrave;u sắc sang trọng, bao gồm V&agrave;ng, X&aacute;m kh&ocirc;ng gian, Bạc v&agrave; m&agrave;u mới Xanh Midnigh đặc biệt.<img alt=\"\" src=\"http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphone11pro/iphone11pro5.jpg\" style=\"height:394px; width:700px\" /></p>\r\n\r\n<h3><strong>Chất liệu si&ecirc;u cứng, ho&agrave;n thiện chắc chắn, chống nước chuẩn IP68</strong></h3>\r\n\r\n<p>Những chất liệu tạo n&ecirc;n iPhone 11 Pro bao gồm th&eacute;p kh&ocirc;ng gỉ v&agrave; k&iacute;nh cường lực cứng nhất thế giới smartphone. Ch&iacute;nh v&igrave; vậy iPhone 11 Pro v&ocirc; c&ugrave;ng chắc chắn, sẵn s&agrave;ng đương đầu mọi thử th&aacute;ch. Khả năng chống nước chuẩn IP68 cho ph&eacute;p bạn ng&acirc;m nước dưới độ s&acirc;u 4m trong thời gian l&ecirc;n tới 30 ph&uacute;t, đồng nghĩa với việc iPhone 11 miễn nhiễm với hầu hết nguy cơ từ chất lỏng.<img alt=\"\" src=\"http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphone11pro/iphone11pro6.jpg\" style=\"height:394px; width:700px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;&gt;</p>\r\n\r\n<div id=\"eJOY__extension_root\" style=\"all:unset\">&nbsp;</div>\r\n', 'Màn hình-5.8 inchs, Super Retina XDR, 1125 x 2436 Pixels-Hệ điều hành-IOS13-Camera sau-Triple 12MP Ultra Wide, Wide and Telephoto cameras-Camera trước-12MP-CPU-Apple A13 Bionic, 6, Đang cập nhật-GPU-	Apple GPU 4 nhân-Bộ nhớ trong-128GB-Thẻ sim-	eSIM và NanoSIM, 1 Sim-', NULL, NULL, NULL, NULL);
INSERT INTO `product` VALUES (6, 'Iphone 11 128GB', 23990000, 0, 'http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphone11/iphone11xanh.png', 'http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphone11/iphone111.jpg-http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphone11/iphone112.jpg-http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphone11/iphone114.jpg-http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphone11/iphone115.jpg-', 'iphone', 1, '<p>&nbsp;</p>\r\n\r\n<h3><strong>Hệ thống camera k&eacute;p mới</strong></h3>\r\n\r\n<p>Apple iPhone 11 sở hữu cụm camera k&eacute;p mặt sau, bao gồm camera g&oacute;c rộng v&agrave; camera g&oacute;c si&ecirc;u rộng. Cảm biến camera g&oacute;c rộng 12MP c&oacute; khả năng lấy n&eacute;t tự động nhanh gấp 3 lần trong điều kiện thiếu s&aacute;ng. B&ecirc;n cạnh đ&oacute; cảm biến g&oacute;c si&ecirc;u rộng cho khả năng chụp cảnh rộng gấp 4 lần, l&agrave; phương tiện ghi h&igrave;nh tuyệt vời cho những chuyến du lịch, chụp h&igrave;nh nh&oacute;m. Nhanh ch&oacute;ng chụp ảnh, quay video, chỉnh sửa v&agrave; chia sẻ ngay tr&ecirc;n iPhone, bạn sẽ kh&ocirc;ng bỏ lỡ bất cứ khoảnh khắc đ&aacute;ng nhớ n&agrave;o.<img alt=\"\" src=\"http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphone11/iphone118.jpg\" style=\"height:525px; width:700px\" /></p>\r\n\r\n<h3><strong>Quay video chưa bao giờ chuy&ecirc;n nghiệp đến thế</strong></h3>\r\n\r\n<p>Điện thoại iPhone 11 64GB c&oacute; khả năng quay những đoạn video tuyệt đẹp với độ ph&acirc;n giải 4K 60fps si&ecirc;u sắc n&eacute;t. Camera g&oacute;c si&ecirc;u rộng mang đến nhiều khung cảnh hơn, đồng thời khả năng quay chuyển động cực ấn tượng sẽ tạo n&ecirc;n những thước phim ho&agrave;n hảo. Ngo&agrave;i ra bạn c&ograve;n c&oacute; thể tập trung thu &acirc;m v&agrave;o một chủ thể khi quay video bằng c&aacute;ch ph&oacute;ng to, zoom h&igrave;nh ảnh &ndash; đồng thời zoom &acirc;m thanh hết sức th&uacute; vị. Tất nhi&ecirc;n sau khi quay video, việc chỉnh sửa v&agrave; xuất bản sẽ diễn ra hết sức tiện lợi, nhanh ch&oacute;ng tr&ecirc;n iPhone 11.<img alt=\"\" src=\"http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphone11/iphone119.jpg\" style=\"height:394px; width:700px\" /></p>\r\n\r\n<div id=\"eJOY__extension_root\" style=\"all:unset\">&nbsp;</div>\r\n\r\n<p>&quot;&gt;</p>\r\n\r\n<p>&quot;&gt;</p>\r\n', 'Màn hình-6.1 inchs, Liquid Retina HD, 828 x 1792 Pixels-Hệ điều hành-	iOS 13-Camera sau-	Dual 12MP Ultra Wide and Wide cameras-Camera trước-	12.0 MP-CPU-	Apple A13 Bionic, 6, Đang cập nhật-Ram-4GB-Bộ nhớ trong-128GB-GPU-	Apple GPU 4 nhân-Thẻ sim-	eSIM và NanoSIM, 1 Sim-', NULL, NULL, NULL, NULL);
INSERT INTO `product` VALUES (7, 'Iphone 11 64GB', 21990000, 0, 'http://localhost:8080/ProjectWeb_1920/images/iphone/iphone11.jpg', 'http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphone11/iphone112.jpg-http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphone11/iphone114.jpg-http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphone11/iphone115.jpg-http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphone11/iphone112.jpg-', 'iphone', 1, '<p>&nbsp;</p>\r\n\r\n<h3><strong>Rực rỡ sắc m&agrave;u, thể hiện c&aacute; t&iacute;nh</strong></h3>\r\n\r\n<p>C&oacute; tới 6 sự lựa chọn m&agrave;u sắc cho iPhone 11 64GB, bao gồm T&iacute;m, V&agrave;ng, Xanh lục, Đen, Trắng v&agrave; Đỏ, tha hồ để bạn lựa chọn m&agrave;u ph&ugrave; hợp với sở th&iacute;ch, c&aacute; t&iacute;nh ri&ecirc;ng của bản th&acirc;n. Vẻ đẹp của iPhone 11 đến từ thiết kế cao cấp khi được l&agrave;m từ khung nh&ocirc;m nguy&ecirc;n khối v&agrave; mặt lưng liền lạc với một tấm k&iacute;nh duy nhất. Ở mặt trước, bạn sẽ được chi&ecirc;m ngưỡng m&agrave;n h&igrave;nh Liquid Retina lớn 6,1 inch, m&agrave;u sắc v&ocirc; c&ugrave;ng ch&acirc;n thực. Tất cả tạo n&ecirc;n chiếc điện thoại tr&agrave;n đầy hứng khởi.<img alt=\"\" src=\"http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphone11/iphone117.jpg\" style=\"height:394px; width:700px\" /></p>\r\n\r\n<h3><strong>Hệ thống camera k&eacute;p mới</strong></h3>\r\n\r\n<p>Apple iPhone 11 sở hữu cụm camera k&eacute;p mặt sau, bao gồm camera g&oacute;c rộng v&agrave; camera g&oacute;c si&ecirc;u rộng. Cảm biến camera g&oacute;c rộng 12MP c&oacute; khả năng lấy n&eacute;t tự động nhanh gấp 3 lần trong điều kiện thiếu s&aacute;ng. B&ecirc;n cạnh đ&oacute; cảm biến g&oacute;c si&ecirc;u rộng cho khả năng chụp cảnh rộng gấp 4 lần, l&agrave; phương tiện ghi h&igrave;nh tuyệt vời cho những chuyến du lịch, chụp h&igrave;nh nh&oacute;m. Nhanh ch&oacute;ng chụp ảnh, quay video, chỉnh sửa v&agrave; chia sẻ ngay tr&ecirc;n iPhone, bạn sẽ kh&ocirc;ng bỏ lỡ bất cứ khoảnh khắc đ&aacute;ng nhớ n&agrave;o.<img alt=\"\" src=\"http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphone11/iphone118.jpg\" style=\"height:525px; width:700px\" /></p>\r\n\r\n<h3><strong>Quay video chưa bao giờ chuy&ecirc;n nghiệp đến thế</strong></h3>\r\n\r\n<p>Điện thoại iPhone 11 64GB c&oacute; khả năng quay những đoạn video tuyệt đẹp với độ ph&acirc;n giải 4K 60fps si&ecirc;u sắc n&eacute;t. Camera g&oacute;c si&ecirc;u rộng mang đến nhiều khung cảnh hơn, đồng thời khả năng quay chuyển động cực ấn tượng sẽ tạo n&ecirc;n những thước phim ho&agrave;n hảo. Ngo&agrave;i ra bạn c&ograve;n c&oacute; thể tập trung thu &acirc;m v&agrave;o một chủ thể khi quay video bằng c&aacute;ch ph&oacute;ng to, zoom h&igrave;nh ảnh &ndash; đồng thời zoom &acirc;m thanh hết sức th&uacute; vị. Tất nhi&ecirc;n sau khi quay video, việc chỉnh sửa v&agrave; xuất bản sẽ diễn ra hết sức tiện lợi, nhanh ch&oacute;ng tr&ecirc;n iPhone 11.<img alt=\"\" src=\"http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphone11/iphone119.jpg\" style=\"height:394px; width:700px\" /></p>\r\n', 'Màn hình-6.1 inchs, Liquid Retina HD, 828 x 1792 Pixels-Hệ điều hành-	iOS 13-Camera sau-	Dual 12MP Ultra Wide and Wide cameras-Camera trước-	12.0 MP-CPU-	Apple A13 Bionic, 6, Đang cập nhật-Ram-4GB-Bộ nhớ trong-64GB-GPU-	Apple GPU 4 nhân-Thẻ sim-	eSIM và NanoSIM, 1 Sim-', NULL, NULL, NULL, NULL);
INSERT INTO `product` VALUES (8, 'Iphone XS 256GB', 26990000, 7500000, 'http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphonexsmax/iphonexsmax.jpg', 'http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphonexsmax/iphonexsmax1.jpg-http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphonexsmax/iphonexsmax3.jpg-http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphonexsmax/iphonexsmax2.jpg-http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphonexsmax/iphonexsmax4.jpg-http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphonexsmax/iphonexsmax5.jpg-', 'iphone', 1, '<p><strong>Iphone XS MAX&nbsp;l&agrave; chiếc iPhone c&oacute; m&agrave;n h&igrave;nh lớn nhất từ trước đến nay, mang đến những trải nghiệm tuyệt vời về m&agrave;n h&igrave;nh v&agrave; thời lượng pin.</strong></p>\r\n\r\n<p><strong><img alt=\"\" src=\"http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphonexsmax/iphonexsmax7.jpg\" style=\"height:303px; width:700px\" /></strong></p>\r\n\r\n<h2><strong>M&agrave;n h&igrave;nh lớn 6,5 inch chuẩn Super Retina si&ecirc;u sắc n&eacute;t</strong></h2>\r\n\r\n<p><strong>iPhone Xs Max 256GB</strong>&nbsp;c&oacute; m&agrave;n h&igrave;nh vượt trội so với mọi thế hệ iPhone trước đ&acirc;y khi lớn tới 6,5 inch, được l&agrave;m từ tấm nền OLED, hỗ trợ hiển thị HDR. HDR mang đến m&agrave;u sắc ch&iacute;nh x&aacute;c, m&agrave;u đen c&oacute; chiều s&acirc;u, độ s&aacute;ng v&agrave; độ tương phản tuyệt vời. Bạn sẽ được tận hưởng một m&agrave;n h&igrave;nh cực lớn, độ ph&acirc;n giải si&ecirc;u cao 2688 x 1242 pixels cho trải nghiệm xem đ&aacute;ng kinh ngạc.<img alt=\"\" src=\"http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphonexsmax/iphonexsmax8.jpg\" style=\"height:351px; width:700px\" />Nếu như bạn lo lắng m&agrave;n h&igrave;nh lớn sẽ khiến cho&nbsp;<strong>iPhone Xs Max 256GB&nbsp;</strong>kh&oacute; cầm nắm tr&ecirc;n tay th&igrave; h&atilde;y qu&ecirc;n ngay nỗi lo đ&oacute;, v&igrave; viền m&agrave;n h&igrave;nh tr&agrave;n 4 cạnh gi&uacute;p cho iPhone Xs Max thậm ch&iacute; c&ograve;n nhỏ gọn hơn iPhone 8 Plus. Ch&uacute;ng ta c&oacute; một chiếc m&aacute;y m&agrave;n h&igrave;nh lớn hơn rất nhiều, nhưng vẫn đủ gọn g&agrave;ng để dễ d&agrave;ng cầm nắm v&agrave; thao t&aacute;c.<img alt=\"\" src=\"http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphonexsmax/iphonexsmax9.jpg\" style=\"height:467px; width:700px\" /></p>\r\n\r\n<h2><strong>Sự kết hợp giữa k&iacute;nh v&agrave; th&eacute;p, sang trọng nhưng kh&ocirc;ng k&eacute;m phần bền vững</strong></h2>\r\n\r\n<p><strong>iPhone Xs Max 256GB</strong>&nbsp;được ho&agrave;n thiện từ những vật liệu đặc biệt, đ&oacute; l&agrave; viền th&eacute;p kh&ocirc;ng gỉ s&aacute;ng b&oacute;ng v&agrave; mặt k&iacute;nh bền nhất trong thế giới smartphone. Kỹ thuật gia c&ocirc;ng ch&iacute;nh x&aacute;c ho&agrave;n hảo của Apple mang đến chiếc điện thoại cứng c&aacute;p, thời trang, vừa vặn trong l&ograve;ng b&agrave;n tay bạn. Ba m&agrave;u sắc thời thượng X&aacute;m kh&ocirc;ng gian, Bạc v&agrave; V&agrave;ng cho những lựa chọn c&aacute; t&iacute;nh. Đặc biệt, hai phi&ecirc;n bản m&agrave;u V&agrave;ng v&agrave; X&aacute;m được sơn bằng qu&aacute; tr&igrave;nh xử l&yacute; m&agrave;u PVD, tạo hiệu ứng &aacute;nh s&aacute;ng qua lớp k&iacute;nh rất đẹp mắt.<img alt=\"\" src=\"http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphonexsmax/iphonexsmax10.jpg\" style=\"height:467px; width:700px\" /></p>\r\n\r\n<h2><strong>Face ID thế hệ mới, mật khẩu ch&iacute;nh l&agrave; khu&ocirc;n mặt của bạn</strong></h2>\r\n\r\n<p>Face ID tr&ecirc;n&nbsp;<strong>iPhone Xs Max 256GB</strong>&nbsp;khiến cho việc bảo mật đơn giản v&agrave; an to&agrave;n hơn. Bạn c&oacute; thể mở kh&oacute;a, đăng nhập, thanh to&aacute;n nhanh ch&oacute;ng th&ocirc;ng qua nhận diện khu&ocirc;n mặt. Ngay khi đưa m&aacute;y l&ecirc;n, hệ thống camera TrueDepth sẽ nhận diện tới 30.000 điểm tr&ecirc;n khu&ocirc;n mặt của bạn v&agrave; mở kh&oacute;a ngay lập tức. Đồng thời, hệ thống n&agrave;y cũng sẽ ghi nhớ v&agrave; học hỏi th&oacute;i quen của bạn để sau n&agrave;y d&ugrave; c&oacute; đội mũ hay đeo k&iacute;nh th&igrave; Face ID vẫn nhận diện nhanh ch&oacute;ng đầy ch&iacute;nh x&aacute;c. Face ID của iPhone Xs Max ch&iacute;nh l&agrave; phương thức bảo mật an to&agrave;n nhất tr&ecirc;n smartphone từ trước đến nay.<img alt=\"\" src=\"http://localhost:8080/ProjectWeb_1920/data/images/iphone/iphonexsmax/iphonexsmax11.jpg\" style=\"height:467px; width:700px\" /></p>\r\n', 'Màn hình-6.5 inchs, Super Retina HD, 1242 x 2688 Pixels-Hệ điều hành-	iOS 13-Camera sau-	Dual 12MP Ultra Wide and Wide cameras-Camera trước-	12.0 MP-CPU-	Apple A12 Bionic, 6, Đang cập nhật-Ram-4GB-Bộ nhớ trong-256GB-GPU-	Apple GPU 4 nhân-Thẻ sim-	eSIM và NanoSIM, 1 Sim-', NULL, NULL, NULL, NULL);
